// https://www.shadertoy.com/view/7dSSW1
#define ID_POS(x) clamp(abs(4.-8.*fract(vec2(x+3,x-3)/8.))-2.,-1.,1.)
vec2 intersect(vec2 p0, vec2 n0, vec2 p1, vec2 n1) {
    float d = n0.x * n1.y - n0.y * n1.x; // 0 if paralelle but does not append here
    vec2 p = (p0 - p1) / d;
    return vec2(p.y * n1.x - p.x * n1.y, p.y * n0.x - p.x * n0.y);
}

vec2[8] getVoronoiPolygon(in vec2 cellId, out int out_sz) {
	vec2 rm = getPoint(cellId); // center of cell
    vec2 c[8], n[8];
    // Get liste of lines
    for(int i=0; i < 8; i++) {
        vec2 g0 = ID_POS(i),
             r0 = getPoint(cellId + g0),  // centre de l'autre cellule
             n0 = r0 - rm;
         c[i] = rm + n0*.5;  // Pt entre les 2 cellules             
         n[i] = normalize(vec2(-n0.y,n0.x));
    }

    // Find intersection between lines
    vec4 seg[8]; // x:-2 y:-1 z:+1 w:+2  
    for(int i=2; i < 10; i++) { // Intersection point with two previous lines
        vec2 t1 = intersect(c[i % 8], n[i % 8], c[(i - 1) % 8], n[(i - 1) % 8]),
             t2 = intersect(c[i % 8], n[i % 8], c[(i - 2) % 8], n[(i - 2) % 8]);
        seg[i % 8].x = t2.x;     // -2 of seg  0
        seg[i % 8].y = t1.x;     // -1 of seg  0
        seg[(i - 1) % 8].z = t1.y; // +1 of seg -1
        seg[(i - 2) % 8].w = t2.y; // +2 of seg -2      
    }

    // Reconstruct the polygon
    out_sz = 0;
    for(int i=0; i<8; i++) {
        float dmin = max(seg[i].z, seg[i].w);
        if (dmin < min(seg[i].x, seg[i].y)) { // skip useless segments
            n[out_sz] = c[i] + dmin * n[i];
            out_sz++;
        }
    }

    return n;
}

float sdBezier(in vec2 p, in vec2 v1, in vec2 v2, in vec2 v3) {
    vec2 c1 = p - v1;
    vec2 c2 = 2.0 * v2 - v3 - v1;
    vec2 c3 = v1 - v2;

    float t3 = dot(c2, c2);
    float t2 = dot(c3, c2) * 3.0 / t3;
    float t1 = (dot(c1, c2) + 2.0 * dot(c3, c3)) / t3;
    float t0 = dot(c1, c3) / t3;

    float t22 = t2 * t2;
    vec2 pq = vec2(t1 - t22 / 3.0, t22 * t2 / 13.5 - t2 * t1 / 3.0 + t0);
    float ppp = pq.x * pq.x * pq.x, qq = pq.y * pq.y;

    float p2 = abs(pq.x);
    float r1 = 1.5 / pq.x * pq.y;

    if (qq * 0.25 + ppp / 27.0 > 0.0) {
        float r2 = r1 * sqrt(3.0 / p2), root;
        if (pq.x < 0.0) root = sign(pq.y) * cosh(acosh(r2 * -sign(pq.y)) / 3.0);
        else root = sinh(asinh(r2) / 3.0);
        root = clamp(-2.0 * sqrt(p2 / 3.0) * root - t2 / 3.0, 0.0, 1.0);
        return length(p - mix(mix(v1, v2, root), mix(v2, v3, root), root));
    }

    else {
        float ac = acos(r1 * sqrt(-3.0 / pq.x)) / 3.0;
        vec2 roots = clamp(2.0 * sqrt(-pq.x / 3.0) * cos(vec2(ac, ac - 4.18879020479)) - t2 / 3.0, 0.0, 1.0);
        vec2 p1 = p - mix(mix(v1, v2, roots.x), mix(v2, v3, roots.x), roots.x);
        vec2 p2 = p - mix(mix(v1, v2, roots.y), mix(v2, v3, roots.y), roots.y);
        return sqrt(min(dot(p1, p1), dot(p2, p2)));
    }
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float zoom = mix(2.0, 10.0, 0.5 + 0.5 * cos(0.25 * iTime));
    float fade = 0.5 + 0.5 * sin(iTime);

    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * zoom + 0.5 * iTime;
    float unit = 2.0 * zoom / iResolution.y;
    vec3 color = vec3(0.0);

    vec2 cell = floor(uv), closest;
    float minDist = 1e20;
    for (int i=-1; i < 2; i++) {
        for (int j=-1; j < 2; j++) {
            vec2 curPoint = getPoint(cell + vec2(i, j));
            vec2 toCurPoint = uv - curPoint;
            float curDist = dot(toCurPoint, toCurPoint);
            if (curDist < minDist) { closest = curPoint; minDist = curDist; }
        }
    }

    int numVerts;
    vec2 closestCell = floor(closest);
    vec2[8] voroPoly = getVoronoiPolygon(closestCell, numVerts);
    for (int n=0; n < numVerts; n++) {
        int randEdge = int(Hash31(vec3(closestCell, n)) * float(numVerts));
        if (randEdge == n) randEdge = (randEdge + 1) % numVerts;

        vec2 rand1 = voroPoly[randEdge], rand2 = voroPoly[(randEdge + 1) % numVerts];
        vec2 v1 = voroPoly[n], v2 = voroPoly[(n + 1) % numVerts];

        vec2 nor1 = normalize(v2 - v1).yx * vec2(-1.0, 1.0);
        vec2 nor2 = normalize(rand2 - rand1).yx * vec2(-1.0, 1.0);

        vec2 a = 0.5 * (v1 + v2), c = 0.5 * (rand1 + rand2);
        vec2 b = a + nor1 * (c.y * nor2.x - c.x * nor2.y + nor2.y * a.x - nor2.x * a.y) / (nor1.y * nor2.x - nor1.x * nor2.y);
        //vec2 b = 0.5 * (a + c) + normalize(c - a).yx * vec2(1.0, -1.0);
        //vec2 b = closest;

        color = max(color, vec3(smoothstep(unit, 0.0, sdBezier(uv, a, b, c))));
        color.b = max(color.b, smoothstep(unit, 0.0, sdLine(uv, v1, v2) - 0.01) * fade);
    }

    color.rg = max(color.rg, 0.6 * Hash22(closestCell) * fade);
    fragColor = vec4(color, 1.0);
}

