/*
A revision of my last attempt, the main difference is that instead of
gathering the volume info during the ray march, i gather it after by
finding the start and end position and create 180 sample points inbetween where
i gather the volume info. (For the sky i step into the scene by 0.2 160
times to gather the volume info).
*/


struct SpecularLight
{
    float highlight;
    float diffuse;
};


SpecularLight Specular(float smoothness, vec3 normal, vec3 rd, vec3 sun_dir)
{
    float specularAngle = acos(dot(normalize(sun_dir - rd), normal));
    float specularExponent = specularAngle / (1. - smoothness);
    float specularHighlight = exp(-specularExponent * specularExponent);
    float diffseLighting = clamp(dot(normal, sun_dir), 0., 1.);
    return SpecularLight(specularHighlight, diffseLighting);
}


// from https://www.shadertoy.com/view/WtGXDD
mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

// from https://www.shadertoy.com/view/WtGXDD
vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}


// from https://www.shadertoy.com/view/WtGXDD
float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}


float Dimple(vec3 p)
{
    return min(p.y + 10., 1. - (abs(length(p.xz) / max((p.y + 5.) / 10., 1.)) - 15.));
}


float Boxes(vec3 p)
{
    return sdBox(abs(p) - vec3(10., 0., 10.), vec3(1., 15., 1.));
}


float GetDst(vec3 p)
{
    float dst1 = length(p) - 2.5;
    float dst2 = Dimple(p);
    float dst3 = Boxes(p);

    return min(min(dst1, dst2), dst3);
}


vec4 GetCol(vec3 p)
{
    float dst1 = length(p) - 2.;
    float dst2 = Dimple(p);
    float dst3 = Boxes(p);

    float dst = min(min(dst1, dst2), dst3);

    if (dst == dst1) return vec4(0.2, 0.2, 0.8, 0.);
    if (dst == dst2) return vec4(0.8, 0.2, 0.8, 1.);
    if (dst == dst3) return vec4(0.2, 0.8, 0.8, 2.);
    return vec4(0., 0., 0., -1.);
}


vec3 GetNormal(vec3 p)
{
	float d = GetDst(p);
    vec2 e = vec2(0.01, 0);

    vec3 normal = d - vec3(
        GetDst(p - e.xyy),
        GetDst(p - e.yxy),
        GetDst(p - e.yyx));

    return normalize(normal);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;
    vec2 m = iMouse.xy / iResolution.xy;  // * vec2(1., 0.62);
    vec3 col = vec3(0.);

    vec3 sun_dir = normalize(vec3(sin(iTime), 1.5, cos(iTime)));
    vec3 sun_col = vec3(1., 0.95, 0.85);

    // from https://www.shadertoy.com/view/WtGXDD (getting ro and rd)
    vec3 ro = vec3(0, 8, -8);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    vec3 rd = GetRayDir(uv, ro, vec3(0., 0., 0.), 1.);

    vec3 p = ro;
    float depth = 0.;
    bool collided = false;
    for (int s = 0; s < 125; s++)
    {
        float dst = GetDst(p);
        p += rd * dst;
        depth += dst;

        if (dst < 0.01)
        {
            collided = true;
            break;
        }
        else if (dst > 35.) break;
    }
    if (collided)
    {
        vec4 object_col_and_mat = GetCol(p);
        vec3 object_col = object_col_and_mat.rgb;
        vec3 normal = GetNormal(p);
        object_col *= dot(sun_dir, normal) * 0.5 + 0.5;
        SpecularLight spec = Specular(0.8, normal, rd, sun_dir);
        object_col += sun_col * spec.highlight;

        float sun_depth = 0.;
        bool shadow_collided = false;
        vec3 sp = p + normal * 0.01 * 2.;
        for (int s = 0; s < 50; s++)
        {
            float dst = GetDst(sp);
            sp += sun_dir * dst;
            sun_depth += dst;

            if (dst < 0.01)
            {
                shadow_collided = true;
                break;
            }
            else if (sun_depth > 35.) break;
        }
        float total_density = 0.;
        float step_size_f = depth / 180.;
        vec3 step_size = rd * step_size_f;
        vec3 dp = ro;
        for (int s = 0; s < 180; s++)
        {
            vec3 srp = dp;
            float slrd = 0.;
            for (int sr = 0; sr < 75; sr++)
            {
                float dst = GetDst(srp);
                srp += sun_dir * dst;
                slrd += dst;

                if (dst < 0.075)
                {
                    total_density += 0.15 * step_size_f;
                    break;
                }
                else if (slrd > 25.) break;
            }
            dp += step_size;
        }

        object_col *= exp(-total_density);
        object_col *= shadow_collided ? 0.5 : 1.;

        col = object_col;
    }
    else
    {
        float total_density = 0.;
        float step_size_f = 0.175;
        vec3 step_size = rd * step_size_f;
        vec3 dp = ro;
        for (int s = 0; s < 180; s++)
        {
            vec3 srp = dp;
            float slrd = 0.;
            for (int sr = 0; sr < 75; sr++)
            {
                float dst = GetDst(srp);
                srp += sun_dir * dst;
                slrd += dst;

                if (dst < 0.075)
                {
                    total_density += 0.15 * step_size_f;
                    break;
                }
                else if (slrd > 25.) break;
            }
            dp += step_size;
        }

        col = vec3(0.3, 0.3, rd.y * 0.5 + 0.5);
        if (rd.y > 0.) col = mix(col, sun_col * 1.3, min(max(pow(dot(sun_dir, rd), 20.) * 0.7, 0.), 1.));
        col *= exp(-total_density);
    }

    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    col =  clamp((col*(a*col+b))/(col*(c*col+d)+e), 0., 1.);

    fragColor = vec4(col,1.0);
}

float innerRadius = 0.45;
float outterRadius = 0.5;
float segmentAngle = 0.7;
float PI = 3.14;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 resultColor = vec3(1.0);
    vec2 uv = fragCoord/iResolution.xy;
    float pix = 1./iResolution.y;
    float x = sin(iTime);
    float y = cos(iTime) * abs(cos(iTime));

    vec2 movingPoint = vec2(x, y);
    float distToCenter = distance(uv, vec2(0.5, 0.5));

    vec2 newUv = uv - vec2(0.5, 0.5);
    float cosA = dot(newUv, movingPoint) / (length(newUv) * length(movingPoint));
    segmentAngle *= abs(y);

    float coefForDist = smoothstep(innerRadius - pix, innerRadius + pix, distToCenter) * smoothstep(distToCenter - pix, distToCenter + pix, outterRadius);
    float coefForAngle = smoothstep(cosA - pix, cosA + pix, segmentAngle) * smoothstep(-segmentAngle - pix, -segmentAngle + pix, cosA);

    resultColor = vec3(1.0) * (1.0 - coefForDist * coefForAngle);

    fragColor = vec4(resultColor, 1.0);
}

