{
 	float rz = 0.;
    float z = 2.;
    for (float i= 0.;i < 3.;i++ )
	{
        rz += abs(fract(x*1.4)-0.5)/z;
        x *= 1.3;
        z *= 1.15;
        x -= time*.65*z;
    }
    return rz;
}

float segm( vec3 p, vec3 a, vec3 b)
{
    vec3 pa = p - a;
	vec3 ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1. );	
	return length( pa - ba*h )*.5;
}

vec3 path(in float i, in float d)
{
    vec3 en = vec3(0.,0.,1.);
    float sns2 = sins(d+i*0.5)*0.22;
    float sns = sins(d+i*.6)*0.21;
    en.xz *= mm2((hash(i*10.569)-.5)*6.2+sns2);
    en.xy *= mm2((hash(i*4.732)-.5)*6.2+sns);
    return en;
}

vec2 map(vec3 p, float i)
{
	float lp = length(p);
    vec3 bg = vec3(0.);   
    vec3 en = path(i,lp);

    float ins = smoothstep(0.11,.46,lp);
    float outs = .15+smoothstep(.0,.15,abs(lp-1.));
    p *= ins*outs;
    float id = ins*outs;

    float rz = segm(p, bg, en)-0.011;
    return vec2(rz,id);
}

float march(in vec3 ro, in vec3 rd, in float startf, in float maxd, in float j)
{
	float precis = 0.001;
    float h=0.5;
    float d = startf;
    for( int i=0; i<MAX_ITER; i++ )
    {
        if( abs(h)<precis||d>maxd ) break;
        d += h*1.2;
	    float res = map(ro+rd*d, j).x;
        h = res;
    }
	return d;
}

//volumetric marching
vec3 vmarch(in vec3 ro, in vec3 rd, in float j, in vec3 orig)
{   
    vec3 p = ro;
    vec2 r = vec2(0.);
    vec3 sum = vec3(0);
    float w = 0.;
    for( int i=0; i<VOLUMETRIC_STEPS; i++ )
    {
        r = map(p,j);
        p += rd*.03;
        float lp = length(p);

        vec3 col = sin(vec3(1.05,2.5,1.52)*3.94+r.y)*.85+0.4;
        col.rgb *= smoothstep(.0,.015,-r.x);
        col *= smoothstep(0.04,.2,abs(lp-1.1));
        col *= smoothstep(0.1,.34,lp);
        sum += abs(col)*5. * (1.2-noise(lp*2.+j*13.+time*5.)*1.1) / (log(distance(p,orig)-2.)+.75);
    }
    return sum;
}

//returns both collision dists of unit sphere
vec2 iSphere2(in vec3 ro, in vec3 rd)
{
    vec3 oc = ro;
    float b = dot(oc, rd);
    float c = dot(oc,oc) - 1.;
    float h = b*b - c;
    if(h <0.0) return vec2(-1.);
    else return vec2((-b - sqrt(h)), (-b + sqrt(h)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord, vec3 _ro, vec3 _rd )
{	
	vec2 p = fragCoord.xy/iResolution.xy-0.5;
	p.x*=iResolution.x/iResolution.y;
	vec2 um = iMouse.xy / iResolution.xy-.5;

	//camera
	vec3 ro = vec3(0.,0.,5.);
    vec3 rd = normalize(vec3(p*.7,-1.5));
    mat2 mx = mm2(time*.4+um.x*6.);
    mat2 my = mm2(time*0.3+um.y*6.); 
    ro.xz *= mx;rd.xz *= mx;
    ro.xy *= my;rd.xy *= my;

    vec3 bro = _ro;
    vec3 brd = _rd;

    vec3 col = vec3(0.0125,0.,0.025);
    #if 1
    for (float j = 1.;j<NUM_RAYS+1.;j++)
    {
        ro = bro;
        rd = brd;
        mat2 mm = mm2((time*0.1+((j+1.)*5.1))*j*0.25);
        ro.xy *= mm;rd.xy *= mm;
        ro.xz *= mm;rd.xz *= mm;
        float rz = march(ro,rd,2.5,FAR,j);
		if ( rz >= FAR)continue;
    	vec3 pos = ro+rz*rd;
    	col = max(col,vmarch(pos,rd,j, bro));
    }
    #endif

    ro = bro;
    rd = brd;
    vec2 sph = iSphere2(ro,rd);

    if (sph.x > 0.)
    {
        vec3 pos = ro+rd*sph.x;
        vec3 pos2 = ro+rd*sph.y;
        vec3 rf = reflect( rd, pos );
        vec3 rf2 = reflect( rd, pos2 );
        float nz = (-log(abs(flow(rf*1.2,time)-.01)));
        float nz2 = (-log(abs(flow(rf2*1.2,-time)-.01)));
        col += (0.1*nz*nz* vec3(0.12,0.12,.5) + 0.05*nz2*nz2*vec3(0.55,0.2,.55))*0.8;
    }

	fragColor = vec4(col*1.3, 1.0);
}


const float	sideAngle = radians(35.0);
const float	horizontalAngle = radians(14.0);
const float	cameraSize      = 2.2;
const float	aspectRatio     = 0.75;
const float quiltColumns	= 8.0;
const float quiltRows		= 6.0;
const float nearClip        = 5.0;  

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
	vec2	coord = fragCoord * vec2(quiltColumns,quiltRows) / iResolution.xy;
	vec2	fract = fract(coord);
	vec2	floor = floor(coord);
	float	imageId = floor.x + floor.y * quiltColumns;
	float	valueId = imageId / (quiltColumns * quiltRows - 1.);
	float	dist = (cameraSize / 2.) / tan(horizontalAngle / 2.);
	float	minCam = -(cameraSize / 2.) - tan(sideAngle / 2.) * dist;

	vec3	camPos = mix(vec3(minCam,0,dist),vec3(-minCam,0,dist),valueId);
	vec3	screenPos = vec3((fract.x-0.5)*cameraSize,(fract.y-0.5)*cameraSize/aspectRatio,0);

	vec4	color;
	vec3 	dirRay = normalize(screenPos-camPos);
	mainImage( color, fract*(iResolution.xy), screenPos - nearClip * dirRay, dirRay );

    fragColor = color;
}

// Alternative by iq suggested by Shane
// Its associative and commutative
// Associative: sminExp(a, sminExp(b, c, k), k) = sminExp(sminExp(a, b, k), c, k)
// Commutative: sminExp(a, b, k) = sminExp(b, a, k)
float sminExp(in float a, in float b, in float k) {
    float res = exp(-k * a) + exp(-k * b);
    return -log(res) / k;
}

// Hash from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
float Hash11(in float x) {
    x = fract(x * 0.1031);
    x *= x + 33.33;
    x *= x + x;
    return fract(x);
}

// Random cycling points
vec2 getPoint(in vec2 cell) {
    float freq = Hash11(dot(cell, vec2(393.84, 673.48))) * 3.0 + 1.0;
    float phase = Hash11(dot(cell, vec2(348.46, 183.37)));
    float amp = Hash11(dot(cell, vec2(275.35, 741.69)));

    float t = iTime * freq + phase;
    return cell + 0.5 + 0.5 * vec2(cos(t), sin(t)) * amp;
}

float smoothVoronoi(in vec2 p, in float k) {
    // Get closest point
    vec2 cell = floor(p), closest = getPoint(cell);
    float minDist = length(p - closest);
    for (int i=-1; i < 2; i++) {
        for (int j=-1; j < 2; j++) {
            if (i == 0 && j == 0) continue;
            vec2 curCell = cell + vec2(i, j), curPoint = getPoint(curCell);
            float dist = length(p - curPoint);
            if (dist < minDist) {
                closest = curPoint;
                minDist = dist;
            }
        }
    }

    // Get closest border (should be doing 5x5 search but it doesn't affect this look)
    cell = floor(closest);
    vec2 p1 = getPoint(cell + 1.0);
    float dist = dot(p - 0.5 * (closest + p1), normalize(closest - p1));
    for (int i=-1; i < 2; i++) {
        for (int j=-1; j < 2; j++) {
            if ((i == 0 && j == 0) || (i == 1 && j == 1)) continue;
            vec2 curPoint = getPoint(cell + vec2(i, j));
            dist = sminExp(dist, dot(p - 0.5 * (closest + curPoint), normalize(closest - curPoint)), k);
        }
    }

    return dist;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 3.0 + 0.5 * iTime;
    float unit = 6.0 / iResolution.y;

    float k = 13.0; // How much the tiles are packed
    float voro = smoothVoronoi(uv, k);

    float dx = (smoothVoronoi(uv + vec2(0.001, 0.0), k) - voro) / 0.001;
    float dy = (smoothVoronoi(uv + vec2(0.0, 0.001), k) - voro) / 0.001;
    vec3 grad = normalize(vec3(dx, dy, 1.0));

    fragColor = vec4(0.0);

    fragColor.gb += 0.5 + 0.5 * grad.xy;
    fragColor.r += grad.z;

    fragColor.rgb -= smoothstep(unit, 0.0, voro);
    fragColor.rgb += pow(grad.z, 10.0);
}

// Inspired by CineShader: https://www.shadertoy.com/view/wlVGWd

// Scale
#define ZOOM 8.

// FBM Scale
#define FBMZ 2.5

float rand(vec2 n) {
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p) {
    vec2 ip = floor(p);
    vec2 u = fract(p);
    u = u*u*(3.0-2.0*u);

    float res = mix(
        mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
        mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
    return res*res;
}

const mat2 m2 = mat2(0.8,-0.2,0.6,0.8);

float fbm( in vec2 p ){
    float f = 0.0;
    f += 0.5000*noise( p ); p = m2*p*2.02;
    f += 0.2500*noise( p ); p = m2*p*2.03;
    f += 0.1250*noise( p ); p = m2*p*2.01;
    f += 0.0625*noise( p );

    return f/0.769;
}

float pattern( in vec2 p ) {
  vec2 q = vec2(fbm(p + vec2(0.0,0.0)));
  vec2 zm = q * FBMZ;

  vec2 r = vec2( fbm( p + zm + vec2(1.1,8.2)));
  r+= iTime * 0.1;
  return fbm( p + 1.760*r );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  	vec2 uv = fragCoord/iResolution.xy;

    uv *= ZOOM;
  	float displacement = pattern(uv);
  	vec4 color = vec4(displacement * 0.8, displacement * 0.8, displacement * 1.2, 1.);

    color.a = min(color.r * 0.75, 1.); // Depth for CineShader
    fragColor = color;
}

float Calculate_distance(vec3 point){

    vec4 ball=vec4(1.,1.,5.,1.);
    float distance_to_ball = length(point-ball.xyz)-ball.w;
    float distance_to_plane = point.y;
    float min_distance = min(distance_to_ball,distance_to_plane);
    return min_distance;
}

vec3 Calculate_Normal(vec3 point){
float distance_ = Calculate_distance(point);
    vec2 disturb = vec2(.01,0.);
    vec3 normal_point = vec3(distance_)-vec3(Calculate_distance(point-disturb.xyy),
                                            Calculate_distance(point-disturb.yxy),
                                            Calculate_distance(point-disturb.yyx)); 
    return normalize(normal_point);
}

float Raytrack(vec3 camera_center, vec3 ray_direction){
    float distance_from_camera = 0.;
    for(int i=0;i<100;i++){
        vec3 point = camera_center+ray_direction*distance_from_camera;
        float distance_to_ball = Calculate_distance(point);
        distance_from_camera = distance_from_camera + distance_to_ball;
        if(distance_from_camera>100.) break;
    }
    return distance_from_camera;
}


float Calculate_Light(vec3 point){
    vec3 light_position = vec3(2.+4.0*sin(iTime),2.+1.0*sin(iTime),3.+3.0*cos(iTime));
    vec3 light_to_point = normalize(light_position-point);
    vec3 normal_point = Calculate_Normal(point);
    float light_strength = clamp (dot(normal_point,light_to_point),0.,1.);
    float point_to_light = Raytrack(point+normal_point*0.01*2.,light_to_point);
    if(point_to_light<length(light_position-point)) light_strength=0.;
    return light_strength;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
	uv-=.5;
    uv.x*=iResolution.x/iResolution.y;

    vec3 ball_center = vec3(0.,1.,0.);
    vec3 ray_direction = normalize(vec3(uv.x,uv.y,1.));
    float distance_from_camera = Raytrack(ball_center,ray_direction);

    vec3 point =  ball_center + distance_from_camera*ray_direction;
    float light = Calculate_Light(point);
	vec3 col = vec3(light);
    fragColor = vec4(col,1.0);
}

#define green vec3(0.0,.3,0.6)

// returns a vec3 color from every pixel requested.
// Generates a BnW Ping on normalized 2d coordinate system
vec3 RadarPing(in vec2 uv, in vec2 center, in float innerTail, 
               in float frontierBorder, in float timeResetSeconds, 
               in float radarPingSpeed, in float fadeDistance, float t)
{
    vec2 diff = center-uv;
    float r = length(diff);
    float time = mod(t, timeResetSeconds) * radarPingSpeed;

    float circle;
    // r is the distance to the center.
    // circle = BipCenter---//---innerTail---time---frontierBorder
    //illustration
    //https://sketch.io/render/sk-14b54f90080084bad1602f81cadd4d07.jpeg
    circle += smoothstep(time - innerTail, time, r) * smoothstep(time + frontierBorder,time, r);
	circle *= smoothstep(fadeDistance, 0.0, r); // fade to 0 after fadeDistance

    return vec3(circle);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    //normalize coordinates 
    vec2 uv = fragCoord.xy / iResolution.xy; //move coordinates to 0..1
    uv = uv.xy*2.; // translate to the center
    uv += vec2(-1.0, -1.0);
    uv.x *= iResolution.x/iResolution.y; //correct the aspect ratio

	vec3 color;
    // generate some radar pings
    float fadeDistance = 0.8;
    float resetTimeSec = 3.0;
    float radarPingSpeed = 0.2;
    vec2 greenPing = vec2(0.0, 0.0);
    color += RadarPing(uv, greenPing, 0.08, 0.00025, resetTimeSec, radarPingSpeed, fadeDistance, iTime) * green;
    color += RadarPing(uv, greenPing, 0.08, 0.00025, resetTimeSec, radarPingSpeed, fadeDistance, iTime + 1.) * green;
    color += RadarPing(uv, greenPing, 0.08, 0.00025, resetTimeSec, radarPingSpeed, fadeDistance, iTime + 2.) * green;
    //return the new color
	fragColor = vec4(color,1.0);
}

float grid(mat2 mat, vec2 pos) {
    vec2 tpos = inverse(mat)*pos;
    vec2 closest = tpos+mod(tpos+0.5, vec2(1))-0.5;
    if (abs(tpos.x-closest.x) < 0.003/length(mat[0])
     || abs(tpos.y-closest.y) < 0.003/length(mat[1]))
        return 1.0;
    return max(0.0, 20.0-1000.0*length(mat*(tpos - closest)));
}

#define REGIONS 4 // min 4
#define CELLS 2

float voronoi(inout mat2 mat, vec2 pos, vec2 show_cell, out int region) {
    // optimization: find most orthogonal primitive cell
    vec2 real1 = mat[1]-round(dot(mat[1], mat[0])/dot(mat[0], mat[0]))*mat[0];
    vec2 real0 = mat[0]-round(dot(mat[0], real1)/dot(real1, real1))*real1;
    real1 = real1-round(dot(real1, real0)/dot(real0, real0))*real0;
    real0 = real0-round(dot(real0, real1)/dot(real1, real1))*real1;
    mat = mat2(real0, real1);

    // find k-nearest neighbors
    vec2 tpos = inverse(mat)*pos;
    float d[REGIONS];
    for (int i = 0; i < REGIONS; i++) d[i] = 1e6;
    vec2 k[REGIONS];
    for (int x = -CELLS; x <= CELLS; x++) {
        for (int y = -CELLS; y <= CELLS; y++) {
            vec2 kn = mod(tpos+0.5, vec2(1))+vec2(x, y)-0.5;
            float dn = length(mat*kn);
            for (int j = 0; j < REGIONS; j++) {
                if (dn < d[j]) {
                    for (int i = REGIONS - 1; i > j; i--) {
                        k[i]=k[i-1];
                        d[i]=d[i-1];
                    }
                    d[j] = dn;
                    k[j] = kn;
                    break;
                }
            }
        }
    }
    // find the region which corresponds to show_cell
    region = -1;
    for (int i = 0; i < REGIONS - 1; i++)
        if (length(k[i] - tpos - show_cell) < 1e-3)
            region = i;

    // make some grid visuals with points and F1 voronoi edges
    vec2 h = mat*(k[3]);
    vec2 w = mat*(k[2]);
    vec2 v = mat*(k[1]);
    vec2 u = mat*(k[0]);
    float edge = min(dot(0.5*(u+v), normalize(v-u)),
                 min(dot(0.5*(u+w), normalize(w-u)),
                     dot(0.5*(u+h), normalize(h-u))));
    return max(0.0, (0.002-min(0.1*d[0], edge)) * 2000.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord;
    vec2 mouse = iMouse.xy;

    // animate if mouse has never moved
    if (length(mouse.xy) == 0.0)
        mouse.x += iTime*50.0;

    // make grid matrix
    mat2 real = mat2(vec2(0.25, 0.0), mouse/iResolution.xy-0.5);
    real[0] *= iResolution.xy/iResolution.y;
    real[1] *= iResolution.xy/iResolution.y;

    // "do voronoi"
    int vregion = -1;
    vec2 pos = (uv-mouse)/iResolution.y + real[1];
    float decoration = voronoi(real, pos, vec2(0), vregion);

    // draw most orthogonal grid instead of voronoi cells
    //decoration = grid(real, pos);

    // define the colors used for the regions
#define COLORS 6
    vec3 colors[COLORS];
    colors[0] = vec3(1,0,0);
    colors[1] = vec3(0,1,0);
    colors[2] = vec3(0,0,1);
    colors[3] = vec3(1,1,0);
    colors[4] = vec3(0,1,1);
    colors[5] = vec3(1,0,1);

    // Output to screen
    vec3 col = colors[vregion % COLORS] * 2.0 / float(2 + vregion / COLORS);
    if (vregion < 0)
        col = vec3(0);
    fragColor = vec4(col + decoration, 1);
}

/*
    Returns hash in range [-1.0; 1.0].
*/
float signedSample1DHashUI32(uint value)
{
    // Pick some enthropy source values.
    // Try different values.
    const uint enthropy0 = 12345u;
    const uint enthropy1 = 67890u;

    // Calculate hash.
	value += enthropy1;
	value *= 445593459u;
	value ^= enthropy0;

    // 2.0f / 4294967295.0f = 4.6566128730773926e-10

    return float(value * value * value) * 4.6566128730773926e-10 - 1.0f;
}

float circleSDF(
    float x,
    float y,
    float cx,
    float cy, 
    float r)
{
    // Sample coordinates relative to circle center.
    float _x = x - cx;
    float _y = y - cy;

    // Signed distance.
    return sqrt(_x * _x + _y *_y) - r;
}

float smoothCircleSDF(
    float x,
    float y,
    float cx,
    float cy, 
    float r,
    float smoothness)
{
    // Sample coordinates relative to circle center.
    float _x = x - cx;
    float _y = y - cy;

    // Signed distance.
    float d =  sqrt(_x * _x + _y *_y) - r;

    // Smoothstep interpolation.
    return smoothstep(-smoothness, smoothness, d);
}

float left(in vec2 fragCoord, float radius)
{     
    float gray = circleSDF(
            fragCoord.x, fragCoord.y,
            iResolution.x * 0.16, iResolution.y * 0.5,
            radius);

    return (1.0 + cos(gray * 0.9) * 0.4);
}

float middle(in vec2 fragCoord, float radius)
{     
    return smoothCircleSDF(
            fragCoord.x, fragCoord.y,
            iResolution.x * 0.495, iResolution.y * 0.5,
            radius,
            4.0);
}


float right(in vec2 fragCoord, float radius)
{     
    float gray = circleSDF(
            fragCoord.x, fragCoord.y,
            iResolution.x * 0.83, iResolution.y * 0.5,
            radius);

    gray = (1.0 + cos(gray * 0.9) * 0.4);

    gray = min(
        gray, 
        smoothCircleSDF(
            fragCoord.x, fragCoord.y,
            iResolution.x * 0.83, iResolution.y * 0.5,
            radius,
            4.0));

    return gray;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 c = iResolution.xy * 0.5;

    float radius = 15.0 + (1.0 + sin(iTime * 0.1)) * 60.0;

    float gray = 0.0;

    float xTreshold0 = 0.33 + signedSample1DHashUI32(uint(fragCoord.y * 7.1)) * 0.00159;
    float xTreshold1 = 0.66 + signedSample1DHashUI32(uint(fragCoord.y * 7.1)) * 0.00159;

    if (uv.x < xTreshold0) { gray = left(fragCoord, radius); }
    else if (uv.x >= xTreshold0 && uv.x < xTreshold1) { gray = middle(fragCoord, radius); }
    else { gray = right(fragCoord, radius); }

    float yTreshold = 0.5 + signedSample1DHashUI32(uint(fragCoord.x * 7.1)) * 0.00159;

    // Output to screen
    if (uv.y > yTreshold) { fragColor = vec4(vec3(1.0 - (gray - 0.5) * (gray - 0.5)),1.0); }
    else { fragColor = vec4(vec3(gray),1.0); }
}

float eta = 1.0 / 1.501;
float FP = 1.24;

vec3 linearLight( vec3 s, vec3 d )
{
	return 2.0 * s + d * 2.0 - 1.0;
}

vec2 uvToCuv( vec2 uv, vec2 maxuv )
{
    return 2.0 * uv - maxuv;
}

vec2 cuvToUv( vec2 cuv, vec2 maxuv )
{
    return (cuv + maxuv) * 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float fract_time = fract(iTime);
    float cos_time = (cos(iTime));

    vec2 uv = fragCoord/iResolution.y;
    vec2 maxuv = vec2(iResolution.x / iResolution.y, 1.0);  

    //centrilize uv coordinates   
    vec2 cuv = uvToCuv(uv, maxuv);

    fragColor = texture(iChannel0, uv);

    vec2 ballCoord = vec2(cos_time, 0.0);
    float ballRadius = 0.5;

    float dist = distance(ballCoord, cuv);

    if (dist < ballRadius * 0.995)
    {	
        vec2 balluv = (cuv - ballCoord) / ballRadius;
        vec3 norm = vec3(balluv.x, balluv.y, -sqrt(1.0 - dist / ballRadius));

		vec3 spec = reflect(vec3(0, 0, 1), norm);
        vec3 refr = refract(vec3(0, 0, 1), norm, eta);

		vec4 ambient = vec4(0.09, 0.08, 0.07, 0.0);
		vec4 spec_light = texture(iChannel0, cuvToUv(spec.xy + ballCoord, maxuv));
        vec4 refr_light = texture(iChannel0, cuvToUv(refr.xy + ballCoord, maxuv));

        float F = (1.0 - eta) * (1.0 - eta) / ((1.0 + eta) * (1.0 + eta));
        float ratio = F + (1.0 - F) * pow(dist / ballRadius, FP);

        fragColor = mix(refr_light, spec_light, ratio) + ambient;

        //glare drawing
        vec2 lightPos = vec2(cos_time, 0.5);
        float distanceToLight = abs(cos_time) * 0.5 + 0.5;
        vec4 lightColor = vec4(0.8, 1.0, .85 ,1);

        float glareSize = 0.5;
        glareSize *= distanceToLight;

        float glareHardness = 0.0;
        float glareMax = 1.0;

        vec2 glareOffset = -lightPos * 0.5;   

        float refrGlareDistance = distance(glareOffset, refr.xy);
        float specGlareDistance = distance(glareOffset, spec.xy);

        if (refrGlareDistance < glareSize)
        {
            float glarePower = 1.0 - smoothstep(glareSize * glareHardness, glareSize, refrGlareDistance);
            fragColor = mix(fragColor, vec4(linearLight(fragColor.rgb, lightColor.rgb), 1.0 ), glarePower * glareMax);
        }

        if (specGlareDistance < glareSize)
        {
            float glarePower = 1.0 - smoothstep(glareSize * glareHardness, glareSize, specGlareDistance);
            fragColor = mix(fragColor, vec4(linearLight(fragColor.rgb, lightColor.rgb), 1.0), glarePower * glareMax);
        }
    }
}

vec2 quadratic(vec2 p1, vec2 p2, vec2 p3, float t) {
    // mix(a, b, t) == (1 - t) * a + t * b
    return mix(
        mix(p1, p2, t),
        mix(p2, p3, t),
        t);
}
vec2 cubic(vec2 p1, vec2 p2, vec2 p3, vec2 p4, float t) {
    return mix(
        quadratic(p1, p2, p3, t),
        quadratic(p2, p3, p4, t),
        t
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Time variable from 0 to 1
    float t = mod(iTime, 1.);

    // The four points defining the curve
    vec2 p1 = vec2(0, 0); // The starting position
    vec2 p2 = vec2(0, 1); // The position that the point moves to initially
    vec2 p3 = vec2(1, 1); // The position that the point moves away from at the end
    vec2 p4 = vec2(1, 0); // The ending position

    // The resulting point for the current time
    vec2 p = cubic(p1, p2, p3, p4, t);

    // The point rendered to the screen
    vec2 uv = fragCoord;
    vec3 col = vec3(smoothstep(2., 3., length(p*iResolution.xy-uv)));
    fragColor = vec4(col,1.0);
}

