mat3 rotateMat(vec3 p, float angle, vec3 axis) {
	vec3 a = normalize(axis);
	float s = sin(angle),
	      c = cos(angle),
	      r = 1. - c;
	return mat3(a.x * a.x * r + c, a.y * a.x * r + a.z * s, a.z * a.x * r - a.y * s, a.x * a.y * r - a.z * s, a.y * a.y * r + c, a.z * a.y * r + a.x * s, a.x * a.z * r + a.y * s, a.y * a.z * r - a.x * s, a.z * a.z * r + c);
}

#define LIGHT	normalize(vec3(1, 1, 0))

float pSphere(float r, vec3 p) { return length(p) - r; }

float pTriPrism(float h, float r, vec3 p) {
	vec3 q = abs(p);
	return max(q.z - h, max(q.x * .866025 + p.y * .5, -p.y) - r * .5);
}

float pCapsule(float r, float h, vec3 p) {
	p.y -= clamp(p.y, 0., h);
	return length(p) - r;
}

float pRoundCone(float r1, float r2, float h, vec3 p) {
	vec2 q = vec2(length(p.xz), p.y);
	float b = (r1 - r2) / h,
	      a = sqrt(1. - b * b),
	      k = dot(q, vec2(-b, a));
	if (k < 0.) return length(q) - r1;
	if (k > a * h) return length(q - vec2(0, h)) - r2;
	return dot(q, vec2(a, b)) - r1;
}

float pEllipsoid(vec3 r, vec3 p) {
	float k0 = length(p / r);
	return k0 * (k0 - 1.) / length(p / (r * r));
}

vec3 mTranslation(vec3 inv_translation, vec3 p) { return p + inv_translation; }

vec3 mRotation(mat3 inv_rotation, vec3 p) { return inv_rotation * p; }

vec3 mMirror(vec3 normal, float dist, vec3 p) { return p - 2. * max(0., dot(normal, p) - dist) * normal; }

vec3 mRepInf(vec3 cell_size, vec3 p) { return p - cell_size * round(p * vec3(greaterThan(cell_size, vec3(0))) / cell_size); }

float oUnion(float d1, float d2) { return min(d1, d2); }

float oSubtraction(float d1, float d2) { return max(d1, -d2); }

float sdf(vec3 p0) {
	float d1, d2, d3, d4, d5, d6, d7;
	{ }
	{
		vec3 p1 = mRepInf(vec3(3, -1, 3), p0);
		{
			mat3 mt = rotateMat(p1, iTime, vec3(0, 1, 0));
			vec3 p2 = mRotation(mt, p1);
			{ d1 = pEllipsoid(vec3(1.2, .05, .1), mTranslation(vec3(0, -.74, 0), p2)); }
		}
		{ d2 = pCapsule(.07, 1., mTranslation(vec3(0, .2, 0), p1)); }
		{
			vec3 p2 = mMirror(vec3(1, 0, 0), .06, p1);
			{ d3 = pSphere(.15, mTranslation(vec3(.17, .1, -.52), p2)); }
		}
		{
			vec3 p2 = mMirror(vec3(1, 0, 0), .06, p1);
			{ d4 = pSphere(.2, mTranslation(vec3(.17, .1, -.52), p2)); }
		}
		{ d5 = pRoundCone(1.2, .5, 1., mTranslation(vec3(0, 3, 0), p1)); }
		{
			vec3 p2 = mMirror(vec3(1, 0, 0), 0., p1);
			{
				vec3 p3 = mTranslation(vec3(.5, .09, 0), p2);
				{ d6 = pTriPrism(.02, .41, mRotation(mat3(.925417, -.163176, .34202, .044233, .942887, .330162, -.376361, -.290409, .879781), p3)); }
			}
		}
		{
			vec3 p2 = mTranslation(vec3(0, .84, 0), p1);
			{ d7 = pRoundCone(1., .5, 1., mRotation(mat3(1, -0, 0, 0, -1, -0, 0, 0, -1), p2)); }
		}
	}
	return oUnion(d6, oUnion(d5, oUnion(oSubtraction(d7, d4), oUnion(d3, oUnion(d2, d1)))));
}

vec3 RayMarch(vec3 rayDir, vec3 cameraOrigin) {
	float dist = .01,
	      totalDist = 0.,
	      itter = 0.;
	vec3 pos = cameraOrigin;
	for (int i = 0; i < 128; i++) {
		dist = sdf(pos);
		itter++;
		totalDist += dist;
		pos += dist * rayDir;
		if (dist < .01 || totalDist > 130.) break;
	}

	return vec3(dist, totalDist, itter / 128.);
}

float AO(vec3 pos, vec3 n) {
	float res = 0.;
	vec3 aopos = pos;
	for (int i = 0; i < 3; i++) {
		aopos = pos + n * .2 * float(i);
		float d = sdf(aopos);
		res += d;
	}

	return clamp(res, 0., 1.);
}

mat3 SetCamera(vec3 ro, vec3 ta, float cr) {
	vec3 cw = normalize(ta - ro),
	     cu = normalize(cross(cw, vec3(sin(cr), cos(cr), 0)));
	return mat3(cu, normalize(cross(cu, cw)), cw);
}

vec4 NorCurv(vec3 p) {
	const vec2 e = vec2(-.01, .01);
	float t1 = sdf(p + e.yxx),
	      t2 = sdf(p + e.xxy),
	      t3 = sdf(p + e.xyx),
	      t4 = sdf(p + e.yyy);
	return vec4(normalize(e.yxx * t1 + e.xxy * t2 + e.xyx * t3 + e.yyy * t4), .25 / e.y * (t1 + t2 + t3 + t4 - 4. * sdf(p)));
}

vec3 Lighting(vec3 n, vec3 rayDir, vec3 reflectDir, vec3 pos) { return vec3(max(0., dot(LIGHT, n)), pow(max(0., dot(reflectDir, LIGHT)), 10.), 1. - max(0., dot(-n, rayDir))) * .5; }

float TriplanarTexture(vec3 pos, vec3 n) { return 0.; }

float BackGround(vec3 rayDir) { return smoothstep(1., 0., clamp(length(rayDir - LIGHT), 0., 1.)) * .5; }

void mainImage(out vec4 fragColor, vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 cameraOrigin = vec3(0);
	if (iMouse.z > 0.) {
		cameraOrigin.x = sin(iMouse.x * .01) * 5.;
		cameraOrigin.y = iMouse.y * .05 - 10.;
		cameraOrigin.z = cos(iMouse.x * .01) * 5. + 15.;
	}
	else {
		cameraOrigin.x = sin(iTime * .25 + 2.) * (6. + sin(iTime * .1));
		cameraOrigin.y = sin(iTime * .3) + 3.;
		cameraOrigin.z = cos(iTime * .25 + 2.) * (6. + sin(iTime * .15));
	}

	vec2 screenPos = uv * 2. - 1.;
	screenPos.x *= iResolution.x / iResolution.y;
	mat3 cam = SetCamera(cameraOrigin, vec3(0, .25, -1), sin(iTime * .15) * .5);
	vec3 rayDir = cam * normalize(vec3(screenPos, 2)),
	     dist = RayMarch(rayDir, cameraOrigin);
	float res,
	      backGround = BackGround(rayDir);
	if (dist.x < .01) {
		vec3 l,
		     pos = cameraOrigin + dist.y * rayDir;
		vec4 n = NorCurv(pos);
		float col,
		      ao = AO(pos, n.xyz);
		l = Lighting(n.xyz, rayDir, reflect(rayDir, n.xyz), pos);
		col = TriplanarTexture(pos, n.xyz);
		col *= n.w * .5 + .5;
		col *= ao;
		col += ao * (l.x + l.y);
		col += l.z * .75;
		col += BackGround(n.xyz) * .25;
		res = col;
	}
	else res = backGround;

	fragColor = vec4(vec3(res), 1);
}

#define Rf 0.05
#define Gf 0.03
#define Bf 0.02
#define K (3.14159265358979323/180.0)
#define SWZ 0.985 

vec3 vScreenRGB(in vec2 pos)
{
    return vec3(0.5+0.5*sin(Rf*pos.x+iTime*10.0), 0.5+0.5*sin(Gf*pos.x+iTime*6.0), 0.5+0.5*sin(Bf*(pos.x)+iTime*4.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 clr = vec3(0.0);
    float max_r = min(iResolution.x, iResolution.y)/2.0;
    vec2 c = iResolution.xy/2.0; //center
    float r = length(c-fragCoord);
    if(r<max_r){
        float a = acos((fragCoord.x-c.x)/r)-pow(r, SWZ)*K;
        vec2 p = vec2(c.x+r*cos(a),c.y+r*sin(a));
        clr = vScreenRGB(p);
    } else {
        clr = vScreenRGB(fragCoord);
        //clr+= vec3(vScreenRGB(fragCoord).x*0.5);
        //clr+= vec3(vScreenRGB(fragCoord).y*0.3);
        //clr+= vec3(vScreenRGB(fragCoord).z*0.2);
    }

    fragColor = vec4(clr,1.0);
}

/*
    Description:
        I'm toying with the idea of using newtonian approximation
        rather than distance stepping to find the points of ray
        intersection to a 0 isosurface.
    Discoveries:
        From other experiments, compound distance fields do not work.
        however, for indavidual objects, Newton-marching requires
        orders of magnitude fewer itterations to find an intersection
        with the same level of detail as sphere-tracing. I've come
        to realize that Newton-marching is FAR more complicated than
        sphere-tracing, as for each object requires 3 functions.
        Sphere-tracing is also one of if not the simplest method of
        rendering a scene.
    Notes:
        - the unit sphere case is the most detailed in the comments
          when it comes to explaining the algorithm.
        - I'm unsure of the relative efficiency with many objects.
        - Will need to experiment with space deformation like with
          sphere-tracing.
        - Likely possible to elimate much of the branching in the
          algorithm to make it even more efficient.
        - I suspect that boolien opperations are possible, however,
          they are likely rather complicated to impliment.
        - Given smarter people, or more time, one could define exact
          derivatives for the gradient calculations rather than use
          the hackey approximations.
        - Thanks to some smarter people (oneshade), some analytic
          gradients/derivatives have been added.
    Purpose:
        The entire purpose for trying out these experiments is to find
        a method of rendering volumetric data directly. As in, given
        3D samples at known locations, rendering the 0 iso-surface
        directly. This more of an overview of the algorithm than a full
        demonstration of the end-use.
*/

//-[ Defines for use later ]-//
#define MAXDIST 100.
#define SURF 0.01
#define eps 1e-5
#define STEPS 16
//< NOTE: Notice that the number of steps is FAR less than sphere-trace rendering. >//

//-[ Unit Sphere Functions ]-//
float UnitSphereVal(vec3 p) //<- SDF of a unit sphere at origin
{
    return length(p) - 1.;
}

float GradUSV(vec3 p, vec3 rd) //<- Find the gradient at the point in the direction of the ray.
{
    float v0 = UnitSphereVal(p);
    float v1 = UnitSphereVal(p + rd * eps);

    return (v1 - v0) / eps;
}

void NUSV(in vec3 ro, in vec3 rd, inout vec3 norm, inout float D) //<- Newtonian distance calculation for unit sphere at origin
{
    float t = UnitSphereVal(ro);          //<- Step forward once just to get close
    float v = UnitSphereVal(ro + rd * t); //<- this is the value of the function in first step
    float g = GradUSV(ro + rd * t, rd);   //<- Find the gradient at the point, in the ray's direction
    vec3 inorm = vec3(0.);

    for(int i = 0; i < STEPS && abs(v) > SURF && g != 0.; i++) //<- The Newtonian approximation loop
    {
        t -= v / g;    //<- find the x intercept of the gradient

        v = UnitSphereVal(ro + rd * t);  //<- Update the new value
        g = GradUSV(ro + rd * t, rd);    //<- Update the new gradient
    }

    if(abs(v) <= SURF && t > 0.)   //<- if the t value is positive, and the function value is within the perameters, then you've hit the surface within the view
    {
        inorm = ro + rd * t;  //<- set the internal normal to the normal of the point on the sphere, which is that point in the case of a unit sphere at the origin.
    }
    else
    {
        t = MAXDIST;  //<- otherwise, the point is either out of view, or not on the surface, set it to the max view distance to effectively exclude.
    }

    if(t < D)   //<- if the calculated point is closer than the passed in point...
    {
        norm = inorm;   //<- set the passed in point to the internal calculated normal
        D = t;          //<- set the passed in distance to the calculated distance
    }
}

//-[ Plane Functions ]-//
float SDPlane(vec3 p, vec4 n)
{
    return dot(p, n.xyz) + n.w;
}

float GradSDP(vec3 p, vec3 rd, vec4 n)
{//-> Oneshade's Analytic gradient
    return dot(rd, n.xyz); 
}

void SSDP(in vec3 ro, in vec3 rd, inout vec3 norm, inout float D, in vec4 n)
{
    float t = MAXDIST;

    float sol = (dot(ro, n.xyz) + n.w) / dot(rd, -n.xyz);

    if(sol >= 0.) t = sol;

    if(t < D)
    {
        D = t;
        norm = n.xyz;
    }
}

void NSDP(in vec3 ro, in vec3 rd, inout vec3 norm, inout float D, in vec4 n)
{
    float t = SDPlane(ro, n);
    float v = SDPlane(ro + rd * t, n);
    float g = GradSDP(ro + rd * t, rd, n);
    vec3 inorm = vec3(0.);

    for(int i = 0; i < STEPS && abs(v) > SURF && g !=0.; i++)
    {
        t -= v / g;

        v = SDPlane(ro + rd * t, n);
        g = GradSDP(ro + rd * t, rd, n);
    }

    if(abs(v) <= SURF && t > 0.)
    {
        inorm = n.xyz; //<- Since the plane is partially defined by its normal, just return the normal of the plane.
    }
    else
    {
        t = MAXDIST;
    }

    if(t < D)
    {
        D = t;
        norm = inorm;
    }
}

//-[ Sphere Functions ]-//
float SphereVal(vec3 p, vec4 s)
{
    return length(p - s.xyz) - s.w;
}

float GradSV(vec3 p, vec3 rd, vec4 s)
{ //-> Oneshade's Analytic Gradient
    p -= s.xyz;
    return dot(p, rd) / length(p);
}

void NSV(in vec3 ro, in vec3 rd, inout vec3 norm, inout float D, in vec4 s)
{
    float t = SphereVal(ro, s);
    float v = SphereVal(ro + rd * t, s);
    float g = GradSV(ro + rd * t, rd, s);
    vec3 inorm = vec3(0.);

    for(int i = 0; i < STEPS && abs(v) > SURF && g != 0.; i++)
    {
        t -= v / g;

        v = SphereVal(ro + rd * t, s);
        g = GradSV(ro + rd * t, rd, s);
    }

    if(abs(v) <= SURF && t > 0.)
    {
        inorm = ((ro + rd * t) - s.xyz) / s.w;  //<- the normal of a sphere at a given point on that shpere is simply just that point relative to the sphere's center devided by the radius of that sphere.
    }
    else
    {
        t = MAXDIST;
    }

    if(t < D)
    {
        D = t;
        norm = inorm;
    }
}

//-[ Scalar Field for Sphere ]-//
float SFSphere(vec3 p, vec4 s)
{//-> Here is a raw scalar field of a sphere.
    vec3 q = p - s.xyz;
    return dot(q, q) - (s.w * s.w);
}

float SFSGrad(vec3 p, vec3 rd, vec4 s)
{
    vec3 q = p - s.xyz;
    vec3 g = 2. * q;     //<- true gradient of the scalar field
    return dot(rd, g);
}

void NSFS(in vec3 ro, in vec3 rd, inout vec3 norm, inout float D, in vec4 s)
{//-> Newton-Raphson approximation for the given sphere scalar field
    float t = 0.;
    float v = SFSphere(ro + rd * t, s);
    float g = SFSGrad(ro + rd * t, rd, s);
    vec3 inorm = vec3(0.);

    for(int i = 0; i < STEPS && abs(v) > SURF && g != 0.; i++)
    {
        t -= v / g;

        v = SFSphere(ro + rd * t, s);
        g = SFSGrad(ro + rd * t, rd, s);
    }

    if(abs(v) <= SURF && t > 0.)
    {
        inorm = ((ro + rd * t) - s.xyz) / s.w;  //<- the normal of a sphere at a given point on that shpere is simply just that point relative to the sphere's center devided by the radius of that sphere.
    }
    else
    {
        t = MAXDIST;
    }

    if(t < D)
    {
        D = t;
        norm = inorm;
    }
}

void SSFS(in vec3 ro, in vec3 rd, inout vec3 norm, inout float D, in vec4 s)
{//-> Direct solution of the scalar field for the sphere
    vec3 o = ro - s.xyz;
    vec3 d = rd;
    float r = s.w;

    float a = dot(d, d);
    float b = dot(o, d);
    float c = dot(o, o) - (r * r);

    float dis = b*b - a * c;

    float t = MAXDIST;

    if(dis >= 0.0)
    {
        float z0 = (-b - sqrt(dis)) / a;
        float z1 = (-b + sqrt(dis)) / a;

        t = min(z0, z1);
    }

    if(t < D)
    {
        D = t;
        norm = ((ro + rd * t) - s.xyz) / s.w;
    }
}

