float tSin(float inp){
return (sin(inp)+1.)/2.;
}

vec3 LorenzDifferential(vec3 tinput,float delta){
    float sigma = 20.0;
    float beta= pow(tSin(speed*iTime/1.242)*10.0,2.);
    float ohmega= pow(tSin(speed*iTime/4.35)*10.0,2.);

    float x = tinput.x;
    float y = tinput.y;
    float z = tinput.z;

    vec3 newPoint = vec3(
        (sigma*(y-(x))),
        (x*(ohmega-abs(z)))-y,
        ((x*y)-(beta*z))
    );
    newPoint = newPoint * delta;
    return newPoint;
}


vec3 lorenzAttractor(vec3 start,float delta,float itterations){



	vec3 currentPoint = start;


	for(float i = 0.;i<itterations;i+=1.0){
		vec3 tdelta = LorenzDifferential(currentPoint,delta);
        vec3 newPoint = currentPoint + tdelta;

        if(abs(newPoint.x)==infinity||abs(newPoint.y)==infinity||abs(newPoint.z)==infinity){
            //return vec3(max(-i,newPoint.x),max(-i,newPoint.y),max(-i,newPoint.z));
            return vec3(-i);
        }

		currentPoint = newPoint;
	}

    vec3 finalTDelta = LorenzDifferential(currentPoint,delta*mod(itterations,1.));

	return currentPoint+finalTDelta;
}


