}

#define rep(p,s) (fract(p/s-0.5)-0.5)*s

float sphere(vec3 p, float r)
{
    return length(p) - r;
}

float box(vec3 p, vec3 s)
{
    p = abs(p) - s;
    return max(p.x, max(p.y, p.z));
}

float smin(float a, float b, float h)
{
    float k = clamp((a-b)/ h * .5 + .5, 0., 1.);
    return mix(a,b,k) - k * (1.-k) * h;
}

vec3 smin(vec3 a, vec3 b, float h)
{
    vec3 k = clamp((a-b)/ h * .5 + .5, 0., 1.);
    return mix(a,b,k) - k * (1.-k) * h;
}

vec3 tunnel(vec3 p)
{
    vec3 off = vec3(0);
    float dd = p.z * 0.02;
    dd = floor(dd) + smoothstep(0., 1., smoothstep(0., 1., fract(dd)));
    dd *= 1.7;
    off.x += sin(dd) * 10.;
    off.y += sin(dd * 0.7) * 10.;

    return off;
}

vec3 kif(vec3 p, float t)
{
  float d;
  float s = 10.;
  for(int i = 0; i < 5; ++i)
  {
    p.xy *= rot(t);
    p.xz *= rot(t*.7);
    p = smin(p, -p, -1.);
    p -= s;

    //p -= s + sin(t - length(p));
    s *= 0.4;
  }

  return p;
}

