    uv /= vec2(iResolution.y / iResolution.x, 1.);

    vec3 s = vec3(0, 0, -50);
    vec3 r = vec3(uv, 1);
    //s -= tunnel(s);

    cam(s);
    cam(r);

    s.z += iTime * 10.;
    s.x += sin(iTime / 16.) * 25.;

    vec3 p = s;
    float d = 0.;

    float i = 0.;
    float dd = 0.;
    vec3 off = vec3(0.1, 0, 0);
    for(i = 0.; i < MAX_STEPS; i++)
    {
        d = map(p);
        dd += d;
        d *= 0.4;
        //vec3 n = normalize(map(p) - vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx)));
        if(d < 0.001)
        {
            d = 0.1;
        }
        p += r*d;
    }

    atsph *= 50.;

    vec3 col = vec3(0);
    //col += vec3(pow(1.-i/(MAX_STEPS + 1.), 3.)) * 1.;
    col += pow(at * 0.016, 6.) * vec3(0.5,0,0.5);
    col += pow(atsph * 0.016, 3.) * vec3(1,0,0);
    col += pow(atbsph * 0.2, sin(iTime * 2.)*.1+1.1) * vec3(0,1.,0.);

    //float dist = length(p-s);
    //col -= 1. / dist;


    fragColor = vec4(col, 1.);
}

