#define i_max 512.
#define escape 4.
#define gamma .6

vec2 isq(vec2 z) { // square of a complex number: s=z^2
    vec2 s;
    s.x = z.x*z.x - z.y*z.y; // real part: x^2-y^2
    s.y = 2. * z.x * z.y;    // imag part: 2xy
    return s;
}

float mandel(vec2 c) { // mandelbrot set: does f(z)=z^2+c diverge? iterations until |z|>escape
    vec2 z = vec2(0.);          // z_0 = 0
    float i;
    for (i=0.; i<i_max; i++) {
		z = isq(z) + c;         // f(z)=z^2+c
        if (length(z) > escape) // |z|>escape
            return i + 1. - log(log(length(z)))/log(2.);
    }
    return 0.;
}

mat2 rot(float a) { // rotation by angle a: used by the camera
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // aspect ratio
    vec2 z = 2.*(2.*fragCoord-iResolution.xy)/length(iResolution.xy);

    // magic camera stuff
    z += vec2(-.25, 0.);
    z *= rot(.2*sin(.17*iTime));
    z += vec2(-.25, 0.);
    z *= 0.8+0.2*sin(0.03*iTime);

    // the magic
    float m = mandel(z)/i_max;

    // attept at exposure
    fragColor = vec4(vec3(pow(m,gamma)),10.);
}

