const float PI = 3.14159265359;
vec2 GetCUV(vec2 coords, vec2 limitCoords){
    return (coords-.5*limitCoords.xy)/limitCoords.y;
}

vec2 GetPolarCoord(vec2 uv) {
    vec2 st = vec2(atan(uv.x, uv.y), length(uv));
    st.x /= PI;
    st.x += .5;
    return st;
}

float GetSeepdTime(float time, float speed) {
    return abs(fract(time*speed)-.5)*2.;
}

float N21(vec2 coords) {
    return fract( sin(coords.x*100. + coords.y*6574.)*5647. );
}

float SmoothNoise(vec2 uv){

    vec2 lv  = fract(uv);
    vec2 id  = floor(uv);

    lv = lv*lv*(3.-2.*lv);

    float bl = N21(id+vec2(0,0));
    float br = N21(id+vec2(1,0));
    float b  = mix(bl, br, lv.x);

    float tl = N21(id+vec2(0,1));
    float tr = N21(id+vec2(1,1));
    float t  = mix(tl, tr, lv.x);

    return mix(b, t, lv.y);
}

float SmoothNoise2(vec2 uv){

    float c = SmoothNoise(uv*4.);
    c+= SmoothNoise(uv*8.) *.5;
    c+= SmoothNoise(uv*16.)*.25;
    c+= SmoothNoise(uv*32.)*.125;
    c+= SmoothNoise(uv*64.)*.0625;

    return c/2.;
}

float Pupil(vec2 uv, float t, float br, float er, float bb, float eb) {

    float r  = t*br + (1.-t)*er;
    float b  = t*bb + (1.-t)*eb;

    return smoothstep(r - b, r, uv.y);
}

float Iris(vec2 uv, float r1, float r2, float p) {
    uv.x = abs(uv.x - .5);

    uv.x *= 4.*p+.4;
    //uv.x += p;

    float iris = SmoothNoise2(uv*6.);
    float c = smoothstep(r1, r1-0.015, uv.y);

    return iris * c * smoothstep(r2-0.1*(1.-p), r2, uv.y);
}

vec3 Eye(vec2 uv, float width) {
    vec3 col = vec3(0.,0.,0.);

    uv = GetPolarCoord(uv);
    uv.y *= 0.33;

    uv.x = fract(uv.x + 0.33);
    float iris = Iris(uv, 0.17, 0.07, .04);
    col = mix(col, vec3(0.2,0.5,1.0), iris);

    uv.x = fract(uv.x + 0.1);
    iris = Iris(uv, 0.165, 0.16, .1);
    col  = mix(col, vec3(0.2, 1.0,0.5), iris*.8);

    uv.x = fract(uv.x + 0.33);
    iris = Iris(uv, 0.17, 0.01, .6);
    col  = mix(col, vec3(0.7,0.7,0.7), iris*(1. - uv.y)*.3);

    float pupil = Pupil(uv, width, 0.01, 0.12, 0.005, 0.03);
    col = mix(vec3(0.0,0.0,0.0), col, pupil);

    return col;
}



