    fragColor = vec4(vec3(col), 1.0);
}

#define N 6

float signedTriangleArea(vec2 v1, vec2 v2, vec2 v3) {
    float det = v2.x*v3.y + v3.x*v1.y + v1.x*v2.y - v2.x*v1.y - v1.x*v3.y - v3.x*v2.y;
    return det;
}


float[N] wachspress(vec2 p, vec2[N] poly) {
    float[N] weights;
    float sumweights = 0.0;
    float A_i, A_iplus1, B;


    A_iplus1 = signedTriangleArea(poly[N - 1], poly[0], p);
    for(int i = 0; i < N; i++) {
        A_i = A_iplus1;
        A_iplus1 = signedTriangleArea(poly[i], poly[(i+1) % N], p);     

        //if the polygon is regular this could be kept constant
        B = signedTriangleArea(poly[(i - 1 + N) % N], poly[i], poly[(i+1) % N]);


        weights[i] = B / (A_i*A_iplus1);
        sumweights += weights[i];
    }

    //normalize
    float recp = 1.0/sumweights;
    for(int i = 0; i < N; i++) {
        weights[i] *= recp;
    }
    return weights;
}

float sdSegment(vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p-a, ba = b-a;
    float A = dot(pa,ba)/dot(ba,ba);
    float h = clamp( A, 0.0, 1.0 );

    return length( pa - ba*h);
}

float sdScene(vec2 p,     vec2[N] poly) {
    float d = sdSegment(p, poly[0], poly[1]);

    for(int i = 0; i < N; ++i) {
       d = min(d, sdSegment(p, poly[i], poly[(i+1) % N])); 
    }

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y - vec2(0.25, 0.0);

    vec3 col = vec3(1.0);


    //create a polygon
    vec2[N] poly;
    float R = 0.5;
    float tpi = 2.0*3.14159;

    float incr = 1.0/float(N);
    float phi = 0.0;
    for(int i = 0; i < N; ++i) {
        poly[i] = vec2(0.5, 0.5) + R * vec2(cos(tpi * phi), sin(tpi * phi));

        phi += incr;
    }

    //perturb right most vertex
    poly[0] += 0.25*vec2(sin(iTime), 0.0);


    float[N] weights = wachspress(uv, poly);
    float d = sdScene(uv, poly);



    //change v to [0,..,N-1] to show the other coordinate functions    
    int v = 0;

    //render isolines
    float linewidth = 0.005;
    float z = 0.001;
    if(weights[0] > 0.0 && weights[1] > 0.0 && weights[2] > 0.0 && weights[3] > 0.0) {
        //to change number of isolines    
        float w = 0.1;
        for(int i = 0; i < 9; ++i) {
            if(weights[v] > (w - linewidth) && weights[v] < (w + linewidth) ) {
                float f = weights[v] - (w - linewidth);
                f /= 2.0 * linewidth;

                if(f < 0.5) {
                    col = vec3(smoothstep(1.0, 0.0, f/0.5));
                }  else {
                    col = vec3(smoothstep(0.0, 1.0, (f - 0.5)/0.5));
                }
            }
            w += 0.1;
            linewidth += z;
        }
    }

    //draw polygon through segments
    linewidth = 0.005;
    float e = smoothstep(0.0, 1.0, d / linewidth);
    if(e < 1.) {
        fragColor = vec4(vec3(e), 1.0);
    } else {
        fragColor = vec4(col, 1.0);
    }
}
