#define PI acos(-1.)

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define rep(p,c) p=(mod(p-c*.5,c)-c*.5)
#define hash11(x) fract(sin(x)*242.4)

#define frt(sp,off) fract((iTime+off)*sp)
#define flt(sp,off) floor((iTime+off)*sp)

struct obj
{
    float d;
    vec3 sha;
    vec3 li;
};

obj minobj (obj a, obj b)
{if(a.d<b.d)return a; else return b;}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}

float sc (vec3 p, float d)
{
    p=abs(p);
    p=max(p,p.yzx);
    return min(p.x,min(p.y,p.z))-d;
}

float idx,speed;
obj stairs (vec3 p)
{
    p.y += .6;
    float perx = 2.;
    idx=floor((p.x-perx*.5)/perx);
    speed = .5+hash11(idx)*2.;
    p.z -= flt(speed,0.)+pow(frt(speed,0.),5.)*.5+.1;
    rep(p.x,perx);

    float perz = .5;
    float idz = floor((p.z-perz*.5)/perz);

    rep(p.z,perz);
    float d = box(p,vec3(.4,2.,.2));

    return obj(d,vec3(0.,0.1,0.3),vec3(0.3,0.99,0.5));
}

obj cubes (vec3 p)
{
    float id = floor((p.x-1.)/2.);
    float anim = (PI/2.)*(flt(speed,0.)+pow(frt(speed,0.),3.));
    rep(p.x,2.);
    rep(p.z,3.);
    p.y -= 1.7;
    p.yz *= rot(-anim);
    float d = max(-sc(p,.13),box(p, vec3(.2)));
    d = hash11(id)<.5?min(d, box(p,vec3(.12))):d;
    return obj(d, vec3(0.5,.0,.1), vec3(.99,.45,.8));
}

obj SDF (vec3 p)
{
    p.yz *= rot(-atan(1./sqrt(2.)));
    p.xz *= rot(PI/4.); 

    p.y += asin(sin(idx*1.4))*.4;

    obj scene = stairs(p);
    scene = minobj(scene,cubes(p));

    return scene;
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.01,0.);
    return normalize(SDF(p).d-vec3(SDF(p-eps.xyy).d,SDF(p-eps.yxy).d,SDF(p-eps.yyx).d));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;

    vec3 ro = vec3(uv*4.,-30.), rd=vec3(0.,0.,1.), p=ro,
    col=vec3(0.),l=normalize(vec3(1.,2.,-2.));
    obj O;

    for (float i=0.;i<64.;i++)
    {
        O = SDF(p);
        if (O.d<0.001) break;
        p += O.d*rd*.5;
    }

    vec3 n = getnorm(p);
    float light = max(dot(n,l),0.);
    col = mix(O.sha, O.li, light);

    fragColor = vec4(sqrt(col),1.);
}

const float SceneDurationSeconds = 3.;
const float Complexity = 10.;
const float ResolutionDivisor = 1.;

vec4 hash42(vec2 p)
{
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

mat2 rot2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

float bayer8x8(vec2 uvScreenSpace) {
  return texture(iChannel0, uvScreenSpace / (ResolutionDivisor * 8.)).r;
}


void mainImage(out vec4 o, vec2 C) {
  vec2 uv = C / iResolution.xx;
  //uv += iMouse.xy/iResolution.xy*.1 - .5;
  uv += 100.; // seed @ 0 is just black because of rng
  float q = 1.;
  vec4 h;
  float sh = 1.0;

  float scene = floor((iTime + 100.) / SceneDurationSeconds);
  vec4 hscene = hash42(uv - uv + scene);
  uv.x += scene;
  float escape = 1. - hscene.x * .12;
  float Speed = hscene.z * .01;

  for (float i = 0.0; i < Complexity; ++i) {
    vec2 cell = floor(uv / q) * q;
    vec2 sq = abs(fract(uv / q));
    sh *= 1. - pow(max(sq.y, max(max(1. - sq.y, sq.x), 1. - sq.x)), 3.5);
    h = hash42(cell);
    if (i > 3. && h.w > escape)
      break;
    uv.x += iTime * sin(h.z * 6.28) * Speed * (i + 1.);
    uv *= rot2D(h.w * 6.28 * hscene.z);
    uv *= 1.2 +
          hscene.y *
              h.y; // scale; there's a small chance of under layer being bigger.
  }
  o = h * pow(sh, .25);
  //o = pow(o, vec4(.6));
  vec2 uvn = C / iResolution.xy - .5;
  float v = 1. - dot(uvn, uvn * 1.6);
  o *= Complexity / 12.; // it's just really dark otherwise

  vec4 rotated = o;
  rotated.xy *= rot2D(iTime * .4);
  rotated.yz *= rot2D(iTime * .4 * .618);
  rotated = abs(rotated);
  rotated = clamp(rotated, 0., 1.);
  o = mix(o, rotated, .3);
  o = abs(o);
  o = clamp(o, 0., 1.);
  o = mix(o, o - o + (max(o.x, max(o.y, o.z))), .4);

  o += (bayer8x8(C) - .5) * .15;
  o = step(o - o + .12, o);
  o *= v;
}

#define STEPS 200.0
#define MDIST 150.0
#define pi 3.1415926535
#define pmod(p,x) (mod(p,x)-0.5*(x))
mat2 rot(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}

float box(vec3 p, vec3 b){
    vec3 d = abs(p)-b;
    return max(d.x,max(d.y,d.z));
}

vec3 glow;

vec2 pmodp(vec2 p, float x){
    float a = atan(p.y,p.x);
    a = mod(a-x/2.,x)-x/2.;
    return vec2 (cos(a),sin(a))*length(p);
}
float vSeg(vec3 p, float h, float r){
  p.y -= clamp(p.y, 0.0, h);
  return length(p)-r;
}

vec2 map(vec3 p){
    vec2 a = vec2(1);
    vec2 b = vec2(2);
    vec3 po = p;
    float t= mod(iTime,999.);
    p.xz*=rot(t*0.2);

    //Log-polar mapping from
    //https://www.osar.fr/notes/logspherical/
    vec2 p2 = p.xz;
    float r = length(p2);
    p2 = vec2(log(r),atan(p2.y,p2.x));  
    float scl = 12.0/pi;

    t*=0.65;
    p2.x+=t;
    float yoff = 20.5;

    //p.y+=t*scl*(2.0/3.0)*yoff;

    p2 *= scl;

    vec2 id = vec2(floor((p2.x)/1.5)+0.5,floor(p2.y/1.5)+0.5);

    p2 = pmod(p2,1.5);

    p.y-=sin(id.y+t)*0.75;
    p.y-=sin(id.x+t*2.5)*0.75;

    float dSpd = 10.0;
    float uSpd = 15.0;

    //apply up/down warp-in motion
    float ring =smoothstep(6.0,9.0,id.x-t*scl*(2.0/3.0))*yoff*dSpd*floor(mod(id.x,2.0));
          ring -= smoothstep(6.0,9.0,id.x-t*scl*(2.0/3.0))*yoff*uSpd*floor(mod(id.x+1.0,2.0));
    p.y -= ring;

    float mul = r/(scl);

    vec3 p3 = vec3(p2.x, p.y/mul,p2.y);

    //======BEGIN NON-DOMAIN WARPED SDF======  

    //Boxes
    a.x = box(p3,vec3(0.6,0.6+0.15,0.6));
    a.x = max(a.x,-box(p3,vec3(3.0,0.3,0.3)));
    //a.x-=0.1;

    //anti overstep artifact hidden stuff
        vec3 d = abs(p3)-1.0;
        float outerBox = max(d.x,d.z);    
        if(length(po.xz)>0.1) a.x = min(-outerBox,a.x);

    //=======END NON-DOMAIN WARPED SDF=======
    a.x*=mul;

    //if(length(po.xz)<0.3) a.x = 0.1;
    a.x = max(a.x,(length(p.xz)-20.0));
    a.x = max(a.x,-(length(p.xz)-0.3)); //this ended up being useless

    p = po;
    //GLOWY BEAM THING
    b.x = length(p.xz)-0.8-sin(p.y-t*10.0)*0.2-sin(p.y*0.3-t*20.0)*0.2-abs(p.y)*0.2;
    glow+=vec3(0.529,0.400,1.000)*0.1/(0.1+b.x*b.x);

    a =(a.x<b.x)?a:b;

    //ROTATING SEGMENTS
    p.zx*=rot(t);
    float alpha = atan(p.z,p.x);
    p.y = abs(p.y)-9.0;
    p.xz = pmodp(p.zx,pi/16.);
    p-=vec3(17,sin(alpha*3.0-t)*2.0,0);
    p.xy*=rot(alpha*1.5-t*3.0);
    p.y+=2.;

    b.x = vSeg(p, 4.0,0.4);
    b.y = 3.0;
    a =(a.x<b.x)?a:b;

    return a;
}


vec3 norm(vec3 p, float s){
    vec2 e= vec2(s,0);
    return normalize(vec3(
    map(p+e.xyy).x,map(p+e.yxy).x,
    map(p+e.yyx).x)-vec3(map(p-e.xyy).x,
    map(p-e.yxy).x,map(p-e.yyx).x));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);
    float t = mod(iTime,999.);
    vec3 ro = vec3(0,2.0+14.0*sin(t),-29.0);

    vec3 lk = vec3(0,0,0);
    vec3 f = normalize(lk-ro);
    vec3 r = normalize(cross(vec3(0,1,0),f));
    vec3 rd = normalize(f*0.7+uv.x*r+uv.y*cross(f,r));

    float dO, shad;
    bool hit = false;
    vec2 d; vec3 p;

    for(float i = 0.0; i<STEPS; i++){
        p = ro+rd*dO;
        d = map(p);
        dO+= d.x*0.85;

        if(abs(d.x)<0.005){
            shad = i/STEPS;
            hit = true;
            break;
        }
        if(dO>MDIST){
            dO = MDIST; break;
        }
    }
    if(hit){
        vec3 n = norm(p,0.02);
        float edge = length(n-norm(p,0.12))*min(1.0,length(p)*0.2);
        //edge =smoothstep(0.,0.15,edge);

        if(d.y == 1.0) col = vec3(0.388);
        if(d.y == 2.0) col = vec3(0.502,0.000,1.000);
        if(d.y == 3.0) col = vec3(0.388);

        //Shamelessly stealing blackle's lighting techniques 
        float ao = smoothstep(-.1,.1,map(p+n*.1).x)*
                   smoothstep(-.3,.3,map(p+n*.3).x)*
                   smoothstep(-.7,.7,map(p+n*.7).x);
        vec3 r = reflect(rd, n);
        float diff = length(sin(n*3.)*.7+.3)/sqrt(3.);
        float spec = length(sin(r*3.)*.5+.5)/sqrt(3.);
        float fres = 1.-abs(dot(rd,n))*.95;
        col = col*pow(diff,3.0) + pow(spec, 12.)*fres*vec3(0.463,0.490,0.898);
        col*=ao;

        if(d.y == 1.0)col+=smoothstep(0.,0.1,edge)*vec3(0.188,0.804,0.910);
        if(d.y == 3.0)col+=smoothstep(0.,0.15,edge)*vec3(0.659,0.188,0.910)*10.0;
    }

    col = mix(col,mix(vec3(0.075),vec3(0.),length(uv)),dO/MDIST);
    col+=glow*0.6;
    fragColor = vec4(col,1.0);
}

// BASED ON:
// "ShaderToy Tutorial - Ray Marching for Dummies!" 
// by Martijn Steinrucken aka BigWings/CountFrolic - 2018
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
// This shader is part of a tutorial on YouTube
// https://youtu.be/PGtv-dBi2wE

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

float GetDist(vec3 p) {
	vec4 s = vec4(0, 1, 6, 1);

    float sphereDist =  length(p-s.xyz)-s.w + noise(p.xy * 10.0) * 0.5 * (2.95 + pow(sin(iTime), 2.0));
    //float planeDist = p.y;

    //float d = min(sphereDist, planeDist);
    float d = sphereDist;

    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 30, 0);
    lightPos.xz += vec2(sin(iTime), cos(iTime))*2.;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l);
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 1, 3);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d;

    //float dif = GetLight(p);
    float dif = (GetDist(p)<=SURF_DIST) ? GetLight(p) : 0.3;

    col = vec3(dif);

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float dist = distance(fragCoord, iResolution.xy/2.0);
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col;
    if(dist < iResolution.y/4.0) {
        col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    }
    else {
        col = 0.5 + 0.5*sin(iTime+uv.xyx+vec3(0,2,4));
    }
    fragColor = vec4(col,1.0);
}

#define max2(v) max((v.x),(v.y))

const float pi = acos(-1.);
const float pi2 = pi*2.;

mat2 rot(float a)
{
    float c=cos(a),s=sin(a);
    return mat2(c,-s,s,c);
}

float circle(vec2 p, float r)
{return length(p)-r;}

float box(vec2 p, vec2 b)
{
p=abs(p)-b;
return max(p.x,p.y)+length(max(p,0.));
}

float smin( float a, float b, float k )
{
    float res = exp2( -k*a ) + exp2( -k*b );
    return -log2( res )/k;
}

float sdf(float d)
{
    return smoothstep(0.,fwidth(d)*1.5,d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/max2(iResolution.xy);

    float w = fwidth(uv.y);

    vec3 col = vec3(0);



    float d[] = float[](1e3,1e3);

    {
        vec2 p = uv;
        float k = cos(iTime*2.);
        k = 2.*abs(k)-1.;
        p.y -= k*.45;
        d[0] = circle(p,.1);
        p = rot(-cos(iTime*2.)*pi2)*p;
        d[1] = min(d[1],box(p,vec2(.1-w*7.5))-w*15.);
    }

    {
        vec2 p = uv;
        float k = 24.;
        d[0] = smin(d[0],-p.y,k);
        d[1] = smin(d[1],p.y,k);               
    }

    float mask = sdf(uv.y);

    float d0 = sdf(d[0]);
    float d1 = sdf(d[1]);
    float fd = mix(1.-d0,d1,mask);

    col += fd;


    fragColor = vec4(col,1.0);
}

//  https://www.shadertoy.com/view/4djSRW
///  2 out, 2 in...
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec2 randomGradient(vec2 point)
{
    return -1.0 + 2.0 * hash22(point);
}

float perlin_noise(vec2 position)
{
    vec2 gridCell = floor(position);
    vec2 local = position - gridCell;

    vec2 weight = local * local * (3.0 - 2.0 * local);

    return mix(
                mix(
                    dot(local - vec2(0,0), randomGradient(gridCell + vec2(0,0))),
                    dot(local - vec2(1,0), randomGradient(gridCell + vec2(1,0))),
                    weight.x),
                mix(
                    dot(local - vec2(0,1), randomGradient(gridCell + vec2(0,1))),
                    dot(local - vec2(1,1), randomGradient(gridCell + vec2(1,1))),
                    weight.x),
                weight.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    uv += iTime * 0.2;
    uv *= 8.0;

    // Time varying pixel color
    vec3 col = vec3(perlin_noise(uv));

    col += 1.0;
    col /= 2.0;

    // Output to screen
    fragColor = vec4(col,1.0);
}

const float PI = 3.1415926535;

float SampleChessBoard(vec2 uv)
{
    return step(0.0,sin(uv.x  * 12.0 * PI + PI) + sin(uv.y  * 6.75 * PI + PI));
}


float SuperSample(vec2 uv,vec2 Resolution)
{
    float SSAAX = 1.0/(Resolution.x * 2.0);
    float SSAAY = 1.0/(Resolution.y * 2.0);
    float sample0 = SampleChessBoard(uv + vec2(SSAAX,SSAAY));
    float sample1 = SampleChessBoard(uv + vec2(SSAAX,-SSAAY));
    float sample2 = SampleChessBoard(uv + vec2(-SSAAX,SSAAY));
    float sample3 = SampleChessBoard(uv + vec2(-SSAAX,-SSAAY));

    return (sample0 + sample1 + sample2 + sample3)/4.0;
}


vec2 RotateUV(vec2 uv,float radian)
{
    return vec2(uv.x * cos(radian) - uv.y * sin(radian), uv.x * sin(radian) + uv.y * cos(radian));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = RotateUV(fragCoord,iTime*0.3)/iResolution.xy;

    uv += 0.5*cos(iTime);

    //uv = RotateUV(uv,iTime);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float ChessBoard = SuperSample(uv,iResolution.xy);


    col = vec3(ChessBoard,ChessBoard,ChessBoard);


    // Output to screen
    fragColor = vec4(col,1.0);
}

float Circle (vec2 uv, vec2 p, float radius, float blur) {
    float d = length(uv-p);
    float c = smoothstep(radius, radius-blur, d);
    return c;
}

float Mask (vec2 uv, vec2 p, float size) {
    uv -= p; //Translate Coordinate System
    uv /= size; //Scale Coordinate System
    float mask = Circle(uv,vec2(0.0),0.4,0.01);
    mask -= Circle(uv,vec2(-0.2,0.0),0.03,0.01);
    mask -= Circle(uv,vec2(0.2,0.0),0.03,0.01);
    return mask;
}

float Band (float t, float start, float end, float blur) {
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);
    return step1*step2;
}

float Rect (vec2 uv, vec4 dims, float blur) {
    float band1 = Band(uv.x,dims.x,dims.x+dims.z,blur);
    float band2 = Band(uv.y,dims.y,dims.y+dims.w,blur);
    return band1*band2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    vec3 col = vec3(1.0);
    uv-=0.5;
    uv.x *= iResolution.x/iResolution.y;

    uv.x+=sin(uv.y*5.0-iTime*6.)/(5.0+cos(iTime));

    float mask = Mask(uv, vec2(0.0,0.0), 0.7);
    mask = Rect(uv, vec4(-0.3,-0.2,0.6,0.4),.01);
    // Output to screen
    fragColor = vec4(col,1.0)*mask;
}

float getSquareSine(float x) {
    return sign(sin(x));
}

float getRotatingMagnifier(vec2 uv, float radius, float rotationDisplacement) {
    return distance(
        uv,
        vec2(
            sin(iTime + rotationDisplacement),
            cos(iTime + rotationDisplacement)
        )
    ) * radius;
}


float getChessPattern(vec2 uv, float size) {
    return (
        (
            getSquareSine(-uv.x * size) + getSquareSine(-uv.y * size)
        ) * -1. * (
            getSquareSine(uv.x * size) + getSquareSine(uv.y * size)
        )
    );
}

float getDistortedTable(vec2 uv, float radius, float size, float rotationDisplacement) {
    float magnifier = getRotatingMagnifier(uv, radius, rotationDisplacement);

    return (
        getChessPattern(
            uv

            // table distortion
            * magnifier
            ,
            size
        ) * magnifier * (1. - magnifier)
    );
}

float getDistortionStack(vec2 uv, float slicingSteps) {
    float distortionStack = 0.;

    for (float timeSlice = 1.; timeSlice <= pow(2., slicingSteps); timeSlice += timeSlice) {
        distortionStack +=
             getDistortedTable(uv, 1., 50., sin(iTime / timeSlice))
           + getDistortedTable(uv, 1., 50., cos(iTime / timeSlice));
    }

    return distortionStack;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-.5;

    uv.y *= iResolution.y / iResolution.x;

    float distortionStack = getDistortionStack(uv, 3.);

    vec3 col = distortionStack * vec3(1);

    fragColor = vec4(col,1.0);
}

// Simplex 2D noise
//https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
vec3 permute(vec3 x) { return mod(((x*34.0)+1.0)*x, 289.0); }

float snoise(vec2 v){
  const vec4 C = vec4(0.211324865405187, 0.366025403784439,
           -0.577350269189626, 0.024390243902439);
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod(i, 289.0);
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
  + i.x + vec3(0.0, i1.x, 1.0 ));
  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy),
    dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}


float metaball(in float radius, in vec2 st){
    return (radius-length(st))/length(st);;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float px = 1.5/iResolution.x;

    vec2 uv = fragCoord/iResolution.y;
    uv -= 0.5*vec2(iResolution.x/iResolution.y, 1.0);

    float t = iTime/12.;
    float t2 = t+0.005;
    float t3 = t+0.01;

    float rsum = 0.; 
    float num = 12.;
    for(float i=0.; i<num; i++){
        float r = snoise(vec2(t, i*300.))*0.4+0.6;
        vec2 pos = vec2(
            snoise(vec2(i+200., t)), 
            snoise(vec2(i*20., t+200.))
            );
        rsum += metaball(r, uv-pos);
    }
    rsum = smoothstep(fwidth(rsum),0.,rsum);

    float gsum = 0.; 
    for(float i=0.; i<num; i++){
        float r = snoise(vec2(t2, i*300.))*0.4+0.6;
        vec2 pos = vec2(
            snoise(vec2(i+200., t2)), 
            snoise(vec2(i*20., t2+200.))
            );
        gsum += metaball(r, uv-pos);
    }
    gsum = smoothstep(fwidth(gsum),0.,gsum);

    float bsum = 0.; 
    for(float i=0.; i<num; i++){
        float r = snoise(vec2(t3, i*300.))*0.4+0.6;
        vec2 pos = vec2(
            snoise(vec2(i+200., t3)), 
            snoise(vec2(i*20., t3+200.))
            );
        bsum += metaball(r, uv-pos);
    }
    bsum = smoothstep(fwidth(bsum),0.,bsum);

    vec3 col = rsum*vec3(1,0,0) + bsum*vec3(0,0,1) + gsum*vec3(0,1,0);
    col = 1. - col;
    //vec3 col = smoothstep(1., 1.-px, 
    //    vec3(rsum, gsum, bsum)
    //    );

    fragColor = vec4(col,1.0);
}

#define NUM_EXPLOSIONS 7.
#define NUM_PARTICLES 100.
#define inv_nparticels (1./NUM_PARTICLES)
#define PI 3.1415926

float Hash11(float t){
    return fract(sin(t*613.2)*614.8);
}
vec2 Hash12(float t){
  return vec2(fract(sin(t*213.3)*314.8)-0.5,fract(sin(t*591.1)*647.2)-0.5);
}

vec2 Hash12_Polar(float t){
    float o = fract(sin(t*213.3)*314.8)*PI*2.0;
    float r = fract(sin(t*591.1)*647.2);
    return vec2(sin(o)*r,cos(o)*r);
}

float Explosion(vec2 uv, float t)
{
    float fract_t=fract(t);
    float floor_t=floor(t);
    float power=0.3+Hash11(floor_t);
    float sparks=0.;
    for(float i=0.;i<NUM_PARTICLES;i++)
    {
        vec2 dir=Hash12_Polar(i*floor_t)*1.;
        float inv_d=1./(length(uv-dir*sqrt(fract_t)));
        float brightness=mix(0.3,0.09,smoothstep(0.,0.1,fract_t))*(1.0-(0.5+0.5*Hash11(i))*fract_t);
        float sparkling= .5+.5*sin(t*10.2+floor_t*i);
        sparks+=power*brightness*sparkling*inv_nparticels*inv_d;
    }
    return sparks;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col=vec3(0);    

    for(float i=0.;i<NUM_EXPLOSIONS;i++){
        float t=iTime*(0.3+0.4*Hash11(i))+i/NUM_EXPLOSIONS;
        float fract_t=fract(t);
        float floor_t=floor(t);

        vec3 color=0.7+0.3*sin(vec3(.34,.54,.43)*floor_t*i);
        vec2 center = Hash12(i+10.+5.*floor_t);
        col+=Explosion(uv-center,t)*color;
    }
    col-=0.1;
    fragColor = vec4(col,1.0);
}

/*
Menger Sponge with simple reflection.
NO OPTIMIZED!
*/

//scene params
#define SpongeMaxIterations 4
#define CyclePeriod 10.0
#define IdlePeriod 6.0
#define SolidPeriod 4.0

//quality params
#define subSamples 2

//uv screen params
#define du 0.5
#define dv 0.5
#define iR vec2(1.0/iResolution.x, 1.0/iResolution.y)
//camera params
#define CameraZoom 0.75
#define CameraDistance 5.0
//mice params
#define miceFactor vec2(-9.0, 3.0)
//march tracing params
#define MarchMaxDistance 1e3
#define MarchSteps 80
#define MarchThreshold 1e-3
#define MarchNormalPrecision 0.03/float(SpongeMaxIterations+1)



float mtf;

mat2 rotate(float a){
    float s = sin(a), c = cos(a);
    return mat2(c,-s,s,c);
}
float sdSphere(vec3 orgn, vec3 pos, float r){
    return length(pos-orgn)-r;
}
float sdBox(vec3 orgn, vec3 pos, vec3 size){
    vec3 p = abs(pos-orgn)-size;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);

}
float crosshair(vec3 orgn, float size){
    float far = 200.0;
    return min(min(sdBox(orgn,vec3(0.0),vec3(far,size,size)), sdBox(orgn,vec3(0.0),vec3(size,far,size))),sdBox(orgn,vec3(0.0),vec3(size,size,far)));
}
float mengerSponge(vec3 orgn, vec3 pos,float size, int iterations){
    pos = pos-orgn;
    vec3 c = abs(pos)-size;
    float dist = max(max(c.x,c.y),c.z);

    vec3 p = pos;
    float space;
    float ii;
    for(int i=1; i<=iterations; i++){
        ii = float(i);
        p = pos;
        space = size*pow(1.0/3.0,ii-1.0) * 2.0;
        p = mod(p+space*0.5,space);
        p = p-space*0.5;
        dist = max(-crosshair(p, size*pow(1.0/3.0,ii)), dist);
    }
    return dist;
}


float distMap(vec3 pos){
    float initSize = 0.5;

    //distField optomize
    float phase1 = sdSphere(pos, vec3(0.0), 1.75*initSize*pow(pow(27.0/20.0,float(SpongeMaxIterations)),1.0/3.0));
    if(phase1 > MarchThreshold*10.0){
        return phase1;
    }

    int it1 = 0;
    int it2 = 0;
    int maxIterations = SpongeMaxIterations;



    float period = CyclePeriod;
    float idle = IdlePeriod;
    float solid = SolidPeriod;
    float fullPeriod = period * float(SpongeMaxIterations+1);

    float transFunction = min(mod(iTime,period)/(period-idle),1.0);
    float solidFunction = max((min(abs(-2.0*(mod(iTime,period)-(period-idle)-idle/2.0)/idle),1.0)-1.0)*(2.0/(idle-solid))+1.0, 0.0);
    float influFunction = pow(smoothstep(0.0,1.0,transFunction),1.0);
    mtf = solidFunction;

    it1 = int(mod(iTime/period,float(maxIterations+1)));
    it2 = int(mod((iTime+period)/period,float(maxIterations+1)));


    float influence = pow(pow(27.0/20.0,1.0/3.0),float(it1)+influFunction);




    if(it2==0){
        mtf = 1.0;
        influence = mix(pow(pow(27.0/20.0,1.0/3.0),float(maxIterations)),1.0,1.0-pow(smoothstep(1.0,0.0,transFunction),3.0));
    }

    float fig1 = mengerSponge(pos, vec3(0.0,0.0,0.0), initSize*influence, it1);
    float fig2 = mengerSponge(pos, vec3(0.0,0.0,0.0), initSize*influence, it2);

    return mix(fig1, fig2, smoothstep(0.0,1.0,transFunction)) + (0.01*sin(length(pos)*9.0-iTime*3.9)/(influence+1.0) + (sin(pos.x*15.7+iTime*9.0)+sin(pos.y*16.1+iTime*7.0)+sin(pos.z*17.2+iTime*8.0))*0.001)*(pow(mtf,3.0))/float(it1+1);
}
vec3 getNormal(vec3 pos){
    float e = MarchNormalPrecision;
    int id;
    vec3 options;
    float anchor = distMap(pos);
    return normalize(vec3(
        anchor - distMap(pos-vec3(e,0.0,0.0)),
        anchor - distMap(pos-vec3(0.0,e,0.0)),
        anchor - distMap(pos-vec3(0.0,0.0,e))
     ));
}

vec4 rayMarch(vec3 position, vec3 direction, float dirFactor, out float pathLen){
    float dist = 0.0;
    pathLen = 0.0;
    for(int i=0; i<MarchSteps; i++){
        dist = distMap(position);
        pathLen += dist;
        if(dist<=MarchThreshold || dist>=MarchMaxDistance) break;
        position += direction*dirFactor*dist;
    }
    return vec4(position, dist);
}

vec2 mice(){
    return vec2(iMouse.x/iResolution.x-.5,iMouse.y/iResolution.y-.5);
}

vec3 getDirection(vec2 uv, vec2 angle, float zoom, out vec3 pos){
    vec3 n = normalize(vec3(uv.x,-uv.y,zoom));
    pos = vec3(0.0, 0.0, -CameraDistance);
    pos.yz *= rotate(angle.y);
    pos.xz*= rotate(angle.x);
    n.yz*= rotate(angle.y);
    n.xz*= rotate(angle.x);
    return n;
}

vec4 sky(vec3 dir, float k, float sh){
    float sharpness = 1.0-sh;
    vec3 upCol = mix(vec3(0.7,0.8,0.9)*1.5,vec3(0.6,0.75,0.9),(1.0+dir.z)*0.5);
    vec3 dnCol = mix(vec3(0.2,0.4,0.7),vec3(0.2,0.3,0.5)*.6,(1.0+dir.z)*0.5);
    return vec4(vec3(mix(upCol,dnCol,(mix(dir.y,pow(abs(dir.y),sharpness)*sign(dir.y), k)+1.0)*0.5)),1.0);
}
vec3 frameSubProcessing(vec2 uv){
    vec3 pos;
    vec3 dir = getDirection(uv, mice()*miceFactor, CameraZoom, pos);
    vec3 norm;
    float pathLen;

    vec4 d = rayMarch(pos, dir, 1.0, pathLen);  
    norm = getNormal(d.xyz);

    vec3 mat1_Col = vec3(0.99,0.99,0.99)*.9;
    vec3 mat2_Col = vec3(0.99,0.85,0.80)*.9;

    if(d.w < MarchMaxDistance){
        return sky(reflect(dir,norm),mix(0.22,.99,mtf),mix(0.1,.99,mtf)).rgb * mix(mat1_Col,mat2_Col,mtf);
    }
    return sky(dir,0.99,.9).rgb;
}
vec3 frameProcessing(vec2 uv){
    float ss = float(subSamples);
    vec2 offset;
    vec3 curPix = frameSubProcessing(uv);
    float count = 1.0;

    float delta = 1.0/(ss+1.0);
    for(int y=0; y<subSamples; y++){
        for(int x=0; x<subSamples; x++){
            offset = vec2((delta*float(x+1)-.5)*iR.x,(delta*float(y+1)-.5)*iR.y);
            if(mod(ss,2.0)>0.0){
                if(x==(subSamples+1)/2 && y==(subSamples+1)/2) continue;
            }
            curPix += frameSubProcessing(uv+offset);
            count += 1.0;
        }
    }
    return curPix/count;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = vec2(fragCoord.x * iR.x - du, (fragCoord.y * iR.y - dv)*iR.x*iResolution.y);
    vec3 rgb = frameProcessing(uv);
    fragColor = vec4(rgb,1.0);
}

/*

	Cheap Cloud Flythrough 
	----------------------

	"Cheap" should probably refer to the quality of the physics in this shader, which is
	virtually non-existent, but it actually refers to the fake cloud algorithm... if you could 
	call it that. :)

	This is merely an attempt to give the impression of a cloud flythrough, whilst maintaining	
	a reasonably acceptable framerate. The key to that is keeping the distance field as simple 
	as possible.

	Due to the amount of cost cutting, it's far from  perfect. However, there's still plenty 
	of room for improvement.

    I've used density based accumulation, which in one way or another, uses concepts from many 
	volumetric examples, but particularly from the following:

	Cloudy Spikeball - Duke
    https://www.shadertoy.com/view/MljXDw
    // Port from a demo by Las - Worth watching.
    // http://www.pouet.net/topic.php?which=7920&page=29&x=14&y=9

    Other shaders worth looking at:

	Clouds - IQ: One of my favorite shaders, and everyone elses, it seems.
	https://www.shadertoy.com/view/XslGRr

	Sample Pinning - huwb: Fast, and pretty.
	https://www.shadertoy.com/view/XdfXzn

	FakeVolumetricClouds - Kuvkar: Fast, using parallax layers. Really cool.
	https://www.shadertoy.com/view/XlsXzN

    Emission clouds - Duke: Nice, and straight forward.
    https://www.shadertoy.com/view/ltBXDm


*/


// IQ's texture lookup noise... in obfuscated form. There's less writing, so
// that makes it faster. That's how optimization works, right? :) Seriously,
// though, refer to IQ's original for the proper function.
// 
// By the way, you could replace this with the non-textured version, and the
// shader should run at almost the same efficiency.
float pn( in vec3 p ){

    vec3 i = floor(p); p -= i; p *= p*(3. - 2.*p);
	p.xy = texture(iChannel0, (p.xy + i.xy + vec2(37, 17)*i.z + .5)/256., -100.).yx;
	return mix(p.x, p.y, p.z);
}



// Basic low quality noise consisting of three layers of rotated, mutated 
// trigonometric functions. Needs work, but sufficient for this example.
float trigNoise3D(in vec3 p){

    p /= 2.;
    float res = 0., sum = 0.;

    // IQ's cheap, texture-lookup noise function. Very efficient, but still 
    // a little too processor intensive for multiple layer usage in a largish 
    // "for loop" setup. Therefore, just one layer is being used here.
    float n = pn(p*8. + iTime*2.);


    // Two sinusoidal layers. I'm pretty sure you could get rid of one of 
    // the swizzles (I have a feeling the GPU doesn't like them as much), 
    // which I'll try to do later.

    vec3 t = sin(p.yzx*3.14159265 + cos(p.zxy*3.14159265+1.57/2.))*0.5 + 0.5;
    p = p*1.5 + (t - 1.5); //  + iTime*0.1
    res += (dot(t, vec3(0.333)));

    t = sin(p.yzx*3.14159265 + cos(p.zxy*3.14159265+1.57/2.))*0.5 + 0.5;
    res += (dot(t, vec3(0.333)))*0.7071;    

	return ((res/1.7071))*0.85 + n*0.15;
}

vec3 fluid(vec3 uv1,float iters){
 //fake fluid physics
 vec3 uv = uv1;
 for (float i = 1.; i < iters; i++)
  {
    uv.x += sin((iTime-uv.y)*.5)*1.5/i* sin(i * uv.y + iTime * 0.5);
    uv.y += sin((iTime-uv.z)*.5)*1.5/i* sin(i * uv.z + iTime * 0.5 );
    uv.z += sin((iTime-uv.x)*.5)*1.5/i* sin(i * uv.x + iTime * 0.5 );
  }
  return uv;
}

// Distance function.
float map(vec3 p,float iters) {
    p = fluid(p,iters);
    return trigNoise3D(p);

    // Three layers of noise, for comparison.
    //p += iTime;
    //return pn(p*.75)*0.57 + pn(p*1.875)*0.28 + pn(p*4.6875)*0.15;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  

    // Unit direction ray vector: Note the absence of a divide term. I came across
    // this via a comment Shadertoy user "coyote" made. I'm pretty easy to please,
    // but I thought it was pretty cool.
    vec3 rd = normalize(vec3(fragCoord - iResolution.xy*.5, iResolution.y*.75)); 
    float t1 = iTime/2.;
    // Ray origin. Moving along the Z-axis.
    vec3 ro = vec3(0, 0, t1);

    // Cheap camera rotation.
    //
    // 2D rotation matrix. Note the absence of a cos variable. It's there, but in disguise.
    // This one came courtesy of Shadertoy user, "Fabrice Neyret."
    vec2 a = sin(vec2(1.5707963, 0) + t1*0.1875); 
    mat2 rM = mat2(a, -a.y, a.x);
    rd.xy = rd.xy*rM; // Apparently, "rd.xy *= rM" doesn't work on some setups. Crazy.
    rd.xz = rd.xz*rM;

    // Placing a light in front of the viewer and up a little, then rotating it in sync
    // with the camera. I guess a light beam from a flying vehicle would do this.
    vec3 lp = vec3( 0, 1, 4);
    lp.xy = lp.xy*rM;
    lp.xz = lp.xz*rM;
    lp += ro;

    // The ray is effectively marching through discontinuous slices of noise, so at certain
    // angles, you can see the separation. A bit of randomization can mask that, to a degree.
    // At the end of the day, it's not a perfect process. Note, the ray is deliberately left 
    // unnormalized... if that's a word.
    //
    // Randomizing the direction.
    //rd = (rd + ((rd.zyx)*.006 - .003)); 
    // Randomizing the length also. 
    rd *= (1. + fract((dot(vec3(7, 157, 113), rd.zyx))*43758.5453)*0.06-0.03);      

    // Local density, total density, and weighting factor.
    float lDe = 0., td = 0., w = 0.;

    // Closest surface distance, and total ray distance travelled.
    float d = 1., t = 0.;

    // Distance threshold. Higher numbers give thicker clouds, but fill up the screen too much.    
    const float h = .5;


    // Initializing the scene color to black, and declaring the surface position vector.
    vec3 col = vec3(0), sp;



    // Particle surface normal.
    //
    // Here's my hacky reasoning. I'd imagine you're going to hit the particle front on, so the normal
    // would just be the opposite of the unit direction ray. However particles are particles, so there'd
    // be some randomness attached... Yeah, I'm not buying it either. :)
    vec3 sn = normalize(-rd);

    // Raymarching loop.
    for (int i=0; i<64; i++) {

        // Loop break conditions. Seems to work, but let me
        // know if I've overlooked something.
        if((td>1.) || d<.01*t || t>80.)break;


        sp = ro + rd*t; // Current ray position.
        d = map(sp,15.-d*15.); // Closest distance to the surface... particle.

        // If we get within a certain distance, "h," of the surface, accumulate some surface values.
        // The "step" function is a branchless way to do an if statement, in case you're wondering.
        //
        // Values further away have less influence on the total. When you accumulate layers, you'll
        // usually need some kind of weighting algorithm based on some identifying factor - in this
        // case, it's distance. This is one of many ways to do it. In fact, you'll see variations on 
        // the following lines all over the place.
        //
        lDe = (h - d)*step(d, h); 
        w = (1. - td)*lDe;   

        // Use the weighting factor to accumulate density. How you do this is up to you. 
        td += w*w*8. + 1./64.; //w*w*5. + 1./50.;
        //td += w*.4 + 1./45.; // Looks cleaner, but a little washed out.


        // Point light calculations.
        vec3 ld = lp-sp; // Direction vector from the surface to the light position.
        float lDist = max(length(ld), .001); // Distance from the surface to the light.
        ld/=lDist; // Normalizing the directional light vector.

        // Using the light distance to perform some falloff.
        float atten = 1./(1. + lDist*.125 + lDist*lDist*.05);

        // Ok, these don't entirely correlate with tracing through transparent particles,
        // but they add a little anglular based highlighting in order to fake proper lighting...
        // if that makes any sense. I wouldn't be surprised if the specular term isn't needed,
        // or could be taken outside the loop.
        float diff = max(dot( sn, ld ), 0.);
        float spec = pow(max( dot( reflect(-ld, sn), -rd ), 0. ), 4.);


        // Accumulating the color. Note that I'm only adding a scalar value, in this case,
        // but you can add color combinations. Note the "d*3. - .1" term. It's there as a bit
        // of a fudge to make the clouds a bit more shadowy.
        col += w*(d*3. - .1)*(.5 + diff + spec*.5)*atten;

        // Try this instead, to see what it looks like without the fake contrasting. Obviously,
        // much faster.
        //col += w*atten*1.25;


        // Enforce minimum stepsize. This is probably the most important part of the procedure.
        // It reminds me a little of of the soft shadows routine.
        t +=  max(d*.5, .02); //
        // t += .2; // t += d*.5;// These also work, but don't seem as efficient.

    }

    col = max(col, 0.);

    // trigNoise3D(rd*1.)
    col = mix(pow(vec3(1.5, 1, 1)*col,  vec3(1, 2, 8)), col, dot(cos(rd*6. +sin(rd.yzx*6.)), vec3(.333))*.35 + .65);
    col = mix(col.zyx, col, dot(cos(rd*9. +sin(rd.yzx*9.)), vec3(.333))*.15 + .85);//xzy


    //col = mix(col.zyx, col, dot(rd, vec3(.5))+.5);

    fragColor = vec4(sqrt(max(col, 0.)), 1.0);
}

