#define PI 3.14159265359
#define TAU 6.28318530718


float random(vec2 st) {
    return fract(sin(dot(st,vec2(1.9898,7.233))+2.)*
        4378.5453123);
}

float growing_poly_pattern(vec2 st,float t,int N,float WIDTH,float GROWING_SPEED){
    float l = length(st),th = atan(st.x,st.y)+PI;
    float r = TAU/float(N);
    float d = cos(floor(0.5+th/r)*r-th)*length(st);
    return 1.0-smoothstep(.4,.41,mod(pow(d,0.508)-t*GROWING_SPEED,WIDTH));
}

float flashing_grid(vec2 st,float t,in vec2 ipos) {
    return step(1.-iMouse.x/iResolution.x,mod(ipos.x+ipos.y,2.)+2.496*random(ipos*.2+t/900000.));
}

float pulse( float x, float a, float b ){
    float k = pow(a+b,a+b) / (pow(a,a)*pow(b,b));
    return k * pow( x, a ) * pow( 1.0-x, b );
}

float box (vec2 st,float size) {
    float d = max(abs(st.x),abs(st.y));
    return step(d,size);
}

mat2 rotate2d (float th) {
    return mat2(cos(th),-sin(th),sin(th),cos(th));
}

float triangle (vec2 st, float size){
  int N = 3;
  float a = atan(st.x,st.y)+PI;
  float r = TAU/float(N);

  float d = cos(floor(.5+a/r)*r-a)*length(st);

  return 1.0-step(.5*size,d);
}

float shapesnbeats(vec2 st, float t) {
    float r = length(st);

    vec3 color = vec3(0.);

    float beat = floor(mod(t,4.));

    float a=.5,b=2.;float x_max=a/(a+b);
    float size = pulse(fract(t),a,b);


    if (beat==0. || beat==2.){
        color += step(r,1.*size);
    } else if (beat==1.){
        st = rotate2d(smoothstep(0.,.5,fract(t))*PI)*st;
        color += box(st,1.*size);
    } else {
        st = rotate2d(smoothstep(0.,.5,fract(t))*2.*PI/3.)*st;
        color += triangle(st,1.3*size);
    }

    return color.x;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord.xy/iResolution.xy;
    st = st*2.-1.;
    st.x *= iResolution.x/iResolution.y;

    // beat
    float BPM = 120.;
    float t = iTime*BPM/60.;



    // polygon pattern
    int N = 6; //number of sides
    float WIDTH = .7;
    float GROWING_SPEED = 0.125;

    st = 2.*rotate2d(PI/6.)*st;
    float hex = growing_poly_pattern(st,t,N,WIDTH,GROWING_SPEED);



    // grid parameters
    float GRID_ROTATE_ANGLE = PI/25.;
    float GRID_ZOOM = 6.5;
    vec2 GRID_VEL = vec2(4.,1.5);

    st = rotate2d(GRID_ROTATE_ANGLE-PI*1./6.)*GRID_ZOOM*st;
    st += GRID_VEL*t;
    vec2 ipos=floor(st),fpos=fract(st);



    // white anim
    vec2 COLOR_SHIFT = vec2(2.,0.0); //COULD BE IMPROVED
    vec3 color_w ;
    color_w.b= flashing_grid(st-COLOR_SHIFT,t,ipos);
    color_w.g= flashing_grid(st,t,ipos);
    color_w.r= flashing_grid(st+COLOR_SHIFT,t,ipos);

    vec2 uv = fragCoord.xy/iResolution.xy;
    color_w += mix(vec3(0.225,0.820,0.702),vec3(1.000,0.977,0.720),smoothstep(.4,1.7,uv.x+uv.y));



    // black anim
    vec3 color_b = vec3(0.100,0.072,0.063);
    fpos = fpos*2.-1.;
    float r = length(fpos);

    vec2 COLOR_SHIFT2 = vec2(.45,0.0)*pow(clamp(0.,1.,length(uv*2.-1.)),2.3);
    color_b.r += shapesnbeats(fpos-COLOR_SHIFT2,t);
    color_b.g  += shapesnbeats(fpos,t);
    color_b.b += shapesnbeats(fpos+COLOR_SHIFT2,t);

    color_b *= 1.-flashing_grid(st,t,ipos);



    // mixing
	vec3 color = mix(color_w,color_b,hex);

    fragColor = vec4(color,1.0);
}

float sDot( vec2 uv, vec2 position, float val, vec2 target )
{
    float distToTarget = 1.0 - distance( uv, target );
    return smoothstep( 0.0, 0.65, max( 0.02 + ( 0.02 * distToTarget ), 0.02) / distance( uv, position + ( ( val * 2.0 - 1.0 ) * 0.015 ) ) );
    //return smoothstep( 0.0, 0.65, ( 0.02 + ( 0.04 * ( sin( iTime * 0.5 ) * 0.5 + 0.5) ) ) / distance( uv, position + ( ( val * 2.0 - 1.0 ) * 0.015 ) ) );
}

float rand (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

vec3 dithering( vec3 color, vec2 uv ) {

    float grid_position = rand( uv );
    vec3 dither_shift_RGB = vec3( 0.25 / 255.0, -0.25 / 255.0, 0.25 / 255.0 );
    dither_shift_RGB = mix( 2.0 * dither_shift_RGB, -2.0 * dither_shift_RGB, grid_position );
    return color + dither_shift_RGB;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord / iResolution.xy;

    vec2 mouse = iMouse.xy / iResolution.xy;
    mouse.x -= 0.5;
    mouse.x *= iResolution.x / iResolution.y;
    mouse.x += 0.5;    

    uv.x -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    uv.x += 0.5;

    vec4 noise = texture( iChannel0, ( fragCoord / iResolution.xy ) * 0.1 );

    vec3 col = vec3( sDot( uv, vec2(mouse.x, mouse.y), noise.r, mouse ) );       

    col += vec3( sDot( uv, vec2(cos( iTime * 0.6 ) * 0.5 + 0.5, 0.5), noise.r, mouse ) );
    col += vec3( sDot( uv, vec2(0.5, 0.1), noise.r, mouse ) );
    col += vec3( sDot( uv, vec2(0.5, cos( iTime * 0.6 ) * 0.5 + 0.5), noise.r, mouse ) );

    vec3 bgGrad = mix( vec3(33.,67.,101.) / 255.0, vec3(4.0, 5.0 ,7.0) / 255.0, uv.y );

    vec3 blobColors = mix( vec3(1.0, 0.0, 0.8), vec3(1.0, 1.0, 1.0), sin( iTime * 0.8 ) * 0.5 + 0.5 );

    vec3 composition = mix( bgGrad, blobColors, col.r );

    fragColor = vec4( dithering( composition, uv ), 1.0 );

}

