#define LIGHT_RGB	vec3(4.9, 3.9, 3.5)
#define R	iResolution
#define Z0	min(iTime, 0.)
#define sat(x)	clamp(x, 0., 1.)
#define S(a, b, c)	smoothstep(a, b, c)
#define S01(a)	S(0., 1., a)
#define NM normalize
#define minH(a, b)	{ float u = a; if (u < h.x) h = vec2(u, b); }

float g = 0.;

vec2 h22(vec2 v) {
	vec3 p = fract(v.xyx * vec3(.1031, .103, .0973));
	p += dot(p, p.yzx + 33.33);
	return fract((p.xx + p.yz) * p.zy);
}

vec4 h44(vec4 p) { p = fract(p * .1031); p *= p + 3.3456; return fract(p * (p + p)); }

float n31(vec3 p) {
	const vec3 s = vec3(7, 157, 113);
	vec3 ip = floor(p);
	p = fract(p);
	p = p * p * (3. - 2. * p);
	vec4 h = vec4(0, s.yz, s.y + s.z) + dot(ip, s);
	h = mix(h44(h), h44(h + s.x), p.x);
	h.xy = mix(h.xz, h.yw, p.y);
	return mix(h.x, h.y, p.z);
}

float fbm(vec3 p) {
	float i,
	      a = 0.,
	      b = .5;
	for (i = Z0; i < 4.; i++) {
		a += b * n31(p);
		b *= .5;
		p *= 2.;
	}

	return a * .5;
}

float smin(float a, float b, float k) {
	float h = sat(.5 + .5 * (b - a) / k);
	return mix(b, a, h) - k * h * (1. - h);
}

float max3(vec3 v) { return max(v.x, max(v.y, v.z)); }

float min3(vec3 v) { return min(v.x, min(v.y, v.z)); }

mat2 rot(float a) {
	float c = cos(a),
	      s = sin(a);
	return mat2(c, s, -s, c);
}

float box(vec3 p, vec3 b) {
	vec3 q = abs(p) - b;
	return length(max(q, 0.)) + min(max3(q), 0.);
}

float cap(vec3 p, float h, float r) {
	p.x -= clamp(p.x, 0., h);
	return length(p) - r;
}

vec3 rayDir(vec3 ro, vec3 lookAt, vec2 uv) {
	vec3 f = NM(lookAt - ro),
	     r = NM(cross(vec3(0, 1, 0), f));
	return NM(f + r * uv.x + cross(f, r) * uv.y);
}

vec4 brkT(vec3 p) {
	float n = n31(p * 7.),
	      s = step(.4, dot(vec3(n31(p), n, n31(p * 15.)), vec3(.75, .15, .1)));
	p.y -= .03;
	p.x -= step(fract(p.y * 1.87 - .05), .5) * 2.6;
	p.z -= .33;
	vec3 c = vec3(.2, .03, .01);
	c *= .2 + n * n * .5;
	return vec4(mix(vec3(.2), c, sat(S(0., .02, min3(abs(fract(p * vec3(1, 3.74, 2.23)) - .1))) - s)), (s + n) * .01);
}

vec2 proj(vec3 p, vec3 n) {
	n = abs(n);
	float m = max3(n);
	return n.x == m ? p.zy : n.y == m ? p.xz : p.xy;
}

float vor(vec2 p) {
	vec2 o,
	     g = floor(p);
	p -= g;
	vec3 d = vec3(2);
	for (int y = -1; y <= 1; y++) {
		for (int x = -1; x <= 1; x++) {
			o = vec2(x, y);
			o += h22(g + o) * vec2(.5, .7) - p;
			d.z = dot(o, o);
			d.y = max(d.x, min(d.y, d.z));
			d.x = min(d.x, d.z);
		}
	}

	return d.y - d.x;
}

vec2 map(vec3 p, bool addGlass) {
	float f, e, l,
	      d = 1e7;
	vec2 h;
	h.x = 1e7;
	vec3 pp,
	     cp = p,
	     op = p;
	if (addGlass) {
        minH(max(abs(p.z - 3.2), abs(p.x) - 4.), 11);
	} else {
		p.x = fract(p.x * 1.74) / 1.74 - .25;
		p.y = abs(p.y + .7) - 1.5;
		p.z -= 3.4;
		minH(max(abs(length(p.xy) - .06) - .0015, abs(p.z) - .03), 9);
	}

	p = cp;
	p.y += 3.5;
	p = p.zyx;
	p.xz *= rot(.2);
	minH(max(max(cap(p, 1., .45), abs(p.y) - .05), -p.x), 8);
	p.x -= .15;
	p.z = abs(p.z) - .5;
	p.xy *= rot(-.1);
	d = max(length(p.xz) - .04, p.y - .5);
	p.xy *= rot(.6);
	p.x -= .3;
	minH(smin(d, max(length(p.xz) - .04, p.y - .8), .04), 3);
	p.z = cp.x - .03;
	p.xy = p.yx;
	minH(max(max(cap(p, .6, .45), abs(p.y) - .04), .3 - p.x), 8);
	p = cp;
	p -= vec3(9, -4.7, 5);
	mat2 r = rot(.36);
	for (f = 0.; f < 5.; f++) {
		minH(max(box(p, vec3(2, .3, 10)), p.z - .5), 1);
		d = smin(d, abs(-8.1 - p.z), 2.);
		p.xz *= r;
		p.y -= .6;
	}

	minH(d, 1);
	p = cp;
	p.y++;
	d = min(box(p, vec3(90, 4, 3)), 8. - p.x);
	p.x = abs(p.x + 8.) - 8.;
	p.z -= 7.;
	cp = p;
	d = -min(d, box(p, vec3(4, 4, 4.1)));
	minH(d, 1);
	d = length(p - vec3(0, 4, 0)) - .2;
	g += .005 / (.01 + d * d);
	minH(d, 12);
	p += vec3(-2.48, 1.8, 2.4);
	e = box(p, vec3(1.4, .02, .5));
	p.x = abs(p.x) - .8;
	d = max(box(p, vec3(.05, 5, .05)), p.y);
	p = cp - vec3(0, .8, 3.95);
	minH(box(p, vec3(.5, .7, .01)), 10);
	minH(box(p, vec3(.6, .8, .01)), 8);
	p.y += 2.;
	l = length(p) - .8;
	f = min(max(max(min(min(l + .05, max(l, abs(p.x) - .2)), max(l, abs(p.y) - .1)), p.y - .1), -l - .1), max(box(p, vec3(.65, .8, .1)), -p.y - .2));
	minH(f, 4);
	p.x = abs(p.x) - .3;
	p.y -= .35;
	d = min(d, length(p) - .1);
	p = cp + vec3(-2.48, 2.5, 1.2);
	e = min(e, box(p, vec3(1.4, .02, .3)));
	p.x = abs(p.x) - .8;
	d = min(d, max(box(p, vec3(.05, 5, .05)), p.y));
	e -= .03;
	p = cp + vec3(3.7, -.5, -2);
	e = min(e, box(p, vec3(.3, .05, 1)));
	p.y -= .48;
	p.z = abs(abs(p.z - .4) - .3) - .15;
	minH(box(p, vec3(.2, .4, .05)) - .03, 5);
	minH(d - .04, 3);
	minH(e, 8);
	p = cp;
	p.x += 2.8;
	p.y += 2.4;
	minH(box(p, vec3(.94, .08, 2.9)) - .1, 7);
	p.y += .23;
	d = box(p, vec3(1.02, .08, 3.05));
	p.y += .7;
	p.z = abs(p.z) - 3.;
	d = min(d, box(p, vec3(1.02, .78, .08)));
	p = cp;
	p.x = abs(abs(p.x) - 2.1) - .75;
	p.z += 3.8;
	d = min(d, box(p, vec3(.1, 4, .1)));
	pp = p;
	p.y = fract(p.y) - .5;
	p.z += .12;
	d = min(d, length(p) - .01);
	p = pp;
	minH(d - .04, 3);
	d = box(cp - vec3(0, 3.5, -3.8), vec3(4, .5, .5));
	p.x = abs(cp.x) - 3.5;
	p.z = abs(p.z + 3.175) - 3.175;
	d = min(d, box(p, vec3(.5, 4, .5)));
	d -= brkT(op).a;
	minH(d, 2);
	return h;
}

vec3 N(vec3 p, float t) {
	float h = t * .2;
	vec3 n = vec3(0);
	for (int i = min(iFrame, 0); i < 4; i++) {
		vec3 e = .005773 * (2. * vec3(((i + 3) >> 1) & 1, (i >> 1) & 1, i & 1) - 1.);
		n += e * map(p + e * h, false).x;
	}

	return NM(n);
}

float shadow(vec3 p, vec3 lp) {
	float d,
	      s = 1.,
	      t = .1,
	      mxt = length(p - lp);
	vec3 ld = (lp - p) / mxt;
	for (float i = Z0; i < 2e2; i++) {
		d = map(t * ld + p, false).x;
		s = min(s, 15. * d / t);
		t += max(.1, d);
		if (mxt - t < .5 || s < .001) break;
	}

	return S01(s);
}

float ao(vec3 p, vec3 n, float h) { return map(h * n + p, true).x / h; }

vec3 lights(vec3 p, vec3 rd, float d, vec2 h) {
	vec4 t;
	float spe = 1.,
	      gg = g,
	      f = h.y <= 6. ? fbm(p * 7.5) : 0.;
	vec3 c,
	     ld = NM(vec3(0, 2.8, 7) - p),
	     n = N(p, d);
	if (h.y == 1.) {
		vec2 uv = proj(p, n) * vec2(1, 2);
		float d = S(0., .15, vor(uv) * f);
		n.xy += vec2(dFdx(d), -dFdy(d)) * 1.5;
		n = NM(n);
		d += f;
		c = mix(vec3(.15, .09, .06), vec3(.04, .03, .04) + S(.4, 1., S(.6, .5, d + f) * n31(p)) * vec3(.054, .06, .04), S01(d + .4));
		spe = .3;
	}
	else if (h.y == 2.) {
		t = brkT(p);
		c = t.rgb;
		n.xy += f;
		n = NM(n);
		spe = .1;
	}
	else if (h.y == 7.) c = vec3(.01, .013, .01);
	else if (h.y == 10.) {
		c = vec3(.02);
		spe = 15.;
	}
	else if (h.y == 3.) c = mix(vec3(.15, .05, .01), vec3(.16, .2, .24) * (.2 + .8 * f), S(0., .15, f));
	else if (h.y == 8.) c = mix(vec3(.04, .02, .02), vec3(.06, .04, .02), S01(n31(p * vec3(2.3, 1, 20))));
	else if (h.y == 5.) c = vec3(.12, .11, 0) - f * .2;
	else if (h.y == 4.) c = vec3(.4, .4, .38) - f * .4;
	else if (h.y == 9.) c = vec3(.9);
	else return LIGHT_RGB;

	float ao = sat(mix(ao(p, n, .2), ao(p, n, 2.), .7)),
	      l1 = sat(.1 + .9 * dot(ld, n)) * (.05 + .95 * shadow(p, vec3(0, 2.8, 7))) * (.3 + .7 * ao),
	      l2 = sat(.1 + .9 * dot(ld * vec3(-1, 0, -1), n)) * .2 + pow(sat(dot(rd, reflect(ld, n))), 10.) * spe;
	g = gg;
	return (l1 + l2 * ao) * c * LIGHT_RGB;
}

bool hitGlass;
vec3 ghit;
vec3 march(inout vec3 p, vec3 rd, float s) {
	float i,
	      d = .01;
	g = 0.;
	vec2 h;
	for (i = Z0; i < s; i++) {
		h = map(p, !hitGlass);
		if (abs(h.x) < .0015) {
			if (h.y == 11.) {
				hitGlass = true;
				ghit = p;
			}
			else break;
		}

		d += h.x;
		p += h.x * rd;
	}

	return g * LIGHT_RGB + lights(p, rd, d, h);
}

vec3 scene(vec3 ro, vec3 rd) {
	hitGlass = false;
	vec3 p = ro,
	     col = march(p, rd, 120.) * exp(dot(p - ro, p - ro) * -.001);
	if (hitGlass) {
		rd = reflect(rd, vec3(0, 0, -1));
		p = ghit;
		col += .01 + .99 * sat(march(p, rd, 64.));
	}

	return col;
}

#define rgba(col)	vec4(pow(max(vec3(0), col), vec3(.45)) * sat(iTime), 0)

void mainVR(out vec4 fragColor, vec2 fc, vec3 ro, vec3 rd) {
	rd.xz *= mat2(1, 0, 0, -1);
	fragColor = rgba(scene(ro, rd));
}

void mainImage(out vec4 fragColor, vec2 fc) {
	float t = mod(iTime, 25.),
	      f = S(0., 18., t);
	vec2 uv = (fc - .5 * R.xy) / R.y,
	     q = fc.xy / R.xy;
	vec3 ro = vec3(-23. + 22.5 * f, 0, 0),
	     col = scene(ro, rayDir(ro, vec3(0, mix(-1. - sat(t), 0., S(16., 20., t)), -sin(S01(f) * 5.7) * 5.), uv));
	col *= .2 + .4 * pow(16. * q.x * q.y * (1. - q.x) * (1. - q.y), .4);
	fragColor = rgba(col);
}

