#define NUM_OF_PARTICLES 72.
#define ANGLE_CIRCLE 6.2832

vec2 Hash12(float threshold){
    float x = fract(sin(threshold * 620.2) * 423.5);
    float y = fract(sin(threshold * 703.2) * 207.5);

    return vec2(x, y);
}

vec2 Hash12_Polar(float threshold){
    float angle = fract(sin(threshold * 620.2) * 423.5) * ANGLE_CIRCLE;
    float dist = fract(sin((threshold + angle) * 703.2) * 207.5);

    return vec2(sin(angle), cos(angle)) * dist;
}

float Explosion(vec2 uv, float time){
    float sparks = 0.;
    for(float i = 0.; i < NUM_OF_PARTICLES; i++){
        vec2 direction = Hash12_Polar(6. * i) * .5;

        float brightness = mix(.0005, .002, smoothstep(.05, 0., time));
        float dist = length(uv - direction * time);

        brightness *= sin(time * 20. * i) * .5 + .5;
        sparks += brightness / dist;
    }

    return sparks;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    vec3 color = sin(vec3(.34, .54, .43) * floor(iTime)) * .5 + .5;

    col += Explosion(uv, fract(iTime)) * color;

    col *= 2.;

    fragColor = vec4(col,1.0);
}

#define SIN_60 0.8660254

/////////////////////////////////////////////////////////////
// Hash without Sine
// Creative Commons Attribution-ShareAlike 4.0 International Public License
// Created by David Hoskins.
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 hash32(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

/////////////////////////////////////////////////////////////
vec3 blob( vec2 uv, vec2 p )
{
    //un-skew the uv to get round dots
    uv.x += uv.y * 0.5; //skew x to shift diagonal into triangles
    uv.x /= SIN_60;
    //also scale the position to get the origin to the correct place.
    p.x /= SIN_60;

    float d = distance( uv, p );

    float r = 0.123;
    float outer = smoothstep(r + 0.001, r - 0.001, d); //invert

    float rb = 0.066;
    float inner = smoothstep(rb - 0.001, rb + 0.001, d); 


    float glow = smoothstep(rb+0.3, rb, d);  //invert

    return vec3(outer,inner, glow);
}
/////////////////////////////////////////////////////////////
vec3 pulse( float falloff, vec3 rnd )
{
    //vec3 colA = vec3(0.4,0.01,0.0);
    //vec3 colB = vec3(1.0,0.7,0.0);

    vec3 colA = vec3(0.1,0.01,0.5);
    vec3 colB = vec3(0.4,0.45,1.0);

    float v = abs(sin(((iTime*(1.0+rnd.z))+rnd.x) * (rnd.y+0.1) * 5.0));
    vec3 col = mix( colA, colB, vec3(v*v*v));

    return col * pow( falloff, mix(7.0,3.0,v));

}
