// from https://www.shadertoy.com/view/wdyXzy
vec2 opRepAng( in vec2 p, float theta, float offset)
{
    float a = atan(p.y, p.x) - offset;
    a = mod(a + .5 * theta, theta) - .5 * theta;
    return length(p) * vec2(cos(a), sin(a));
}

// From iq
float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.x *= iResolution.x/iResolution.y;
    vec3 backCol = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec2 p = uv - vec2(0.85, 0.5);
    float circle = 1.0 - smoothstep(0.0, 0.2, sdSphere(p*(2.-0.55*noise(p*5.50 + vec2(iTime*1.5))), 0.35));
    float circle1 = 1.0 - smoothstep(0.1, 1.0, sdSphere(p*(2.33-1.9*noise(p*10.50 + vec2(iTime, iTime*2.))), 0.2));

    p = opRepAng(p, 2.*PI / 20., PI*0.5);
    float len = 0.4;
    float offset = 0.02 * sin(iTime * 10.0) * 5.02*(circle1 - circle);
    float val = smoothstep(0.01, 0.015, sdSegment(p*1.5, vec2(len*0.9 - offset, 0.0), vec2(len + offset, 0.0)));
    float addVal = mix(0.21, 13., sdSegment(p*1.5, vec2(len*0.9 - offset, 0.0), vec2(len + offset, 0.0)));
    vec3 addValCol = clamp(0.,1.,1.0-addVal)*vec3(0.3,0.5,0.99);
    vec3 baseValCol = vec3(0.4,0.8,0.88)*(1.0-val);
    vec3 col = vec3(addValCol+baseValCol);

    col = mix(col, backCol + col, col.x);
    // vec3 col = vec3(circle1 - circle);

    fragColor = vec4(col,1.0);
}

