vec4 nami_nami(vec2 uv){
    float theta = mod( (atan(uv.y,uv.x)+PI)/(2.*PI), .3);
    vec4 spect = texture(iChannel0, vec2(theta,0.0));
    vec4 spect_low = texture(iChannel0, vec2(0.2,0.0));
    float f = floor((iTime/4.)*spect_low.x*2.);
    vec4 nami_theta = vec4((sin(f*atan(uv.y,uv.x) + iTime + 5.*spect.x)));
    vec4 nami_r = vec4(sin(2.*length(uv)*2.*PI + iTime*2. + 3.*spect.x));
