// returns 3D value noise
float noise( vec3 x )
{
    // grid
    vec3 p = floor(x);
    vec3 w = fract(x);

    // quintic interpolant
    vec3 u = w*w*w*(w*(w*6.0-15.0)+10.0);


    // gradients
    vec3 ga = hash( p+vec3(0.0,0.0,0.0) );
    vec3 gb = hash( p+vec3(1.0,0.0,0.0) );
    vec3 gc = hash( p+vec3(0.0,1.0,0.0) );
    vec3 gd = hash( p+vec3(1.0,1.0,0.0) );
    vec3 ge = hash( p+vec3(0.0,0.0,1.0) );
    vec3 gf = hash( p+vec3(1.0,0.0,1.0) );
    vec3 gg = hash( p+vec3(0.0,1.0,1.0) );
    vec3 gh = hash( p+vec3(1.0,1.0,1.0) );

    // projections
    float va = dot( ga, w-vec3(0.0,0.0,0.0) );
    float vb = dot( gb, w-vec3(1.0,0.0,0.0) );
    float vc = dot( gc, w-vec3(0.0,1.0,0.0) );
    float vd = dot( gd, w-vec3(1.0,1.0,0.0) );
    float ve = dot( ge, w-vec3(0.0,0.0,1.0) );
    float vf = dot( gf, w-vec3(1.0,0.0,1.0) );
    float vg = dot( gg, w-vec3(0.0,1.0,1.0) );
    float vh = dot( gh, w-vec3(1.0,1.0,1.0) );

    // interpolation
    return va + 
           u.x*(vb-va) + 
           u.y*(vc-va) + 
           u.z*(ve-va) + 
           u.x*u.y*(va-vb-vc+vd) + 
           u.y*u.z*(va-vc-ve+vg) + 
           u.z*u.x*(va-vb-ve+vf) + 
           u.x*u.y*u.z*(-va+vb+vc-vd+ve-vf-vg+vh);
}

float map(vec3 p){
    float noise = noise(p*5.);
    float d = sphere(p+vec3(0., sin(iTime), -2.), 1.3);
    d = min(d, p.y+1.);
    //d = cos(d)*sin(d)*.4;
    return d+noise*.2;
}

vec3 calcNormal( in vec3 pos )
{

    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ));
}

vec3 trace( vec3 ro, vec3 rd) {

    vec3 p = ro;
    for(int i=0; i <255; i++){
        p += rd * map(p); 
    }
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv*2.-1.;
    uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(0., 0., -1.);
    vec3 rd = normalize(vec3(uv, 1.));
    vec3 p = trace(ro, rd);

    vec3 color = vec3(.5,.3,.0);


    vec3 normal = calcNormal(p);
    vec3 soleil_direction = normalize(vec3(0.25, 1., 0.5));


    // Time varying pixel color
    vec3 col = vec3(1.) * max( dot(normal,soleil_direction), 0.); 
    col = clamp(col, vec3(0.), vec3(1.));
    col = mix(col, vec3(1.), min(distance(p,ro)*0.1, 1.));

    //col += vec3(.6, .5, .3);


    vec3 pshadow = trace(p+normal*.1, soleil_direction);
    if (map(pshadow) < 1.)
        col *= 0.25;

    // Output to screen
    fragColor = vec4(col,1.0);
}

