//SDF from https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

float sdCross( in vec2 p, in vec2 b, float r ) 
{
    p = abs(p); p = (p.y>p.x) ? p.yx : p.xy;
    vec2  q = p - b;
    float k = max(q.y,q.x);
    vec2  w = (k>0.0) ? q : vec2(b.y-p.x,-k);
    return sign(k)*length(max(w,0.0)) + r;
}

// Smooth Union Function from https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm

float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

// Simplex 2D noise
// GLSL Function from https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
vec3 permute(vec3 x) { return mod(((x*34.0)+1.0)*x, 289.0); }

float snoise(vec2 v){
  const vec4 C = vec4(0.211324865405187, 0.366025403784439,
           -0.577350269189626, 0.024390243902439);
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod(i, 289.0);
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
  + i.x + vec3(0.0, i1.x, 1.0 ));
  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy),
    dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = 2.*(gl_FragCoord.xy-.5*iResolution.xy)/iResolution.y;

    vec2 camera_pos = uv;

    float cube = sdBox(camera_pos + vec2(0., cos(iTime)*.2), vec2(.5));
    float cube_subtract = sdBox(camera_pos + vec2(0., cos(iTime)*.2), vec2(.6));

    float sphere = sdCircle(camera_pos+vec2(.5,.0) - vec2(cos(iTime)*.2), .3) ;
    float sphere_subtract = sdCircle(camera_pos+vec2(.5,.0) - vec2(cos(iTime)*.2), .33);

    float final_cube = opSubtraction(cube * snoise(uv + iTime*.3), cube_subtract);

    float final_sphere = opSubtraction(sphere, sphere_subtract);

    float depth = opSmoothUnion(final_cube, final_sphere, .1);

    vec3 color = vec3(1.0) - sign(depth);

    if(depth <= 0.01) {
        fragColor = vec4(color,1.0);
    } else {
        fragColor = BG_C;
    }

}

void mainImage( out vec4 O, in vec2 I ) {
    //obj
    vec2 uv = (I/iResolution.xy);
    float r = length(uv);    
    float dx = 1./iResolution.x;
    int tx = int(uv.y);
    float react = texelFetch( iChannel0, ivec2(tx,0), 0 ).x*0.05;

    float v = texture(iChannel0, vec2(uv.x, 1.)).r;
    float vp = texture(iChannel0, vec2(uv.x+dx, 1.)).r;

    vec2 p  = vec2(uv.y, v );
    vec2 pp = vec2(uv.y+dx, vp );    
    vec2 np = normalize(pp-p);

    //col

    float d = max(abs(np.x)-react * 0.5, abs(np.x)*react/0.25)*0.8 * react + .01;

    float k = mix(1.,0., (abs(uv.y-.5)*iResolution.y*5.01*v) * d);

    vec3 c = vec3(k + cos(iTime+uv.xyx+vec3(0,2,4)));

    O += vec4(c * .5 + react * (10. + c), 1.0);
}

