/*
    Author: Felipe Tovar-Henao [www.felipe-tovar-henao.com]
    Description: Reflecting hexagonal spiral, using shapers, modular distortion, and value quantization.
*/

#define PI 3.14159265359
#define u_time iTime
#define u_resolution iResolution

float quantize(in float x, in float d) {
    return floor(x/d)*d;
}

float fold(in float x) {
    return abs(mod(x+1.0,2.0)-1.0);
}

vec2 rotate2D(in vec2 vUV, in float theta) {
    vec2 r = vec2(cos(theta), sin(theta));
    return vUV * mat2(r.x, -r.y, r.y, r.x);
}

float relu_ramp(in float x, in float s) {
    return floor(x) + clamp((max(1.0, s)*(fract(x) - 0.5)) + 0.5, 0.0, 1.0);
}

float cosine_ramp(in float x, in float s) {
    float y = cos(fract(x)*3.14159265359);
    return floor(x) + 0.5 - (0.5*pow(abs(y), 1.0/s)*sign(y));
}

float sine_ramp(in float x, in float s) {
    float y = fract(x);
    float h = abs(0.5 - (0.5/max(1.0, s*s)));
    return floor(x) + y + h*sin(6.28318530718*y);
}

float bump_ramp(in float x, in float s) {
    float y = fract(x);
    return floor(x) + pow(y, exp(s*y));
}

float polygonSDF(in vec2 vUV, in int V) {
    float N = max(1.0, float(V));
    float rot = floor(N*(atan(-vUV.x,-vUV.y) / 6.28318530718 + 0.5));
    float offset = 3.14159265359*(N+2.0)/(2.0*N);
    float theta = (6.28318530718/N) * rot + offset;
    float side = tan(3.14159265359/max(N, 3.0));
    vUV *= mat2(cos(theta), -sin(theta), sin(theta), cos(theta));
    float scl = 0.5 * (float(N==2.0)*(0.25/sqrt(3.0)) + step(2.0,N)) / (step(1.0,N) + step(N, 2.0));
    vec2 r = vec2(step(3.0, N), side) * scl;
    vec2 P = abs(vUV)-r;
    return length(max(vec2(0.0), P)) + min(0.0, max(P.x, P.y/side));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 vUV = fragCoord.xy / u_resolution.xy;
    vUV -= 0.5;
    vUV.x *= u_resolution.x / u_resolution.y;

    float r = polygonSDF(vUV * 1.5, 6)*20.0;
    float res = fold(bump_ramp(u_time*0.1, 5.0)) / 1500.0;
    float a = atan(vUV.x, vUV.y) / PI + (u_time*r*res);
    float theta = relu_ramp((r + a - sine_ramp(u_time*0.1, 1.7)), 8.0);

    vUV = rotate2D(vUV, quantize(fold(theta), 1.0) + u_time * 0.025);

    float t = cosine_ramp(u_time*0.23, 1.5) + sine_ramp(-u_time*0.12, 4.0);

    float vx = fold(fract(vUV.x+vUV.y - t*0.013));
    float vy = fold(fract(vUV.y-vUV.x + t*0.023));

    float qx = fract(vUV.x*3.13 + t*0.131);
    float qy = fract(vUV.y*2.329 - t*0.151);
    float lim = 0.1;
    float mx = fold(quantize(vx, max(lim, qx) + lim));
    float my = fold(quantize(vy, max(lim, qy) + lim));
    float x = mod(vx, mx);
    float y = mod(vy, my);

    vec3 color = vec3(0.0);

    float c = abs(fold(x*y*2.0));
    c = quantize(sqrt(c), 0.2) * fold(quantize(theta+a, 0.125));

    vec3 c1a = vec3(0.502, 0.4863, 0.4118) * sin(sine_ramp(u_time*0.25, 4.0))*0.5 + 0.5;
    vec3 c1b = vec3(0.302, 0.4549, 0.5333);
    vec3 c2a = vec3(0.102, 0.0863, 0.0863);
    vec3 c2b = vec3(0.1412, 0.1725, 0.1882);

    color += clamp(c, 0.0, 1.0) * mix(c1a, c1b, fold(theta*3.0));
    vec3 shadow= clamp(1.0-color, 0.0, 1.0) * mix(c2a, c2b, fold(theta*4.32));
    color = clamp(color-shadow, 0.0, 1.0) + shadow + color;

    fragColor = vec4(color, 1.0);
}

/* 
To compute the maximum norm distance between a point p and a 
primitive we need to find the size of the smallest 
axis-aligned cube (compared to the size of the smallest 
sphere under Euclidian norm) centered at p that touches the 
surface of the primitive. There are usually three cases that 
need to be handled depending on whether the point that 
touches the surface of the primitive lies on a corner, edge 
or face of the cube.
*/

float ellipse(vec2 position, vec2 radius) {
    position = abs(position);
    float d = max(abs(position.x - radius.x), position.y);
    d = min(d, max(position.x, abs(position.y - radius.y)));
    float a = dot(vec2(1.0), 1.0 / (radius * radius));
    float b = dot(vec2(1.0), position / (radius * radius));
    float c = dot(vec2(1.0), (position * position) / (radius * radius)) - 1.0;
    float discriminant = b * b - a * c;
    if (discriminant >= 0.0) {
        float t = (b - sqrt(discriminant)) / a;
        d = min(t, d);
    }
    return d;
}

float ellipsoid(vec3 position, vec3 radius) {
    position = abs(position);

    // closest point lies on cube face
    float d = max(max(abs(position.x - radius.x), position.y), position.z);
    d = min(d, max(max(position.x, abs(position.y - radius.y)), position.z));
    d = min(d, max(max(position.x, position.y), abs(position.z - radius.z)));

    // closest point lies on cube edge
    {
        float a = dot(vec2(1.0), 1.0 / (radius.yz * radius.yz));
        float b = dot(vec2(1.0), position.yz / (radius.yz * radius.yz));
        float c = dot(vec2(1.0), (position.yz * position.yz) / (radius.yz * radius.yz)) - 1.0;
        float discriminant = b * b - a * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / a;
            d = min(d, max(t, position.x));
        }
    }

    // closest point lies on cube edge
    {
        float a = dot(vec2(1.0), 1.0 / (radius.xz * radius.xz));
        float b = dot(vec2(1.0), position.xz / (radius.xz * radius.xz));
        float c = dot(vec2(1.0), (position.xz * position.xz) / (radius.xz * radius.xz)) - 1.0;
        float discriminant = b * b - a * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / a;
            d = min(d, max(t, position.y));
       }
    }

    // closest point lies on cube edge
    {
        float a = dot(vec2(1.0), 1.0 / (radius.xy * radius.xy));
        float b = dot(vec2(1.0), position.xy / (radius.xy * radius.xy));
        float c = dot(vec2(1.0), (position.xy * position.xy) / (radius.xy * radius.xy)) - 1.0;
        float discriminant = b * b - a * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / a;
            d = min(d, max(t, position.z));
        }
    }

    // closest point lies on cube corner
    float a = dot(vec3(1.0), 1.0 / (radius * radius));
    float b = dot(vec3(1.0), position / (radius * radius));
    float c = dot(vec3(1.0), (position * position) / (radius * radius)) - 1.0;
    float discriminant = b * b - a * c;
    if (discriminant >= 0.0) {
        float t = (b - sqrt(discriminant)) / a;
        d = min(d, t);
    }

    return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 position = fragCoord / iResolution.xy;
    position = (position * 2.0) - 1.0;
    position.x *= iResolution.x / iResolution.y;

    vec2 mousePosition = iMouse.xy / iResolution.xy;
    mousePosition = (mousePosition * 2.0) - 1.0;
    mousePosition.x *= iResolution.x / iResolution.y;

    float z = (-1.0 + fract(iTime * 0.2) * 2.0) * 0.6;

    // float d = ellipse(position, vec2(0.75, 0.5));
    vec3 radius = vec3(0.75, 0.5, 0.5);
    float d = ellipsoid(vec3(position, z), radius);

    // same colorization that Inigo Quilez uses in his shaders
    vec3 color = vec3(1.0, 0.9, 1.0) + sign(d) * vec3(-0.3, 0.4, 0.3); // base color
    color *= 1.0 - exp(-3.0 * abs(d)); // gradient
	color *= 0.8 + 0.2 * cos(180.0 * d); // ripples
	color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 0.01, abs(d))); // outline
    fragColor = vec4(color, 1.0);
}

float random (in vec2 st) {
    return fract(sin(dot(st.xy,vec2(23.4325,67.233)))*54634.545422);
}

float noise2D (vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}


float fbm ( vec2 st, int octaves) {
    // Initial values
    float value = 0.;
    float amplitude = .4;

    // Loop of octaves
    for (int i = 0; i < octaves; i++) {
        value += amplitude * noise2D(st);
        st *= 4.;
        amplitude *= .6;
    }
    return value*1.3;
}

float moon(vec2 uv, float r){
    vec2 pos = uv - vec2(5., 8.);
    pos.x += sin(iTime/50.)*5.;

    float delta = sin(iTime/1.)*.08+0.45;
    return 1.-smoothstep(r,r+delta,dot(pos, pos));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv*=10.;

    float n = fbm(uv, 7);

    vec3 col = vec3(n);

    for(int i = 0; i < 3; i++){
        n *= fbm(uv+vec2(.2*iTime), 9);
    }

    vec3 starColor = smoothstep(0.89, 0.94, texture(iChannel0, uv).rrr);
    float star = starColor.x;
    if(star < 0.8){
    	star = 0.;   
    }

    col += vec3(star)*3.*abs(sin(iTime));

    col += vec3(moon(uv, .1))*6.;

    col *= n;

    col *= vec3(.4, .5, .8);

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 foreg= vec3(1.,.25,.55);//pink
vec3 backg= vec3(0.1,0.25,.9);//water blue

const float rate = .2;//advance
const float speed = .3;//retract
const float repeats = 6.;
const float verticalduplicate= 11.;
float thickness = 0.09;
float fade= 1.5;
float height= 2.;

float rand(float x){ return fract(sin(x*1234.4321)*4321.1234);}//classic

float shape(float x){
    x= fract(x);

    float s1 = 0.15;      // flat
    float s2 = s1 + 0.2;  // b1
    float s3 = s2 + 0.34; // flat
    float s4 = s3 + 0.1;  // b2
    float s5 = s4 + 0.03; // flat
    float s6 = s5 + 0.2;  // b3

    if (x < s1)  return 0.00;    
    if (x < s2)  return 0.05 - 0.05*cos((x-s1)*30.0);
    if (x < s3)  return 0.00;
    if (x < s4)  return 0.02 - 0.02*cos((x-s3)*60.0);
    if (x < s5)  return 0.0 ;
    if (x < s6)  return 0.3*sin((x-s5)*35.0);
    if (x > 1.0) return -.4;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv= (2.*fragCoord.xy+iResolution.xy)/iResolution.x;

    float offset= speed*iTime;
    float rate=  rate*(rand(floor(uv.y*repeats*2.))+1.);//random speed per row

    float x= fract(rate*iTime)+offset;
    uv.x+= offset;
    vec2 headPos = vec2(   x, shape(x   *repeats)*height);
    vec2 linePos = vec2(uv.x, shape(uv.x*repeats)*height);

    uv.y= fract(uv.y*verticalduplicate)*2.-1.;//repeat vertical

    fragColor = vec4(sqrt(
        mix(backg,foreg,
            abs(uv.y-linePos.y)>thickness ?
                0.:
                pow( fract(linePos.x-headPos.x), fade)
        )),1.0);
}

/* 
To compute the maximum norm distance between a point p and a 
primitive we need to find the size of the smallest 
axis-aligned cube (compared to the size of the smallest 
sphere under Euclidian norm) centered at p that touches the 
surface of the primitive. There are usually three cases that 
need to be handled depending on whether the point that 
touches the surface of the primitive lies on a corner, edge 
or face of the cube. In the case of a plane the point 
closest to the primitive always lies on a corner of the 
cube. 
*/

float plane(vec3 position, vec3 planeNormal, float planeDistance) {
    float d = dot(position, planeNormal) + planeDistance;
    return d / dot(planeNormal, sign(planeNormal));
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 position = fragCoord / iResolution.xy;
    position = (position * 2.0) - 1.0;
    position.x *= iResolution.x / iResolution.y;

    vec2 mousePosition = iMouse.xy / iResolution.xy;
    mousePosition = (mousePosition * 2.0) - 1.0;
    mousePosition.x *= iResolution.x / iResolution.y;

    vec3 planeNormal = vec3(cos(iTime), sin(iTime), 0.0);
    float planeDistance = 0.0; // distance from plane to origin
    float d = plane(vec3(position, 0.0), planeNormal, planeDistance);

    // same colorization that Inigo Quilez uses in his shaders
    vec3 color = vec3(1.0, 0.9, 1.0) + sign(d) * vec3(-0.3, 0.4, 0.3); // base color
    color *= 1.0 - exp(-3.0 * abs(d)); // gradient
	color *= 0.8 + 0.2 * cos(180.0 * d); // ripples
	color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 0.01, abs(d))); // outline

    d = plane(vec3(mousePosition, 0.0), planeNormal, planeDistance);
    color = mix(color, vec3(1.0, 1.0, 0.0), 1.0 - smoothstep(0.0, 0.01, abs(max(abs(position.x - mousePosition.x), abs(position.y - mousePosition.y)) - abs(d)))); // box
	fragColor = vec4(color,1.0);
}

#define E 2.718281828459

float CubicBezier(float x, vec4 u) {
    return u.x*pow(1.-x,3.)+3.*u.y*pow(1.-x,2.)*x+3.*u.z*pow(1.-x,2.)*pow(x,2.)+u.w*pow(x,3.);
}

float move(){
    return pow(E, -5. * pow(fract(iTime),2.));
}

vec3 shade(vec4 res )
{
    float ra = length(res.yz);
    float an = atan(res.y,res.z) + 2.0*iTime;
    float pa = sin(3.0*an);

    vec3 cola = 0.5 + 0.5*sin( (res.w/32.0)*3.5 + vec3(0.0,1.0,2.0) );

    vec3 col = vec3(0.0);
    col += cola*0.4*(1.0-smoothstep( 0.90, 1.00, ra) );
    col += cola*1.0*(1.0-smoothstep( 0.00, 0.03, abs(ra-0.8)))*(0.5+0.5*pa);
    col += cola*1.0*(1.0-smoothstep( 0.00, 0.20, abs(ra-0.8)))*(0.5+0.5*pa);
    col += cola*0.5*(1.0-smoothstep( 0.05, 0.10, abs(ra-0.5)))*(0.5+0.5*pa);
    col += cola*0.7*(1.0-smoothstep( 0.00, 0.30, abs(ra-0.5)))*(0.5+0.5*pa);

    return col*0.3;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv.y *= 2.;

    vec4 bezierU = vec4(0., 1., 0., 1.);
    uv.y -= CubicBezier(clamp(uv.x, 0.0, 1.), bezierU);

    vec3 col = vec3(0.5);
    col += smoothstep(0.03, 0.02, distance(uv.y, 0.5) * 2.)  
            * smoothstep(0., 0.1, 1. - distance(uv.x, 0.5) * 2.) 
            * smoothstep(0.5, 0., distance(1. - uv.x, move()) * 2. );

    fragColor = vec4(1.0/(1.0+col),1.);
}

const int COLORS_SIZE = 4;
const vec3 ERR_COLOR = vec3(0.0, 0.0, 0.0);

const int SAMPLES_S = 4;
const int N_SAMPLES = SAMPLES_S * SAMPLES_S;

float function(float x, float y, float t)
{
    t = t + 1.8;    
    float t2 = t / 2.0;
    float t4 = t / 4.0;
    float st2 = sin(t2);
    float ct2 = cos(t2);
    float st4 = sin(t4);
    float tt4 = tan(t4);

    float xr = x * cos(pow(t2, 1.01));
    float yr = y * sin(pow(t2, 1.01));

    return (0.4*x * cos(0.5*y)) + (tt4 * 0.6*xr * sin(xr)) + (st4 + 1.0) * pow(0.3*xr + 0.2*yr, 2.0);
}

vec2[N_SAMPLES] supersampling(vec2 coord, float pixSize)
{
    float sampleSize = pixSize / float(SAMPLES_S);
    vec2 leftUp = coord - pixSize / 2.0 + sampleSize / 2.0; 

    vec2 samples[N_SAMPLES];
    for (int i = 0; i < SAMPLES_S; i++)
    {
        for (int j = 0; j < SAMPLES_S; j++)
        {
            vec2 sampl = leftUp + vec2(sampleSize * float(j), sampleSize * float(i)); 
            samples[i * SAMPLES_S + j] = sampl;
        }
    }
    return samples;    
}

vec2 uvToCarthesian(vec2 uv, vec2 xDomain, vec2 yDomain)
{
    float x = xDomain.x + uv.x * (xDomain.y - xDomain.x);
    float y = yDomain.x + uv.y * (yDomain.y - yDomain.x);
    return vec2(x, y);
}

vec2 getOrigin(vec2 xDomain, vec2 yDomain)
{
    float x = xDomain.x + (xDomain.y - xDomain.x) / 2.0;
    float y = yDomain.x + (yDomain.y - yDomain.x) / 2.0;
    return vec2(x, y);
}

vec2 carthesianCircleInversion(vec2 carth, vec2 o, float r)
{
    vec2 dir = carth - o;
    float x = r * r / length(dir);
    return x * normalize(dir) + o;
}

vec3 getColor(float value, vec2 range, vec4 colors[COLORS_SIZE])
{
    value = clamp(value, range.x, range.y);

    float len = range.y - range.x; 
    float norm = (value - range.x) / len;

    // Get two closest colors and mix them
    vec3 color = ERR_COLOR;
    for (int i = 0; i < COLORS_SIZE-1; i++)
    {
        vec4 curr = colors[i];
        vec4 next = colors[i+1];
        if (curr.w <= norm && norm <= next.w)
        {
            float btw = (norm - curr.w) / (next.w - curr.w);
            color = mix(curr.xyz, next.xyz, btw);
            break;
        }
    }
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Function domain
    const vec2 xDomain = vec2(-35.0, 35.0);
    const vec2 yDomain = vec2(-25.0, 25.0);

    // Function range
    const vec2 range = vec2(0, 100);

    // Colors
    vec4 colors[COLORS_SIZE] = vec4[](
        vec4(0.0, 0.0, 0.0, 0.0),
        vec4(0.78, 0.16, 0.11, 0.34),
        vec4(0.89, 0.48, 0.21, 0.63),
        vec4(0.0, 0.0, 0.0, 1.0)
    );

    vec2 origin = getOrigin(xDomain, yDomain);

    vec2 samples[] = supersampling(fragCoord, 1.0);
    vec3 totalColor = vec3(0.0);
    for (int i = 0; i < N_SAMPLES; i++)
    {
        vec2 uv = samples[i] / iResolution.xy;
        uv.y = 1.0 - uv.y;

        vec2 coord = uvToCarthesian(uv, xDomain, yDomain);
        coord = carthesianCircleInversion(coord, origin, 22.0);

        float value = function(coord.x, coord.y, iTime);

        totalColor = totalColor + getColor(value, range, colors);
    }
    vec3 avgColor = totalColor / float(N_SAMPLES);    

    fragColor = vec4(avgColor, 1.0);
}

