    vec3 col = vec3(0.);
    for (int i = 0; i < 2; i++)
    {
        for (int j = 0; j < 2; j++)
        {
            vec3 ro = vec3(0., 0., -3.);
            vec3 rd = normalize(vec3(uv + vec2(float(i)*0.5+0.25, float(j)*0.5+0.25)*0.006, 0.) - ro);

            vec2 res = march(ro, rd);
            float dist = res.x;
            float iters = res.y;

            if (dist < 10.)
            {
                vec3 hit = ro + dist * rd;
                vec3 light = vec3(sin(iTime), 0.5, cos(iTime)) - hit;
                vec3 ld = normalize(light);
                vec3 norm = normal(hit);
                vec3 albedo = (norm * 0.5 + 0.5);
                float diff = 0.;

                if (march(hit + norm * 0.001, ld).x >= length(light))
