#define M_PI 3.1415926535897932384626433832795

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

vec3 hash3( vec3 p )
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*13.5453123);
}

float noise( in vec3 p ) {
    vec3 a = hash3(p);
    return sin(dot(a,a));
}

vec3 rotateX( in vec3 p, float t )
{
    float co = cos(t);
    float si = sin(t);
    p.yz = mat2(co,-si,si,co)*p.yz;
    return p;
}
vec3 rotateY( in vec3 p, float t )
{
    float co = cos(t);
    float si = sin(t);
    p.xz = mat2(co,-si,si,co)*p.xz;
    return p;
}
vec3 rotateZ( in vec3 p, float t )
{
    float co = cos(t);
    float si = sin(t);
    p.xy = mat2(co,-si,si,co)*p.xy;
    return p;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec4 opU( vec4 d1, vec4 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

vec4 opS( vec4 d2, vec4 d1 )
{
	return (-d1.x>d2.x) ? vec4(-d1.x, d1.yzw) : d2;
}

vec3 opRep( in vec3 p, in vec3 c)
{
    return mod(p+0.5*c,c)-0.5*c;
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

vec4 mapA(vec3 p) {
    vec3 q = rotateZ(p, M_PI*0.15);
    vec4 A = vec4(sdBox(q, vec3(0.5,3.0, 0.5)), vec3(1.0));
    q -= vec3(-1.0, 0.0, 0.0);
    vec4 D = vec4(sdBox(q, vec3(0.5,3.0, 0.6)), vec3(1.0));
    q = rotateZ(p- vec3(1.4, 0.0, 0.0), -M_PI*0.15) ;
    vec4 A2 = vec4(sdBox(q, vec3(0.5,3.0, 0.5)),vec3(1.0));
    q -= vec3(1.0, 0.0, 0.0);
    vec4 D2 = vec4(sdBox(q, vec3(0.5,3.0, 0.6)), vec3(1.0));
    q = p - vec3(0.7, -2.1, 0.0);
    vec4 D3 = vec4(sdBox(q, vec3(0.7,0.7, 0.6)), vec3(1.0));
    q = p - vec3(0.7, 1.3, 0.0);
    vec4 A3 = vec4(sdBox(q, vec3(1.1, 0.4, 0.5)),vec3(1.0));
    A = opU(A, A2);
    A = opS(A, D);
    A = opS(A, D2);
    A = opS(A, D3);
    A = opU(A, A3);
    return A;
}

vec4 map(vec3 p) {
    vec4 res = vec4(1e10, 0, 0,0);

    {
    float s = (1.0+sin((p.x/20.0)*M_PI+(p.z/20.0)*M_PI+iTime*M_PI))/2.0;
    vec3 c = vec3(10.0, 10.0, 6.0);
    vec3 q = opRep(p, c);
    vec3 id = floor((p+0.5*c)/c);
    float t2 = 2.0*M_PI*noise((id)*0.1);
    float t = t2;
    q.xy*=rot(-t);
    q.yz*=rot(-t);
    q.zx*=rot(t);
    vec4 A = mapA(q/s)*s;
    res = opU(res, A);
    res.x = max(res.x, sdBox(id, vec3(2.0, 2.0,3.0)));
    //res.x -= 0.3*noise(p);
    }

    return res;
}

vec2 iSphere( in vec3 ro, in vec3 rd, in float rad )
{
	float b = dot( ro, rd );
	float c = dot( ro, ro ) - rad*rad;
	float h = b*b - c;
	if( h<0.0 ) return vec2(-1.0);
    h = sqrt(h);
	return vec2(-b-h, -b+h );
}

vec3 calcNormal( in vec3 p )
{
    const float h = 0.05;
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map( p + k.xyy*h ).x + 
                      k.yyx*map( p + k.yyx*h ).x + 
                      k.yxy*map( p + k.yxy*h ).x + 
                      k.xxx*map( p + k.xxx*h ).x );
}   

vec4 raycast( in vec3 ro, in vec3 rd )
{
    vec4 res = vec4(-1.0);
    vec2 tminmax = iSphere( ro, rd, 100.0 );
	if( tminmax.y>0.0 )
    {
        float t = max(tminmax.x,0.001);
        for( int i=0; i<256 && t<tminmax.y; i++ )
        {
            vec4 h = map(ro+t*rd);
            if( h.x<0.01 ) { res=vec4(t,h.yzw); break; }
            t += 0.5*h.x;
        }
    }

    return res;
}

vec3 skyColor(vec3 rd) {
    float k = (max(rd.y,0.0)*0.8+0.2)*0.8;
    vec3 sky = vec3(pow(1.0-k,2.0), 1.0-k, 0.6+(1.0-k)*0.4) * 1.1;
    return sky;
}

float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t).x;
        if( h<0.001 )
            return 0.0;
        res = min( res, k*h/t );
        t += h;
    }
    return res;
}

vec3 render( in vec3 ro, vec3 rd )
{ 
    vec4 res = raycast(ro, rd);
    vec3 pos = ro + res.x*rd;
    vec3 col = vec3(0.0);
    vec3 normal = calcNormal(pos);

    if (res.x <0.0) {
        return skyColor(rd);
    }
    vec3 sunDir = normalize(vec3(1.0,-1.0,1.0));
    vec3 material = vec3(1.0);
    float sun = clamp( dot( normal, sunDir ), 0.0, 1.0 );
    float sky = clamp( 0.5 + 0.5*normal.y, 0.0 ,1.0 );
    float sha = softshadow(pos, sunDir, 0.03, 100.0, 30.0);
    return 0.4*sky + sun*vec3(1.64,1.27,0.99)*pow(vec3(sha),vec3(1.0,1.2,1.5));
}

vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

mat3 viewMat( in vec3 eye, in vec3 look )
{
    vec3 w = normalize(eye-look);
    vec3 u = normalize(cross(w, vec3(0,1,0)));
    vec3 v = cross(w, u);
    return mat3( u, v, w );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 dir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec2 mouse = iMouse.xy /iResolution.xy;
    if (iMouse.xy == vec2(0)) {
        mouse = vec2(0.5,0.5);
    }
    vec2 spr = vec2(mouse.x * M_PI - M_PI/2.0, mouse.y*M_PI-M_PI)*0.8;
    spr.x += 0.3*iTime;
    float r = 100.0;
    vec3 eye = vec3(-r*sin(spr.x)*sin(spr.y), -r*cos(spr.y),-r*sin(spr.y)*cos(spr.x)) + vec3(0.0, -2.0, 0.0);
    vec3 look = vec3(0.0,0.0,0);
    mat3 view = viewMat(eye, look);
    dir = view * dir;
    fragColor = vec4(render(eye, dir), 1.0);
}

