float PrSphDf (vec3 p, float r);
float SmoothMax (float a, float b, float r);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);

vec3 ltDir;
float tCur, dstFar;
int idObj;
bool isRefl;
const float pi = 3.1415927;

#if 1
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

vec3 DodecSym (vec3 p)
{
  vec2 csD;
  csD = sin (0.5 * atan (2.) + vec2 (0.5 * pi, 0.));
  p.xz = Rot2Cs (vec2 (p.x, abs (p.z)), csD);
  p.xy = Rot2D (p.xy, - pi / 10.);
  p.x = - abs (p.x);
  for (int k = 0; k <= 3; k ++) {
    p.zy = Rot2Cs (p.zy, vec2 (csD.x, - csD.y));
    p.y = - abs (p.y);
    p.zy = Rot2Cs (p.zy, csD);
    if (k < 3) p.xy = Rot2Cs (p.xy, sin (-2. * pi / 5. + vec2 (0.5 * pi, 0.)));
  }
  p.xy = sin (mod (atan (p.x, p.y) + pi / 5., 2. * pi / 5.) - pi / 5. +
     vec2 (0., 0.5 * pi)) * length (p.xy);
  p.xz = - vec2 (abs (p.x), p.z);
  return p;
}

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 t;
  float dMin, d, a1, a2, rEx, s;
  rEx = 3.;
  dMin = dstFar;
  t = vec2 (0.365, -1.);
  q = DodecSym (p);
  a1 = 0.5 * acos (-1. / sqrt (5.));
  a2 = 0.5 * acos (- (80. + 9. * sqrt (5.)) / 109.);
  d = length (q) - rEx;
  d = SmoothMax (d, min (dot (q.yz, sin (a1 - pi + vec2 (0., 0.5 * pi))),
     dot (q.xy, sin (pi / 5. + vec2 (0.5 * pi, 0.)))) - 0.04, 0.04);
  DMIN (1);
  s = - dot (q.yz, sin (a1 - a2 + vec2 (0., 0.5 * pi)));
  q.yz -= t * 0.82 * rEx;
  d = length (q) - 0.17 * rEx;
  d = max (s - 0.91 * rEx, - d);
  DMIN (1);
  q.yz -= t * 0.05 * rEx;
  if (! isRefl) { 
    d = length (q) - 0.16 * rEx;
    DMIN (2);
  }
  q.yz -= t * 6. * rEx;
  d = length (q) - rEx;
  DMIN (3);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 ObjCol (vec3 rd, vec3 vn)
{
  vec4 col4;
  vec3 col;
  if (idObj == 1) col4 = vec4 (0.7, 0.5, 0., 0.2);
  else if (idObj == 2) col4 = vec4 (0.6, 0.6, 0.7, 0.5);
  else if (idObj == 3) col4 = vec4 (1., 0., 0., -1.);
  if (col4.a >= 0.) col = col4.rgb * (0.1 + 0.2 * max (- dot (vn, ltDir), 0.) +
     0.8 * max (dot (vn, ltDir), 0.)) + col4.a * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
  else col = col4.rgb * (0.2 + 0.8 * max (- dot (vn, rd), 0.));
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, colR, vn;
  float dstObj;
  isRefl = false;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col = ObjCol (rd, vn);
    if (idObj == 2) {
      rd = reflect (rd, vn);
      ro += 0.01 * rd;
      isRefl = true;
      dstObj = ObjRay (ro, rd);
      if (dstObj < dstFar) {
        ro += dstObj * rd;
        vn = ObjNf (ro);
        colR = ObjCol (rd, vn);
      } else colR = vec3 (0.1, 0.1, 0.2);
      col = mix (col, colR, 0.7);
    }
  } else col = vec3 (0.1, 0.1, 0.2);
  return clamp (col, 0., 1.);
}

#define AA  1   // optional antialiasing

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az -= 0.03 * pi * tCur;
    el -= 0.1 * pi * sin (0.02 * pi * tCur);
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -18.);
  zmFac = 5.;
  dstFar = 50.;
  ltDir = normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

