#define fGlobalTime iTime
#define STEPS 128.0
#define MDIST 2000.0
#define pi 3.1415926535
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define pmod(p,x) (mod(p,x)-0.5*(x))
#define fft1 texelFetch( iChannel0, ivec2(1,0), 0 ).x*0.08
#define fft2 texelFetch( iChannel0, ivec2(128,0), 0 ).x*0.04
#define fft3 texelFetch( iChannel0, ivec2(25,0), 0 ).x*0.02
vec3 glow = vec3(0);
vec3 cam = vec3(0);
bool bnc = false;
void mo(inout vec2 p){if(p.y<p.x) p = p.yx;}
void boxFold(inout vec3 z, vec3 r){
  z = clamp(z,-r,r)*2.0-z;
}
float frame (vec3 p, vec3 s, float r){
  p = abs(p)-s;
  mo(p.xz);
  mo(p.zy);
  p.z=max(0.,p.z);
  return length(p)-r; 
}
float ext(vec3 p, float s, float h){
  vec2 b = vec2(s,abs(p.y)-h);
  return min(max(b.x,b.y),0.)+length(max(b,0.));
}

float disks(vec3 p, float t){
  float m = 1.0+fft1*20.0;
  float id = floor(p.y/m)+0.5;
  p.y = pmod(p.y, m);
  float width = 5.0-id*0.1;

  p.zx*=rot(id*3.0*t*0.1);
  p.zx = abs(p.zx)-6.0+sin(t*2.5);
  float a = length(p.xz)-width;
  a = ext(p,a,0.3);
  a-=0.15;
  return a;
}
vec2 map(vec3 p){
  float t= mod(fGlobalTime,999.0);
  vec3 po = p;
  vec2 a = vec2(1);
  vec2 b = vec2(2);


   //FANCY ROT
  float tt = pow(fract(t*2.2),3.0)+floor(t*2.2);
  p.xy*=rot(tt*pi/3.0);
  p.zx*=rot(-tt*pi/3.0);
  p.yz*=rot(-tt*pi/3.0);

  p = abs(p)-40.0-fft2*1000.0;

  p.yz*=rot(-0.4);
  p.zx*=rot(-0.4);
  p.xy*=rot(0.4);
   //FANCY ROT
 // float tt = pow(fract(t*1.5),3.0)+floor(t*1.5);
  p.xy*=rot(tt*pi/3.0);
  p.zx*=rot(-tt*pi/3.0);
  p.yz*=rot(tt*pi/3.0);


  //DISKS
  a.x = disks(p,t);
  a.x*=0.6;
  a.y = 1.0;
  glow+=0.1/(0.1+a.x*a.x)*vec3(0.75,0.3,0.1);


  //FRAME
  p.zx*=rot(t);
  b.x = frame(p,vec3(15,200,15),0.75);
  glow+=0.1/(0.1+b.x*b.x)*vec3(0.4,0.23,0.6);
  b.y = 2.0;
  a =(a.x<b.x)?a:b;

  //SPHERE 
  p = po;

  p.xy*=rot(-tt*pi/16.0);
  p.zx*=rot(-tt*pi/16.0);
  p.yz*=rot(-tt*pi/16.0);


  boxFold(p,vec3(110.0));
  b.x = length(p)-40.0;
  p.zx*=rot(-t*0.75);
  p*=0.1;
  float disp = sin(p.x)*sin(p.y)*sin(p.z);

  b.y = 3.0;
  glow+=0.3/(0.1+b.x*b.x)*
  mix(vec3(0,0.2,0.6),vec3(0.600,0.102,0.392),clamp(length(po/300.0),0.0,1.0));
  b.x+=disp*(1.5+fft3*200.0);
  a =(a.x<b.x)?a:b;

  //POLE
  p = po;
  b.y = 5.0;
  p.zx = pmod(p.zx,600.0);
  p.xy = pmod(p.xy,600.0);
  p.yz = pmod(p.yz+300.0,600.0);
  b.x = length(p.zx)-20.0;
  b.x = min(b.x,length(p.xy)-20.0);
  b.x = min(b.x,length(p.yz)-20.0);
  //glow+=
  if(!bnc)a =(a.x<b.x)?a:b;

  a.x = max(a.x,-(length(po-cam)-35.0));

  return a;
}
vec3 norm(vec3 p){
  vec2 e = vec2(0.01,0);
  return normalize(map(p).x-vec3(
  map(p-e.xyy).x,
  map(p-e.yxy).x,
  map(p-e.yyx).x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
	uv -= 0.5;
	uv /= vec2(iResolution.y / iResolution.x, 1);
  vec3 col = vec3(0);
  float t= mod(fGlobalTime,999.0);
  vec3 ro = vec3(0,20.0+sin(t)*70.0,-70.0)*2.2;
  ro.zx*=rot(t*0.2);
  cam = ro;
  vec3 lk = vec3(0,10,0);
  vec3 f = normalize(lk-ro);
  vec3 r = normalize(cross(vec3(0,1,0),f));
  vec3 rd = normalize(f*0.65+uv.x*r+uv.y*cross(f,r));
  vec3 p = ro;
  vec2 d;
  float shad, dO;
  bool hit = false;
  //bool bnc = false;
  for(float i = 0.0; i<STEPS; i++){
    p = ro+rd*dO;
    d = map(p);

    if(abs(d.x)<0.005){
      if(!bnc&&d.y==3.0){
        vec3 n = norm(p);
        ro = p+n*0.05;
        rd = n;
        dO = 0.0;
        bnc = true;
      }
      else{
        hit = true;
      shad = i/STEPS;
      break;
      }
    }
    if(dO>MDIST){
      dO=MDIST;
      break;
    }
    dO+=d.x*0.8;
  }
  col+=glow*0.1;

  if(hit){

    vec3 ld = vec3(-1,1,1);
    float shadow = 1.0;
    for(float h = 0.4;h<100.0;){
      float dd = map(p+ld*h).x;
      if(dd<0.1){shadow = 0.; break;}
      h+=dd*0.9;
      shadow = min(shadow,dd*3.0);
    }
    shadow = max(0.4,shadow);

    vec3 n = norm(p);
    //col = vec3(shad);
    col += vec3(length(n*0.5+0.5))*0.6;
    col *=shadow;

    if(d.y==5.0)col = glow*3.0;
  }

  //if(bnc) col = 1.0-col;
	fragColor = vec4(col,0);
}

