	vec3 lookat=vec3(0.0,0.0,0.0);
	float zoom=5.0;

	vec3 f=normalize(lookat-ro);
	vec3 r=normalize(cross(vec3(0.0,1.0,0.0),f));
	vec3 u=cross(f,r);
	vec3 c=ro+f*zoom;
	vec3 i=c+uv.x*r+uv.y*u;
	vec3 rd=normalize(i-ro);

    float d,t;
    vec3 p,n;

	vec3 col=vec3(0.0);

    for(int i=0;i<ITR;i++){
        p=ro+rd*t;
        d=mainDist(p);
        n=genNormal(p);
        if(d<EPS)break;
        t+=d;
    }
	col=n*0.5+0.5;

	fragColor=vec4(col,1.0);
}

