// Rand value between 0 and 1
float rand(vec2 p) {
	return fract(sin(dot(p, vec2(12.543,514.123)))*4732.12);
}

// Value noise
float noise(vec2 p) {
	vec2 f = smoothstep(0.0, 1.0, fract(p));
	vec2 i = floor(p);
	float a = rand(i);
	float b = rand(i+vec2(1.0,0.0));
	float c = rand(i+vec2(0.0,1.0));
	float d = rand(i+vec2(1.0,1.0));
	return mix(mix(a, b, f.x), mix(c, d, f.x), f.y);

}

// Fractal noise
float fbm(vec2 p) {
    float a = 0.5;
    float r = 0.0;
    for (int i = 0; i < 8; i++) {
        r += a*noise(p);
        a *= 0.5;
        p *= 2.0;
    }
    return r;
}

// Lasers originating from a central point
float laser(vec2 p, int num) {
	float r = atan(p.x, p.y);
	float sn = sin(r*float(num)+iTime);
    float lzr = 0.5+0.5*sn;
    lzr = lzr*lzr*lzr*lzr*lzr;
    float glow = pow(clamp(sn, 0.0, 1.0),100.0);
	return lzr+glow;
}

// Mix of fractal noises to simulate fog
float clouds(vec2 uv) {
    vec2 t = vec2(0,iTime);
	float c1 = fbm(fbm(uv*3.0)*0.75+uv*3.0+t/3.0);
	float c2 = fbm(fbm(uv*2.0)*0.5+uv*7.0+t/3.0);
	float c3 = fbm(fbm(uv*10.0-t)*0.75+uv*5.0+t/6.0);
    float r = mix(c1, c2, c3*c3);
	return r*r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.y;
    vec2 hs = iResolution.xy/iResolution.y*0.5;
    vec2 uvc = uv-hs;
	float l = (1.0 + 3.0*noise(vec2(15.0-iTime)))
        * laser(vec2(uv.x+0.5, uv.y*(0.5 + 10.0*noise(vec2(iTime/5.0))) + 0.1), 15);
	l += fbm(vec2(2.0*iTime))
        * laser(vec2(hs.x-uvc.x-0.2, uv.y+0.1), 25);
	l += noise(vec2(iTime-73.0))
        * laser(vec2(uvc.x, 1.0-uv.y+0.5), 30);
    float c = clouds(uv);
    vec4 col = vec4(0, 1, 0, 1)*(uv.y*l+uv.y*uv.y)*c;
    fragColor = pow(col, vec4(0.75));
}

#define PI 3.141592653 
#define countX 17.
#define petalWidth .4
#define AA 2.

vec3 colMap(float v) {

	v=mod(v, PI+1.3)-.8;	
    return vec3(
		sin(sin(v-.6)),
		sin(sin(v)),
		sin(sin(v+.8))
	);

} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    for(float aa=0.; aa<AA; aa++){
        for(float bb=0.; bb<AA; bb++){

            vec2 uv = (fragCoord + vec2(aa,bb)*1./AA)/iResolution.xy;


            uv-=.5;
            uv.y*=iResolution.y/iResolution.x;
            uv*=2.;

            float tim=-iTime*.1;
            float posintim=sin(tim)*.5+.5;
            float dst=length(uv);
            vec2 tuv = vec2(0.,dst);
            vec2 id = vec2(0.);
            float zoomSpeed=tim*.4;
            float angle1=(atan(uv.x,uv.y)/PI*.5+.5)+zoomSpeed + tim*.3;
            float angle2=angle1-2.*zoomSpeed;
            float add=pow(dst, posintim*.7+.1)*countX*petalWidth;
            tuv.x=mod(angle1*countX+add, 1.);
            tuv.y=mod(angle2*countX-add, 1.);
            id.x=ceil(angle1*countX+add);
            id.y=floor(angle2*countX-add);
            float edgeDist = max(max(tuv.x, tuv.y), max(1.-tuv.x, 1.-tuv.y));
            tuv-=.5;
            tuv*=dst*3.;
            float t=log(dst+1.6);
            float v=abs(tuv.x+tuv.y)+pow(tuv.y-tuv.x, 2.);
            v+=posintim *pow(edgeDist,25.);
            vec3 col=vec3(smoothstep(t+.1,t,v) );
            col*=colMap(abs(id.x-id.y)*.4);


            fragColor += vec4(col/(AA*AA),1.0);
        }
    }
}

