#define MAX_DEPTH 3 // 2 reflection passes. 60fps on this machine

#define PI 3.14159

// utils from inigo
float sdSphere(vec3 p, float r) {
  return length(p) - r;
}
float sdBox(vec3 p, vec3 b) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
float opRep( in float p, in float s ) {
    return mod(p+s*0.5,s)-s*0.5;
}
float expImpulse(float x, float k) {
    float h = k*x;
    return h*exp(1.0-h);
}
// modified from mercury sdf
vec3 pModPolarXY(vec3 p, float repetitions) {
	float angle = 2.*PI/repetitions;
	float a = atan(p.y, p.x) + angle/2. + p.z / 2.; // twist the tunnel
	float r = length(p.xy);
	float c = floor(a/angle);
	a = mod(a,angle) - angle/2.;
	return vec3(cos(a) * r, sin(a) * r, p.z);
}

const vec3 Killmonger = vec3(0.77, 0.62, 0.32);
const vec3 TChalla = vec3(0.72, 0.52, 0.99); // RIP
const vec3 Okoye = vec3(0.84, 0.53, 0.46);
// All The Stars from Black Panther has a beat per minute of 97
const float secondPerBeat = 60. / 97.;
// Beat's peak doesn't happen at time 0. Shift it to be so
// This eases manually syncing with the music's beat if you press restart
const float beatOffset = 0.075;

struct material { vec3 attenuation; vec3 emission; float sdf; };

material map(vec3 p, float beat) {
    // polar repeat & twist field
    vec3 rotatedP = pModPolarXY(p, 8.); // 8 rail blocks
    rotatedP.x += beat * 0.03 - 0.3; // periodic impulse, try in graphtoy
    rotatedP.z = opRep(p.z - iTime / 2., 0.25); // z axis move & rail block repeat

    const vec3 halfSize = vec3(.015, .077, 0.11);
    float dRailways = sdBox(rotatedP, halfSize);
    float dLights = sdBox(rotatedP, halfSize + vec3(-0.01, 0.01, -0.01)) - 0.004; // rounder
    float dOrb = sdSphere(p, 0.1);

    // colors
    vec3 attenuation, emission;
    if (dRailways > dLights || dRailways > dOrb) {
        vec3 color = mod(iTime + beatOffset, secondPerBeat * 2.) > secondPerBeat ? TChalla : Killmonger;
        attenuation = color;
        emission = color * (0.7 + beat / 2.); // tune up emission when the beat's high
    } else {
        attenuation = Okoye;
        emission = vec3(0);
    }

    return material(attenuation, emission, min(dRailways, min(dLights, dOrb)));
}

vec3 calcNormal( in vec3 p, float beat) {
    float h = 1e-5;
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map( p + k.xyy*h, beat).sdf + 
                      k.yyx*map( p + k.yyx*h, beat).sdf + 
                      k.yxy*map( p + k.yxy*h, beat).sdf + 
                      k.xxx*map( p + k.xxx*h, beat).sdf );
}

float maxT = 12.;
bool rayMarch(vec3 ro, vec3 rd, float beat, out float t, out material m, out vec3 glow) {
    t = 1e-3;
    glow = vec3(1);
    for(int i = 0; i < 85; i++) {
        vec3 p = ro + t * rd;
        m = map(p, beat);
        float df = abs(m.sdf);

        if (df < 0.0007) return true;
        if (t > maxT) return false;

        // add a physically incorrect glow when near an emissive material
        const float glowDist = .05;
        float diff = glowDist - df;
        glow += m.emission * step(0., diff) * diff * 5.; // adjust 5 for glow strengh

        t += max(0.001, m.sdf);
    }
    return false;
}

vec3 rayTrace(vec3 ro, vec3 rd, float beat, vec2 seed) {
    vec3 color = vec3(0);
    // We want the emissive lights to also keep reflecting. See usage below
    // This should be the correct way to calculate such light. I haven't seen
    // it anywhere else (iteratively, on GLSL) so I had to derive the calculations.
    vec3 accumulatedAttenuation = vec3(1);

    vec3 ro_ = ro;
    vec3 rd_ = rd;

    const vec3 background = vec3(0.09, 0.09, .27);
    const vec3 backgroundAttenuation = background * 0.1;
    vec3 glow;

    for (int depth = 0; depth < MAX_DEPTH; depth++) {
        float t;
        material m;
        bool hit = rayMarch(ro_, rd_, beat, t, m, glow);

        if (hit) {
            vec3 pos = ro_ + rd_ * t;
            vec3 normal = calcNormal(pos, beat);
            vec3 reflected = reflect(rd_, normal);

            color += accumulatedAttenuation * m.emission;
            accumulatedAttenuation *= m.attenuation;

            if (dot(reflected, normal) > 0.) {
                ro_ = pos;
                rd_ = reflected;
            }
        } else {
            float gradient = rd_.y / iResolution.y * 500. + 0.5;
            vec3 emission = background * gradient;

            color += accumulatedAttenuation * emission;
            accumulatedAttenuation *= backgroundAttenuation;
            color += accumulatedAttenuation;

            break;
        }
    }
    color *= glow; // this isn't physically accurate but whatever

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (1. * fragCoord) / iResolution.xy;
    float aspect_ratio = iResolution.x / iResolution.y;

    // camera
    vec3 ro;
    if (length(iMouse.xy) == 0.) {
        ro = smoothstep(0., 0.9, vec3(vec2(1) - iTime / 4., 1));
    } else {
        ro = vec3(-(2. * iMouse.xy - iResolution.xy) / iResolution.y, 1);
    }
    vec3 lookat = vec3(cos(iTime) / 10., sin(iTime) / 10., 0);
    vec3 vup = vec3(0, 1, 0);
    float vfov = 50.;

    float theta = radians(vfov);
    float h = tan(theta / 2.);
    float viewport_height = 2.0 * h;
    float viewport_width = aspect_ratio * viewport_height;

    vec3 w = normalize(ro - lookat);
    vec3 u = normalize(cross(vup, w));
    vec3 v = cross(w, u);

    vec3 horizontal = viewport_width * u;
    vec3 vertical = viewport_height * v;
    vec3 lower_left_corner = ro - horizontal / 2. - vertical / 2. - w;

    vec3 rd = normalize(lower_left_corner + uv.x * horizontal + uv.y * vertical - ro);

    float beat = expImpulse(mod(iTime + beatOffset, secondPerBeat), 14.);
    vec3 color = rayTrace(ro, rd, beat, uv);

    color = pow(color, vec3(1.0/2.2)); // gamma correction

    fragColor = vec4(color, 1.0);
}

float hash( float n )
{
    return fract(sin(n)*758.5453)*2.;
}

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x); 
    //f = f*f*(3.0-2.0*f);
    float n = p.x + p.y*57.0 + p.z*800.0;
    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x), mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
            mix(mix( hash(n+800.0), hash(n+801.0),f.x), mix( hash(n+857.0), hash(n+858.0),f.x),f.y),f.z);
    return res;
}

float fbm(vec3 p)
{
    float f = 0.0;
    f += 0.50000*noise( p ); p = p*2.02+0.15;
    f -= 0.25000*noise( p ); p = p*2.03+0.15;
    f += 0.12500*noise( p ); p = p*2.01+0.15;
    f += 0.06250*noise( p ); p = p*2.04+0.15;
    f -= 0.03125*noise( p );
    //return f/0.984375;
    return f;
}

float cloud(vec3 p)
{
    p-=fbm(vec3(p.x,p.y,0.0)*0.5)*0.7;

    float a =0.0;
    a-=fbm(p*3.0)*2.2-1.1;
    if (a<0.0) a=0.0;
    a=a*a;
    return a;
}

mat2 rot( float th ){ vec2 a = sin(vec2(1.5707963, 0) + th); return mat2(a, -a.y, a.x); }


vec3 hash33(vec3 p)
{
    const float UIF = (1.0/ float(0xffffffffU));
    const uvec3 UI3 = uvec3(1597334673U, 3812015801U, 2798796415U);
    uvec3 q = uvec3(ivec3(p)) * UI3;
	q = (q.x ^ q.y ^ q.z)*UI3;
	return vec3(q) * UIF;
}


// 3D Voronoi- (IQ)
float voronoi(vec3 p){

	vec3 b, r, g = floor(p);
	p = fract(p);
	float d = 1.; 
	for(int j = -1; j <= 1; j++)
    {
	    for(int i = -1; i <= 1; i++)
        {
		    b = vec3(i, j, -1);
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));
		    b.z = 0.0;
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));
		    b.z = 1.;
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));
	    }
	}
	return d;
}

// fbm layer
float noiseLayers(in vec3 p) {

    vec3 pp = vec3(0., 0., p.z + iTime*.09);
    float t = 0.;
    float s = 0.;
    float amp = 1.;
    for (int i = 0; i < 5; i++)
    {
        t += voronoi(p + pp) * amp;
        p *= 2.;
        pp *= 1.5;
        s += amp;
        amp *= .5;
    }
    return t/s;
}

vec3 n2 (vec2 fragCoord)
{
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
        float dd = length(uv*uv)*.025;

	vec3 rd = vec3(uv.x, uv.y, 1.0);

    float rip = 0.5+sin(length(uv)*20.0+iTime)*0.5;
    rip = pow(rip*.38,4.15);
    rd.z=1.0+rip*1.15;// apply a subtle ripple
    rd = normalize(rd);
    rd.xy *= rot(dd+iTime*.0125);
    rd*=2.0;

	float c = noiseLayers(rd*1.85);
    float oc = c;
    c = max(c + dot(hash33(rd)*2. - 1., vec3(.006)), 0.);
    c = pow(c*1.55,2.5);    
    vec3 col =  vec3(.55,0.85,.25);
    vec3 col2 =  vec3(1.4,1.4,1.4)*5.0;
    float pulse2 = voronoi(vec3((rd.xy*1.5),iTime*.255));
    float pulse = pow(oc*1.35,4.0);
    col = mix(col,col2,pulse*pulse2)*c;
    return col;

}
#define PI 3.14159


float vDrop(vec2 uv,float t)
{
uv.y *= 0.25;
    uv.x = uv.x*128.0;						// H-Count
    float dx = fract(uv.x);
    uv.x = floor(uv.x);
    uv.y *= 0.05;							// stretch
    float o=sin(uv.x*215.4);				// offset
    float s=cos(uv.x*33.1)*.3 +.7;			// speed
    float trail = mix(95.0,35.0,s);			// trail length
    float yv = fract(uv.y + t*s + o) * trail;
    yv = 1.0/yv;
    yv = smoothstep(0.0,1.0,yv*yv);
    yv = sin(yv*PI)*(s*5.0);
    float d2 = sin(dx*PI);
    return yv*(d2*d2);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;

	vec2 position = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
	float ss = sin(length(position*3.0)+time*0.1)*2.5;
	ss+=8.0;
   	vec2 coord = ss*position;
    coord*=rot(ss*0.04+time*0.037);
    coord*=0.25;
    coord+=fbm(sin(vec3(coord*8.0,time*0.001)))*0.05;
    coord+=time*0.0171;
    float q = cloud((vec3(coord*1.0,0.222)));
    coord+=time*0.0171;
    q += cloud((vec3(coord*0.6,0.722)));
    coord+=time*0.0171;
    q += cloud(vec3(coord*0.3,.722));
    coord+=time*0.1171;
    q += cloud((vec3(coord*0.1,0.722)));


	float vv1 = sin(time+ss+coord.x)*0.3;
	float vv2 = sin(time*0.9+ss+coord.y)*0.2;

    vec3 col = vec3(1.7-vv2,1.7,1.7+vv1) + vec3(q*vec3(0.7+vv1,0.5,0.3+vv2*1.15));
	col = pow(col,vec3(2.2))*0.08;

	float dd = length(col*.48)+vv1;

	float nn = 0.5+sin(ss*2.7+position.x*2.41+time*0.9)*0.5;

    vec3 col2 = n2(fragCoord)*0.9;

    vec2 p = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    float d = length(p);
	p = vec2(atan(p.x, p.y) / PI, 2.5 / d);
    float t = -time*0.014;
    float drop = vDrop(p,t);
    drop += vDrop(p,t+0.5);
    drop*=d;

        col2+=(col*.565);

	col = mix(col,col2,nn);
    col = mix(col,col*1.075,drop);

    col+=col*((d+dd)*0.28);
    col *= d;


    fragColor = vec4( col, 1.0 );
}

#define PI 3.1415926535897932384626433832795
#define half_PI 1.570796326794896619231321692

float max_dist = length(vec2(.5));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord - .5*iResolution.xy) / min(iResolution.x, iResolution.y);
    float l = length(uv) / max_dist;

    float a = (1.0 + atan(uv.y, uv.x) / PI) / 2.0;

    //a = fract(a + cos(l) / 5.0 + iTime / 60.0);
    a = fract(a + l / 1.0 - iTime / 30.0);

    float val;
    //val = smoothstep(0.0, 1.0, abs(2.*fract(a * 5.0)-1.));
    //val = smoothstep(1., abs(2.*fract(a * 5.0)-1.), l * 1.5);
    val = smoothstep(1.0, sin(a * PI * 10.0) / PI, l * 1.4);

    //fragColor = vec4(vec3(val), 1.0);

    float val1 = smoothstep(1.0, sin(a * PI * 10.0 + 1.1) / PI, l * 1.5);
    float val2 = smoothstep(1.0, sin(a * PI * 10.0 + 2.2) / PI, l * 1.5);

    fragColor = vec4(val, val1, val2, 1.0);
}

#define MIN_DIST 0.
#define MAX_DIST 100.
#define MAX_STEPS 100
#define EPSILON 0.0001


// UTILITIES
float norm1(vec3 p){return abs(p.x)+abs(p.y)+abs(p.z);}
float norminf(vec3 p) {return max(abs(p.x),max(abs(p.y),abs(p.z)));}
float random(vec3 p){
    return fract(sin(dot(p,vec3(24.5825,92.4585,134.4876))+27.66+iTime*.0001));
}

float intersectSDF(float distA, float distB) {
    return max(distA, distB);
}

float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}

mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

//PRIMITIVES
float cubeSDF(vec3 p,vec3 center, float size) {
    return norminf(p-center) -size;
}
float diamondSDF(vec3 p) {
    return norm1(p) - 1.;
}
float sphereSDF(vec3 p,vec3 center,float size) {
    return length(p-center)-size;
}
float torusSDF(vec3 p, vec3 center,float radius, float width){
    vec2 q = vec2(length((p-center).xz)-radius,(p-center).y);
    return length(q)-width;
}


// SCENE AND TRACING
float SDF(vec3 p) {
    float t =iTime;
    float SDF = sphereSDF(p,vec3(0.),.5);
    SDF = unionSDF(SDF,sphereSDF(p,1.5*vec3(cos(t),0.,sin(t)),.3));
    SDF = unionSDF(SDF,differenceSDF(torusSDF(p,vec3(0.),1.5,.0075),sphereSDF(p,1.5*vec3(cos(t),0.,sin(t)),.5)));
    return SDF;
}

float trace(vec3 cam,vec3 dir,float start,float end) {
    float depth = start;
    for (int i = 0; i < MAX_STEPS; i++) {
        float dist = SDF(cam + depth*dir);
        if (dist < EPSILON){
            return depth;
        }
        depth += dist;
        if (dist >= end){
            return end;
        }
    }
    return end;
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        SDF(vec3(p.x + EPSILON, p.y, p.z)) - SDF(vec3(p.x - EPSILON, p.y, p.z)),
        SDF(vec3(p.x, p.y + EPSILON, p.z)) - SDF(vec3(p.x, p.y - EPSILON, p.z)),
        SDF(vec3(p.x, p.y, p.z  + EPSILON)) - SDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

//CEL SHADING 
vec3 cel_single(vec3 colorMat,vec3 colorSpec,float alphaSpec,float slices, vec3 p, vec3 cam, vec3 lightPos, vec3 lightInt){
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos-p);
    vec3 V = normalize(cam - p);
    vec3 R = normalize(reflect(-L, N));

    if (dot(L,N)<0.) {
        return vec3(0.0);
    } else if (dot(R,V)<0.) {
        return lightInt*colorMat*dot(L,N);
    } else {
        return lightInt*(colorMat*dot(L,N) + colorSpec*pow(dot(R,V),alphaSpec));
    }
}
vec3 cel_scene(vec3 colorAmb,vec3 colorMat,vec3 colorSpec,float alphaSpec, float slices, vec3 p, vec3 cam, float t) {
    vec3 color = colorAmb*colorMat;
    vec3 illu = vec3(0.);

    t = t*.5 + .5;
    vec3 light2pos = 6.*vec3(cos(t),.3,sin(t));
    vec3 light2int = vec3(0.525,0.525,0.525);
    illu = max(illu,cel_single(colorMat,colorSpec,alphaSpec,slices,p,cam,light2pos,light2int));

    //color += sqrt(illu*floor(slices*norminf(illu))/slices);
    color += floor(slices*norminf(illu))/slices;

    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.xy;
    st = st*2.-1.;
    st.x *= iResolution.x/iResolution.y;

    float bitCrushFactor = 90.;
    st = floor(st*bitCrushFactor)/bitCrushFactor;

    float t = iTime;



    //ray casting
    vec3 cam = vec3(2.,2.,-2.);
    vec3 dir = normalize(vec3(st,-2.));
    mat4 view = viewMatrix(cam,vec3(0.),vec3(0.,1.,0.));
    dir = (view*vec4(dir,0.)).xyz;

    float dist = trace(cam,dir,MIN_DIST,MAX_DIST);
    vec3 p = cam+dist*dir;



    //background
    vec3 backgroundColor = vec3(0.123,0.145,0.104);
    vec3 starColor = vec3(0.765,0.765,0.367);
    vec2 ipos = floor(st*250.);

    float lit = smoothstep(.99996,.99999,random(vec3(ipos,.0)));
    backgroundColor = backgroundColor*(1.-lit)+starColor*lit ;



    //shading
    vec3 color;
    if (dist > MAX_DIST-EPSILON){
        color = backgroundColor;
    } else {
        vec3 colorAmb = vec3(0.225,0.225,0.225);
        vec3 colorMat = vec3(1.000,0.648,0.851);
        vec3 colorSpec = vec3(0.110,0.110,0.110);
        float alpha = 2.;

        color = cel_scene(colorAmb,colorMat,colorSpec,alpha,7.,p,cam,t);
    }

    fragColor = vec4(color,1.0);
}

