const float Nlen = 100.0;
const float Ns = 1.0;
const float Ts = 1.5;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 _ms = vec3(1.0,0.0,0.0);
    vec4 _ps = vec4(-10.0);
    vec2 rd0 = abs(fragCoord/iResolution.xy);
    vec2 rds = rd0;
    vec2 fc = fragCoord;
    float nlim = floor(iTime*Ns+abs(cos(iTime))*Ns);
    float nmin = (nlim-Nlen)*step(Nlen, nlim);

    for(float n = nmin; n < nlim; n++){
        float t=(iTime-n/Ns);
        t = (t-0.9*abs(sin(t/1.5-n)))*Ts;
        float s = 30.0+cos(n)*10.0;
        float r = min(iResolution.x,iResolution.y)/s;
        vec2 p0 = vec2( cos(n*cos(n)) , cos(n*sin(n)) );
        vec2 pt = vec2( t*0.0005*cos(n/7.0) , t*0.005*abs(cos(n)*5.0+pow(100.0,cos(n))) );
        vec2 p = p0+pt;
        vec2 rdc = fc * 2.0 - iResolution.xy + p*iResolution.xy;
        float rl = r + sin(length(fc)/r*7.0)*r/(10.0+cos(n)*2.0);
        float m1 = rl - length(rdc);

        vec2 rl0 = vec2(r,pt.y*iResolution.y*(1.0-smoothstep(0.0,100.0,t)));
        float m2 = rl/1.1 - length(vec2(rdc.x,rdc.y-rl/10.0));
        float rr = (r+_ms.z)/min(iResolution.x,iResolution.y)*0.6;
        float py = _ps.y+(_ms.y-t)*(_ps.w-_ps.y)/_ms.y;
        bool fx = (_ps.x < p0.x+rr && p.x-rr < _ps.z)||(p0.x-rr < _ps.x && _ps.z < p.x+rr);
        bool fy = (py < p0.y+rr && p.y-rr < _ps.w)||(p0.y-rr < py && _ps.w < p.y+rr);
        if(fx&&fy){
            rds = rd0;
            _ms.x = 1.0;
        }

        vec2 rs =rd0*2.0-1.0;
        if (step(length(rdc/rl0),1.0)*step(0.0,rdc.y)==1.0){
            if (step(0.0,m1)==0.0){
                rds.xy=vec2((-(rs.x+p.x)-p.x+1.0)/1.8,rds.y);
            }
        }
        if (step(0.0,m1)==1.0){
            rds = abs(1.0-((rs+p)*length(rdc)/rl*s*1.5+vec2(p.x,0.0)+1.0)/2.0);
            _ms = vec3(m2,t,r);
            _ps = vec4(p0,p);
        }
    }

    vec3 bg = vec3(3.0,4.0,10.0)*(rds.y/3.5+rds.x*rds.x/20.0)+0.15;
    vec3 bg0 = vec3(3.0,4.0,10.0)*(rd0.y/3.5+rd0.x*rd0.x/20.0)+0.1;
    vec4 Color = vec4(bg,1.0)-step(_ms.x,0.0);
    fragColor =Color*0.35+vec4(bg0,1.0)*0.65;
}

