// https://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float udRoundBox( vec3 p, vec3 b, vec3 r )
{
  return length(max(abs(p)-b,0.0))-r.x;
}

// https://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

// https://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCylinder( vec3 p, vec2 h )
{
  return max( length(p.xz)-h.x, abs(p.y)-h.y );
}

// http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float opRepLim( in float p, in float s, in float mi, in float ma )
{
    return p-s*clamp(round(p/s),mi,ma);
}

vec3 hash3( float n )
{
    return fract(sin(vec3(n,n+1.0,n+2.0))*vec3(13.5453123,31.1459123,37.3490423));
}

//----------------------------

float obj1( in vec3 p )
{
    vec3 q = vec3( opRepLim(p.x+1.1,0.2,-122.0,123.0), p.yz-0.1 );
	return udRoundBox( q, vec3(0.091,0.06*sin(10.0*(floor(5.0*p.x))+(iTime*10.0))+0.075,0.6)-0.005, vec3(0.01) );
}

float obj2( in vec3 p, in float d )
{
    vec3 q = vec3( opRepLim(p.x,0.2,-121.0,123.0), p.y-0.185, p.z - 0.3 );
	float k = mod( round( p.x/0.2 ), 7.0 );

	if( k==2.0 || k==6.0 ) return d;

	return udRoundBox( q, vec3(0.06,0.075,0.4)-0.01, vec3(0.01,0.01,0.01) );
}

float obj3( in vec3 p )
{
	float d1 = udRoundBox( p - vec3(0.0, 0.0,1.7), vec3(115.4,0.6,1.0), vec3(0.05) );
	float d2 = udRoundBox( p - vec3(0.0,-0.3,0.1), vec3(115.4,0.3,0.6), vec3(0.05) );
	float d3 = udRoundBox( p - vec3(0.0,-1.0,2.5), vec3(115.4,3.0,1.0), vec3(0.05) );
	float d4 = sdCylinder( vec3(abs(p.x),p.y,p.z) - vec3(115.25,-2.2,-0.35), vec2(0.1,2.0) );
    d4 -= 0.03*smoothstep(-0.7,0.7,sin(18.0*p.y)) + 0.017*p.y + 0.025;

	return min( min( min( d1, d2 ), d3 ), d4 );
}

float obj4( in vec3 p )
{
