vec2 cmul(vec2 a, vec2 b)
{
    return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 mouseUV = (2.*iMouse.xy-iResolution.xy)/iResolution.y;

    float ITER = 100.;

    if(iMouse.z > 0.)
    {
        // Magnification around the mouse position
        uv = mouseUV + 0.1*uv;
        ITER = 200.;
    }

    vec2 z = vec2(0);
    float zz;
    vec2 zmin = z;
    float zzmin = 1000.;
    float i;
    for(i=0.; i<ITER; i++)
    {
        // The loop to determine whether a point is inside or outside
        z = cmul(z, z) + uv;
        zz = dot(z,z);
        //zzmin = min(zz, zzmin);
        if(zz < zzmin)
        {
            zzmin = zz;
            zmin = z;
        }
        if(zz > 1000.) break;
    }

    vec3 col;
    if(i < ITER)
    {
        // We're outside!
        // Color outside according to the rate of exit
        // https://en.wikipedia.org/wiki/Plotting_algorithms_for_the_Mandelbrot_set#Continuous_(smooth)_coloring
        float phi = log2(zz)/exp2(i);
        col = 0.5 + 0.5*cos(log2(phi) + vec3(0,1,2) - iTime);
        col *= smoothstep(0.8,0.,phi);
    }
    else
    {
        //col = 0.5 + 0.5*cos(0.5*round(iTime+6.*z.xyx)+vec3(0,1,2));
        //col = 0.5 + 0.5*cos(iTime+3.*z.xyx+vec3(0,1,2));

        // Color inside according to smallest absolute value of the orbit
        col = 0.5 + 0.5*cos(iTime+log(zzmin) + vec3(0,1,2));
        //col = (0.5 + 0.5*cos(iTime+log(zzmin))) * vec3(0.1,0.5,1.);

        // Color according to the point whose orbit goes nearest the origin
        vec3 col2 = 0.5 + 0.5*cos(iTime+zmin.yyy/zzmin+vec3(0,1,2));
        //col += (0.5 + 0.5*cos(iTime+5.*zmin.yyy)) * vec3(1., 0.5, 0.1);

        float limit = 0.1 + 0.09*cos(0.618*iTime + uv.x);
        col = mix(col, col2, smoothstep(0.0,limit,zzmin));

        //col = sqrt(1.-exp(-col));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Experimenting with tone mapping. I'm not aiming for realism, I want
// cartoonishly extravagant colors. In particular, I want red to fade to orange
// or yellow before fading to white, whereas most tone mappers just fade from
// red to white. Similarly, green should fade through cyan, and blue should fade
// through magenta.

// The function commented as "Old HDR function" did this, but had patches where
// the color didn't change. This produced ugly results in the final image. It's
// easy to do smooth tone mapping, and it's easy to bleed one channel into
// another in the asymmetric way I want, but it turns out to be really hard to
// do both of those things at the same time.

// After a bunch of failed experiments I settled on the hdr function it's
// running now. This is a complicated piecewise function that essentially has
// six hard coded gradients, for different hues, and lerps between them (plus a
// bunch of corrections and hacks).


vec3 mid3_(vec3 v) { return v.y < v.z ? vec3(0, v.y, 0) : v.x < v.z ? vec3(0, 0, v.z) : vec3(v.x, 0, 0); }
vec3 mid3(vec3 v) { return v.x < v.y ? mid3_(v) : mid3_(v.yxz).yxz; }
vec3 max3v(vec3 v) { return v.x < v.y ? (v.y < v.z ? vec3(0, 0, v.z) : vec3(0, v.y, 0)) : (v.x < v.z ? vec3(0, 0, v.z) : vec3(v.x, 0, 0)); }
float max3(vec3 v) { return max(max(v.x, v.y), v.z); }
vec3 lerp(vec3 a, vec3 b, float t) { return (1. - t) * a + t * b; }
vec3 qerp(vec3 a, vec3 b, float t) { return sqrt(1. - t * t) * a + t * b; }

float tri(float x) {
  x = mod(2. * x, 2.);
  return x < 1. ? 3. * x - 1. : 5. - 3. * x;
}

vec3 hue(float h) {
  return clamp(vec3(tri(h+0.5), tri(h+1./6.), tri(h-1./6.)), 0., 1.);
}

vec3 baseColor(vec2 uv) {
  return 3. * uv.y * lerp(hue(uv.x), vec3(1), 0.5 + 0.5 * sin(iTime));
}

vec3 rgb2hsv(vec3 c) {
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

// Uchimura is copied from https://www.shadertoy.com/view/llXyWr
float Tonemap_Uchimura(float x, float P, float a, float m, float l, float c, float b) {
    // Uchimura 2017, "HDR theory and practice"
    // Math: https://www.desmos.com/calculator/gslcdxvipg
    // Source: https://www.slideshare.net/nikuque/hdr-theory-and-practicce-jp
    float l0 = ((P - m) * l) / a;
    float L0 = m - m / a;
    float L1 = m + (1.0 - m) / a;
    float S0 = m + l0;
    float S1 = m + a * l0;
    float C2 = (a * P) / (P - S1);
    float CP = -C2 / P;

    float w0 = 1.0 - smoothstep(0.0, m, x);
    float w2 = step(m + l0, x);
    float w1 = 1.0 - w0 - w2;

    float T = m * pow(x / m, c) + b;
    float S = P - (P - S1) * exp(CP * (x - S0));
    float L = m + a * (x - m);

    return T * w0 + L * w1 + S * w2;
}

float Tonemap_Uchimura(float x) {
    const float P = 1.0;  // max display brightness
    const float a = 1.0;  // contrast
    const float m = 0.22; // linear section start
    const float l = 0.4;  // linear section length
    const float c = 1.33; // black
    const float b = 0.0;  // pedestal
    return Tonemap_Uchimura(x, P, a, m, l, c, b);
}

vec3 Tonemap_Uchimura_RGB(vec3 v) {
  return vec3(Tonemap_Uchimura(v.r), Tonemap_Uchimura(v.g), Tonemap_Uchimura(v.b));
}


float uchi(float x) {
  float w0 = 1.0 - smoothstep(0.0, 0.22, x);
  float w2 = step(0.532, x);
  float S = 1. - 0.468 * exp((0.532 - x) / 0.468);
  return 0.22 * pow(x / 0.22, 1.33) * w0 + S * w2 + x * (1.0 - w0 - w2);
}
vec3 uchi(vec3 v) { return vec3(uchi(v.r), uchi(v.g), uchi(v.b)); }


