// Nicest algorithm I've got so far. Super complicated piecewise approach.
float qq(float q, float p) { return pow(p, 4. * q * (q - 1.)); }
vec3 hdrr(vec3 c) {
  float l = min(c.g, c.b);
  float m = (c.r - l);
  if (m < 1e-6) return c;
  float k = (max(c.g, c.b) - l) / m;
  vec3 d = c + (1. - k) * vec3(0, 1.1 * max(c.r - 0.7, 0.), max(c.r - 1.2, 0.));
  if (c.g > c.b) {
    d.b += k * max(c.g - 0.8, 0.);
  } else {
    d.g += k * max(c.b - 0.8, 0.);
  }
  float j = 0.5 + 0.5 * (c.g > c.b ? k : -k);
  float g = clamp(log(m / l), 0., 1.);
  const float J = 0.38;
  if (j < J) {
    d.g *= qq(j / J, 1. - g * 0.2);
    d.b /= qq(j / J, 1. - g * 0.2);
  } else {
    d.b *= qq((j - J) / (1. - J), 1. - g * 0.4);
    d.g /= qq((j - J) / (1. - J), 1. - g * 0.2);
  }
  if (m < 0.3) {
    return lerp(c, d, m / 0.3);
  }
  return d;
}

vec3 hdr(vec3 c) {
  if (c.r > c.g && c.r > c.b) {
    c = hdrr(c);
  } else if (c.g > c.b && c.g > c.r) {
    c = hdrr(c.gbr).brg;
  } else if (c.b > c.r && c.b > c.g) {
    c = hdrr(c.brg).gbr;
  }
  return uchi(c) * 1.05;
}

