void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(1.0);
    float a = 0.5+0.5*cos(iTime +uv.x+uv.y+iMouse.x*0.01);
    float b = 0.5+0.5*sin(iTime +uv.x+uv.y+iMouse.x*0.01);
    if(uv.x>a && uv.y>a)
    {
        // Time varying pixel color
        col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    }
    else if(uv.x>a && uv.y<a)
    {
        col = 0.5 + 0.5*sin(iTime+uv.xyx+vec3(0,2,4));
    }
    else if(uv.x<a && uv.y<a)
    {
        col = 0.5 + 0.5*sin(iTime+uv.xyx+vec3(0,2,6));
    }
    else
    {
        col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,6));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

