          float fr = pow(1.+dot(n,rd),8.); // Frenel
          col = vec3(.01)*diff+ vec3(1.)*spc+fr*vec3(.0,1.,.0)*.5; //Color composition

          break; 
          } else if(d.y == 2.){ // Texture 2 : The terrain outline
          // Outline stuff is basically computing the normal on the same point but at different
          // "scale / resolution / epsilon value" and taking the different / lenght of it
          // -> If you are on a surface that doesn't change that much (plane of sphere) length will be 0
          // -> If you are close to and edge (corner , breaking in the shape), the length > 0
              float edge = length(n-norm(rp,.001*length(ro-rp))); 
            col  = vec3(.0,1.,.0)*edge;

              break;
          } else if(d.y== 3.){  // texture 3: Boring texture for shuriken
  float diff=  max(0.,dot(normalize(light-rp),n));            
            col = diff * vec3(1.);

                break;
            } else if(d.y == 4.){ // Texture 4, the generative texture applied to shuriken
               float diff=  max(0.,dot(normalize(light-rp),n));
                col = diff*vec3(2.)*txt(oop.xz*.1,1.);
                break;
            }
       }

       rp+=rd*d.x*.9; // Poor trick to avoid glitch (as you can see not fixing everything)

   }
  col +=acc; // Don't forget the glow

    // YAY
    fragColor = vec4(sqrt(col),1.0);
}

#define TWO_PI 6.2832

bool ALIASED = false;

float MOON_SIZE = 0.005;
float BREATHING_ROOM = 0.001;
float R_STEP;

float ANTI_ALIAS_RADIUS;
float ARC_ANTI_ALIAS_RADIUS;

float DISPLACEMENT_FACTOR;

// -------------------------

vec2 hash( vec2 x )  
{
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

float fbm( in vec2 uv )
{
 	uv *= 2.;
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
	float f = 0.;
    f  = abs(sin(0.5000*noise( uv ))); uv = m*uv;
	f += abs(sin(0.2500*noise( uv ))); uv = m*uv;
	f += abs(sin(0.1250*noise( uv ))); uv = m*uv;
	f += abs(sin(0.0625*noise( uv ))); uv = m*uv;
	//f  = 0.5 + 0.5*f;
    return f;
}


// ------------------

vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

// ------------------

void setup() {
    R_STEP = (MOON_SIZE + BREATHING_ROOM)*2.;

    float rel_moon_size = 0.02 / MOON_SIZE;

    ARC_ANTI_ALIAS_RADIUS = (0.2 * rel_moon_size);
    ANTI_ALIAS_RADIUS = 1. - ARC_ANTI_ALIAS_RADIUS;

    ANTI_ALIAS_RADIUS *= MOON_SIZE;

    DISPLACEMENT_FACTOR = 0.01 / pow(rel_moon_size, 0.5);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    setup();

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
     uv -= vec2(0.5, 0.5);
    uv.x *= iResolution.x / iResolution.y;
    uv.y -= 0.2;
    uv *= 10.;



    float new_theta = atan(uv.y, uv.x) + TWO_PI*float(uv.y<0.);
    float new_r = length(uv)/ R_STEP + 0.5;

    new_r += fbm(uv)*pow(new_r+1000.,1.5)*0.0005;
    new_theta += fbm(uv)*pow(new_r+1000.,1.6)*0.0001;


    float phase = new_theta*2./TWO_PI + pow(new_r,0.75)*0.1 - iTime * 0.25;
    phase = mod(phase, 2.);



    //phase += *0.5;

    //phase *= (1. + fbm(uv)*pow((new_r-1.),0.2)*5.);

    phase = 2. - smoothstep(-1., 1., (phase - 1.))*2.;



    //phase = mix(2. - smoothstep(0.,1.,smoothstep(-1., 1., (phase - 1.)))*2., 2. - smoothstep(-1., 1., (phase - 1.))*2., min(10000.*pow(new_r + 90.,-2.),1.));

    vec3 col = vec3(1.0);

    //float brightness = max((1. - min(phase, 2. - phase))*2.  + (1.-abs(1.-phase))/length(uv)* 0.3, 0.01);

    float brightness = smoothstep(0., 1., smoothstep(0., 1., smoothstep(0., 1., smoothstep(0., 1., abs(phase - 1.)))))*1.5;
    brightness = max(brightness, 1.5*min(1., pow(new_r*0.015, -2.)));


    vec3 colorHSL = vec3(phase * .5 + 0.3, 0.6, .76);
    vec3 colorRGB = hsl2rgb(colorHSL);

    col = mix(col, colorRGB, brightness);

    fragColor = vec4(col, 1.0);

}

