void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = 3.*( 2.*u - R ) / R.y, a,b;

    float s = .33+.67*exp2( mod(iTime,2.) ), // original: 1. + mod(iTime,2.);
          m =  99., l;

    int i,z,p = T.length(), n = int(26.5*(s-1.)+1.);
    for ( z=0; z < 3; s/=3., z++ ) {                    // fractal levels
        if (z==2) p = n;                                // full L0,L1, evol L2
        L( U, T, p, s);                                 // draw trajectory tile
    }
    l = m; s*=27.;

    for ( z=0; z < 6; s/=3., z++ )                      // fractal levels
        L( U, L, L.length(), s );                       // draw maze tile

    O = vec4(1);                                        // paint
    O -= m==l ? S( sqrt(m) - 20./R.y ) * vec4(0,1,1,0)
              : vec4( S( sqrt(m)));
}

const float TAU = 6.28318530718;

const vec3 color1 = vec3(0.035, 0.011, 0.149);
const vec3 color2 = vec3(0.141, 0.019, 0.137);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = mix(color1, color2, uv.x 
        + sin(uv.y * TAU + iTime) * 0.1
        + sin(uv.y * TAU * 1.734 - iTime * 1.743) * 0.1
        + sin(uv.y * TAU * 0.823 + iTime * 1.324) * 0.1
    );

    // Output to screen
    fragColor = vec4(col,1.0);
}

