#define AA  0   // optional antialiasing

float PrBoxDf (vec3 p, vec3 b);
float PrBox2Df (vec2 p, vec2 b);
float PrCylDf (vec3 p, float r, float h);
float Minv2 (vec2 p);
float Maxv2 (vec2 p);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float Fbm1 (float p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 bSize, sunDir, vnBlk;
vec2 blbRad;
float tCur, dstFar, pcUp;
int idObj;
bool isSh;
const float pi = 3.1415927;

#define VAR_ZERO min (iFrame, 0)

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

vec3 SMap (vec3 p, float t)
{
  float f;
  f = 2.;
  for (int k = 0; k < 5; k ++) {
    p += 0.4 * sin (1.7 * p.yzx / f + f * t);
    f *= 0.8;
  }
  return p;
}

float BlobDf (vec3 p)
{
  vec3 q;
  float d, t;
  t = tCur + 1.31;
  q = p;
  q.xz = Rot2D (q.xz, 0.2 * t);
  d = SmoothMin (PrBoxDf (SMap (q - vec3 (0.7, 0., 0.), t + 2.), vec3 (blbRad.x)),
     PrBoxDf (SMap (q - vec3 (-0.7, 0., 0.), 1.3 * t), vec3 (blbRad.y)), 0.2);
  return d * (isSh ? 1. : 0.25);
}

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 cs, b, s;
  float dMin, d, g, w;
  dMin = dstFar;
  cs = sin (pi / 16. + vec2 (0., 0.5 * pi));
  w = 0.06;
  p.y -= pcUp;
  q = p;
  q.y -= 7.5;
  g = dot (vec2 (abs (q.x), q.y), cs);
  d = min (max (min (abs (g) - w, 0.), - q.y - 0.7),
     max (min (abs (q.y + 0.725) - w, 0.), g - w));
  q.y -= -0.25;
  g = dot (vec2 (abs (q.x), q.y), cs);
  b = vec2 (4., 3);
  q = p;
  q.y -= 3.5;
  s = abs (q.xy) - b;
  d = min (d, min (max  (min (s.y - w, 0.), min (abs (s.x) - w, 0.)),
     max  (min (s.x - w, 0.), min (abs (s.y) - w, 0.))));
  q = p;
  q.yz -= vec2 (4., bSize.z);
  d = max (PrBoxDf (q - vec3 (0., 0., -0.2), bSize), - d);
  q.y -= -0.5;
  d = max (d, - PrBox2Df (q.xy, b - 3. * w));
  q.y -= -2.7;
  d = max (d, - PrCylDf (q.xzy, 3., 0.3));
  DMIN (1);
  q = p;
  q.y -= 4.;
  d = PrBoxDf (q, vec3 (bSize.xy, 0.1));
  q.y -= -0.5;
  d = max (d, - PrBox2Df (q.xy, b - 3. * w + 0.05));
  DMIN (2);
  q = p;
  q.y -= 3.5;
  g = abs (q.x) - b.x;
  q.xz = vec2 (mod (q.x + 0.75, 1.5) - 0.75, abs (q.z - bSize.z) - bSize.z + 0.3);
  d = PrCylDf (q.xzy, 0.2, b.y - 4. * w);
  q.y = abs (q.y) - b.y + 4. * w;
  d = max (min (d, PrCylDf (q.xzy, 0.275, w)), g);
  DMIN (3);  
  q = p;
  q.yz -= vec2 (3., bSize.z);
  if (! isSh) d = PrBoxDf (q, vec3 (b - 3. * w, bSize.z)) + 0.1;
  if (isSh || d < dMin) {
    d = BlobDf (q);
    DMIN (4);
    q.y -= -2.75;
    d = PrCylDf (q.xzy, 3., 0.3);
    DMIN (5);
  } else dMin = min (dMin, d);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 180; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec4 ObjNfL (vec3 p)
{
  vec4 v;
  vec2 e;
  float s;
  e = vec2 (0.01, -0.01);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  s = dot (v, vec4 (1.));
  return vec4 (normalize (2. * v.yzw - s), (s - 2. * v.x - 4. * ObjDf (p)) / (e.x * e.x));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 40; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

float ExObjDf (vec3 p)
{
  vec3 q;
  float d;
  p.y -= bSize.y + pcUp;
  p.z -= -0.1;
  q = p;
  d = PrBoxDf (q, vec3 (bSize.xy, 0.1));
  q.xy -= bSize.xy * vec2 (-0.8, 0.6);
  d = max (d, - PrBox2Df (q.xy, vec2 (1.)));
  return d;
}

float ExObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 40; j ++) {
    h = ExObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

float BlkHit (vec3 ro, vec3 rd, vec3 sz)
{
  vec3 v, tm, tp;
  float dMin, dn, df;
  dMin = dstFar;
  v = ro / rd;
  tp = sz / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    dMin = dn;
    vnBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  }
  return dMin;
}

float RippleHt (vec2 p)
{
  vec2 q;
  float s1, s2;
  q = Rot2D (p, -0.02 * pi) * vec2 (0.5, 1.);
  s1 = abs (sin (4. * pi * abs (q.y + 1.5 * Fbm2 (0.7 * q))));
  s1 = (1. - s1) * (s1 + sqrt (1. - s1 * s1));
  q = Rot2D (p, 0.01 * pi) * vec2 (0.5, 1.);
  s2 = abs (sin (3.1 * pi * abs (q.y + 1.9 * Fbm2 (0.5 * q))));
  s2 = (1. - s2) * (s2 + sqrt (1. - s2 * s2));
  return 1.2 * mix (s1, s2, 0.1 + 0.8 * smoothstep (0.3, 0.7, Fbm2 (2. * p)));
}

vec3 RippleNorm (vec2 p, vec3 vn, float f)
{
  vec2 e;
  float h;
  e = vec2 (0.002, 0.);
  h = RippleHt (p);
  vn.xy = Rot2D (vn.xy, f * (RippleHt (p + e) - h));
  vn.zy = Rot2D (vn.zy, f * (RippleHt (p + e.yx) - h));
  return vn;
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol;
  vec2 q;
  float f, fd, ff;
  if (rd.y < 0.03 * Fbm1 (16. * atan (rd.z, - rd.x))) {
    col = vec3 (0.3, 0.4, 0.5);
  } else {
    q = 0.02 * (ro.xz + 0.5 * tCur + ((100. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    col = mix (vec3 (0.4, 0.5, 0.8), clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  }
  return col * vec3 (1., 0.8, 0.8);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, vn4;
  vec3 col, vn, rob, roo;
  vec2 vf, s;
  float dstObj, dstGrnd, db, sh, f, y, sb;
  bool isBg;
  isBg = true;
  sh = 1.;
  roo = ro;
  db = BlkHit (ro + vec3 (0., - bSize.y - pcUp, 0.1), rd, vec3 (bSize.xy, 0.05));
  if (db < dstFar) {
    rob = ro + db * rd;
    vf = vec2 (0.);
    sb = Maxv2 (abs (vec2 (rob.x, rob.y - bSize.y - pcUp)) - bSize.xy);
    if (rob.z < -0.1 && sb < -0.2) {
      blbRad = vec2 (1.1 + 0.31 * sin (tCur + 1.31), 1. + 0.41 * sin (1.7 * (tCur + 1.31)));
      isSh = false;
      dstObj = ObjRay (ro, rd);
      if (dstObj < dstFar && idObj == 5) {
        ro += dstObj * rd;
        rd = reflect (rd, vec3 (0., 1., 0.));
        ro += 0.01 * rd;
        dstObj = ObjRay (ro, rd);
      }
      if (dstObj < dstFar) {
        ro += dstObj * rd;
        if (idObj != 4) vn = ObjNf (ro);
        else {
          vn4 = ObjNfL (ro);
          vn = vn4.xyz;
        }
        y = ro.y - pcUp;
        if (idObj == 1) {
          if (vn.y > 0.99 && length (ro.xz - vec2 (0., bSize.z)) < 3.) {
            col4 = vec4 (0.95, 0.95, 1., 0.3);
          } else {
            col4 = vec4 (0.6, 0.4, 0.3, 0.05) * (0.7 + 0.3 * Fbm2 (vec2 (ro.x, y) * vec2 (1., 32.)));
            if (vn.z < -0.99) vf = vec2 (8., 1.);
            else vf = vec2 (16., 1.);
          }
        } else if (idObj == 2) {
          col4 = vec4 (0.5, 0.4, 0.3, 0.1);
        } else if (idObj == 3) {
          col4 = vec4 (0.6, 0.4, 0.2, 0.2) * (1. -
             0.5 * smoothstep (0.45, 0.5, abs (mod (3. * y + 0.5, 1.) - 0.5)));
          s = vec2 (mod (ro.x + 0.75, 1.5) - 0.75, abs (ro.z - bSize.z) - bSize.z + 0.3);
          vn.xz = Rot2D (vn.xz, -0.15 * pi * sin (pi * (0.5 -
             mod (12. * (atan (s.x, s.y) / (2. * pi) + 0.5), 1.))));
        } else if (idObj == 4) {
          col4 = vec4 (vec3 (0.95, 0.95, 1.) * (1. - step (8., vn4.w)), 0.3);
        }
        if (vf.x > 0.) vn = VaryNf (vf.x * vec3 (ro.x, y, ro.z), vn, vf.y);
        isSh = true;
        isBg = false;
      } else if (rd.y < 0.) {
        dstGrnd = - ro.y / rd.y;
        ro += dstGrnd * rd;
        isSh = true;
      }
      sh = isSh ? ObjSShadow (ro + 0.01 * vn, sunDir) : 1.;
      if (! isBg) col = col4.rgb * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.)) +
         col4.a * step (0.95, sh) * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
      else if (isSh) ro -= dstGrnd * rd;
    } else {
      ro = rob;
      vn = vnBlk;
      col4 = vec4 (0.85, 0.85, 0.8, 0.1);
      vf = vec2 (64., 0.5);
      if (rob.z < -0.1) {
        if (sb < -0.2) col4 *= 0.5;
        isBg = false;
      } else {
        vn.z *= -1.;
        f = 0.;
        rob.y -= bSize.y + pcUp;
        if (abs (rob.y) < bSize.y - 0.5) f = abs (rob.x);
        if (abs (rob.x + 0.5 * bSize.x) < 0.5 * bSize.x - 1.) 
           f = abs (abs (rob.y + 0.3 * bSize.y) - 0.5);
        s = abs (vec2 (rob.xy) - bSize.xy * vec2 (-0.8, 0.6)) - 0.8;
        if (Maxv2 (s) < 0.02) f = Minv2 (abs (s));
        if (f > 0.) col4 *= 0.5 + 0.5 * smoothstep (0.02, 0.04, f);
        isBg = false;
        if (max (s.x, s.y) < -0.02) {
          isBg = true;
          ro = roo;
        }
      }
      if (! isBg) {
        vn = VaryNf (vf.x * ro, vn, vf.y);
        col = col4.rgb * (0.2 + 0.8 * max (dot (vn, sunDir), 0.)) +
           col4.a * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
      }
    }
    col *= vec3 (1., 0.9, 0.9);
  }
  if (isBg) {
    ro = roo;
    if (rd.y < 0.) {
      dstGrnd = - ro.y / rd.y;
      ro += dstGrnd * rd;
      vn = vec3 (0., 1., 0.);
      f = 1. - smoothstep (0.3, 0.6, dstGrnd / dstFar);
      if (f > 0.) vn = VaryNf (4. * ro, RippleNorm (ro.xz, vn, 4. * f), f);
      if (sh == 1.) sh = ExObjSShadow (ro + 0.01 * vn, sunDir);
      col = mix (vec3 (1., 0.8, 0.5), vec3 (0.9, 0.7, 0.5), 0.2 +
         0.6 * smoothstep (0.7, 1.1, 2. * Fbm2 (2. * ro.xz)));
      col = col * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.));
      col = mix (col, vec3 (0.3, 0.4, 0.5), pow (1. + rd.y, 16.)) * vec3 (1., 0.8, 0.8);
    } else col = SkyBgCol (ro, rd);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.015 * pi;
  if (mPtr.z > 0.) {
    az -= 2. * pi * mPtr.x;
    el -= pi * mPtr.y;
  } else {
    az = 0.8 * pi * (2. * mod (floor (0.05 * tCur), 2.) - 1.) *
       SmoothBump (0.3, 0.7, 0.15, mod (0.05 * tCur, 1.));
  }
  el = clamp (el, -0.4 * pi, 0.02 * pi);
  vuMat = StdVuMat (el, az);
  bSize = vec3 (7., 4., 3.75);
  pcUp = 0.3 * bSize.y * SmoothBump (0.25, 0.75, 0.2, mod (0.1 * tCur, 1.));
  ro = vuMat * vec3 (0., 4.5, -27.);
  zmFac = 4.5;
  dstFar = 100.;
  sunDir = normalize (vec3 (0., 0., -1.));
  sunDir.xz = Rot2D (sunDir.xz, 0.2 * pi * sin (0.05 * 2. * pi * tCur));
  sunDir.yz = Rot2D (sunDir.yz, pi * (0.17 + 0.07 * sin (0.03 * 2. * pi * tCur)));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  }
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

#define PI 3.14159265359
#define viewAngle (PI*0.6)
#define distToScreen (0.5*max(iResolution.x, iResolution.y)/tan(0.5*viewAngle))
#define maxDist 70.0
#define maxStep 500
#define nEPS 0.0125

#define meanWaterLevel -0.5

#define SKY 0.0
#define WATER 1.0
#define LAND 2.0

// MODE 0 sticks the camera to the ground. Drag the mouse to explore.
// MODE 1 makes the camera fly above the terrain
#define MODE 1


//increase this constant to increase the amount of erosion
#define OCTAVES 5

float fbm(in vec2 uv)
{
    //this function generates the terrain height
    float value = 0.;
    float factor = 3.;
    uv *= factor;
    for (int i = 0; i < OCTAVES; i++)
    {
        //uv = sin(uv/factor)*factor;
        uv += min(sin(uv*factor)/factor,cos(uv/factor)*factor).yx;
        value += sin((uv.x-uv.y-value))/factor;

        //uv += sin(dot(sin(uv/factor),sin(uv.yx/factor)));
        uv= -uv/(factor);
    }

    return value;
}


struct MarchResult {
    float dist;
    vec3 pos;
    float type;
};

float getElevation(vec2 uv, float d) {
    float factor = 3.0;
    return fbm(uv/factor)*factor;
}

vec3 getNormal(vec3 p, float d) {
    return normalize(vec3(
        getElevation(vec2(p.x-nEPS,p.z), d) - getElevation(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getElevation(vec2(p.x,p.z-nEPS), d) - getElevation(vec2(p.x,p.z+nEPS), d)
    ));
}

float getWaterLevel(vec2 p, float d) {
    if (d<5.0) {
    	float t = iTime*1.0;
    	p*=7.0;
    	float w = 0.00025*smoothstep(0.0, 1.0, 0.5/(d+0.00001));
    	return w*(sin(p.y*7.37+t*2.0) + sin(p.x*2.37+t)) + meanWaterLevel;
    }
	else return meanWaterLevel;
}

vec3 getWaterNormal(vec3 p, float d) {
    return normalize(vec3(
        getWaterLevel(vec2(p.x-nEPS,p.z), d) - getWaterLevel(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getWaterLevel(vec2(p.x,p.z-nEPS), d) - getWaterLevel(vec2(p.x,p.z+nEPS), d)
    ));
}

vec3 rayToPixel(vec2 pixel) {
    pixel -= 0.5*iResolution.xy;
    return normalize(vec3(pixel.x, pixel.y, distToScreen));
}

float estDistToTrn(vec3 p, float d) {
    return (p.y - getElevation(p.xz, d))*(d*0.015+0.35);
}


// TODO generate procedural textures for rocks and grass on the fly
vec4 rock(vec3 p) {
    return texture(iChannel0, p.xz);
}

vec4 grass(vec3 p) {
    return mix(vec4(0.2, 0.4, 0.15, 1.0), texture(iChannel1, p.xz), 0.1);
}

vec4 snow(vec3 p) {
    return vec4(0.9, 0.9, 0.9, 1.0);
}

vec4 fog(vec3 ray, float d, vec3 sunDir, vec4 material) {
    float fogAmount = 1.0-exp(-d*0.035);
    float sunAmount = pow(max(dot(ray, sunDir), 0.0), 90.0);
    vec4 fogCol = mix(vec4(0.3, 0.7, 0.9, 1.0), vec4(1.0, 0.9, 0.7, 1.0), sunAmount);
    return mix(material, fogCol, fogAmount);
}

vec4 terrain(vec3 p, vec3 sunDir) {
    vec3 normal = getNormal(p, 0.0);
	vec3 abnormal = abs(normal);	    
	vec4 grassRock = mix(grass(p), rock(p), smoothstep(0.0, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 snowRock = mix(snow(p), rock(p), smoothstep(0.75, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 fragC = mix(grassRock, snowRock, smoothstep(0.5, 1.0, p.y));
   	fragC *= max(dot(sunDir, normal), 0.2);
    return fragC;
}

MarchResult march(vec3 p0, vec3 ray, bool withWater) {
    float type = SKY;
    float d = 0.0;
    int stp = 0;
    vec3 p = p0;
    while (type==SKY && d<(withWater?maxDist:maxDist*0.125) && (stp++<(withWater?maxStep:maxStep/3))) {
        p = p0 + d*ray;
        float waterLevel = withWater ? /*getWaterLevel(p.xz, d)*/ meanWaterLevel : -9999.9;
        float stpSize = estDistToTrn(p,d) * (withWater?1.0:2.0);
        // TODO fix this mess
        if (p.y<=waterLevel) {
            type = WATER;
            d = (waterLevel-p0.y)/ray.y;
            p = p0+d*ray;
        }
        else if (stpSize<d*0.001) type = LAND;
        else d+= stpSize;
    }
    d = min(d, maxDist);
    return MarchResult(d, p, type);
}


vec4 water(vec3 p, float d, vec3 ray, vec3 sunDir) {
    vec3 normal = getWaterNormal(p, d);
    vec3 ref = normalize(reflect(-sunDir, normal));
    vec4 wc = vec4(0.2,0.55,0.8,1.0);
    vec4 sc = vec4(0.9,0.9,0.7,1.0);
    wc *= max(0.35, dot(sunDir, normal));

    MarchResult uwr = march(p, normalize(reflect(ray, normal)), false);
    vec4 uwt = terrain(uwr.pos, sunDir);
    wc = mix(wc, uwt, uwr.type*0.25);

    return mix(wc, sc, 0.85*pow(max(dot(ref, -ray),0.0),8.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	

    float pitch = MODE==0 ? 0.0 : 0.2*sin(iTime*0.2);
    float yaw = 0.0;
    float roll = MODE==0 ? 0.0 : 0.1*sin(iTime*0.5);

    vec3 ray = rayToPixel(fragCoord);

    mat3 tr = mat3(
        cos(roll),  -sin(roll), 0.0,
        sin(roll), cos(roll), 0.0,
        0, 0, 1
    ) 
    * mat3(
        cos(yaw), 0.0, sin(yaw),
        0.0, 1.0, 0.0,
        -sin(yaw), 0.0, cos(yaw)
    )
    * mat3(
        1.0, 0.0, 0.0,
        0.0, cos(pitch), -sin(pitch),
        0.0, sin(pitch), cos(pitch)
    )
    ;
    ray *= tr;


    #if MODE
    vec3 p0 = vec3(17.25, 2.0, 1.0*iTime);
    #else
    vec3 p0 = vec3(60.0*iMouse.x/iResolution.x, -0.25, 60.0*iMouse.y/iResolution.y);
    #endif
    p0.y = max(getElevation(p0.xz,0.0), getWaterLevel(p0.xz,0.0)) + 1.;

    MarchResult res = march(p0, ray, true);
    vec3 sunDir = normalize(vec3(0.2, 0.1, 0.15));

    fragColor = vec4(1.0);
    if (res.dist<maxDist) {
        if (res.type==WATER) {
            fragColor = water(res.pos, res.dist, ray, sunDir);
        } else if (res.type==LAND) {
    		fragColor = terrain(res.pos, sunDir);
        }
    }

    fragColor = fog(ray, res.dist, sunDir, fragColor);
}

// Reuse some code to calculate ray direction from shader created by inigo quilez - iq/2019
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

const float PI = 3.14159265;

const vec3 lightDir = vec3(-0.577, 0.577, 0.577);
float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float smoothMin(float d1, float d2, float k){
    float h = exp(-k * d1) + exp(-k * d2);
    return -log(h) / k;
}

float sdSphere( in vec3 p, in float r )
{
    return length(p)-r;
}

float dFn(vec3 p){
    const float div = 7.;
    const float an = 2.*PI/div;
    float index = round(atan(p.y,p.x)/an);
    float pick = (index+1.)/div;
    vec4 spect = texture(iChannel0, vec2(pick,0.));
    float angrot = index*an;
    vec3 q = p;
    q.xy = mat2(cos(angrot),-sin(angrot),
                sin(angrot), cos(angrot))*q.xy;

    float r = 1.5;
    float lenxy = r*spect.x ;
    float d = sdSphere( abs(q) - vec3(lenxy, 0, .5*sqrt(pow(r,2.) - lenxy)), .2*lenxy );
    float d2 = sdSphere( p , 1. );
    return smoothMin(d, d2, 20.0);
}

vec3 norm(vec3 p){
    float d = 0.001;
    return normalize(vec3(
        dFn(p + vec3(  d, 0.0, 0.0)) - dFn(p + vec3( -d, 0.0, 0.0)),
        dFn(p + vec3(0.0,   d, 0.0)) - dFn(p + vec3(0.0,  -d, 0.0)),
        dFn(p + vec3(0.0, 0.0,   d)) - dFn(p + vec3(0.0, 0.0,  -d))
    ));
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

vec4 march(vec2 p){

    vec4 spect = texture(iChannel0, vec2(0,0.));
    spect = spect-.5;

    float rotate = spect.x*.5*PI + 1.5*iTime;
    vec3 target = vec3( 0.0, 0.0, 0.0 );
    float r = 5.;
    vec3 cPos = target + vec3( r*cos(rotate), 1.5*cos(.3*rotate), r*sin(rotate) );
    mat3 direction = setCamera( cPos, target, 0. );
    float fl = 2.0;
    vec3 ray = direction * normalize( vec3(p,fl) );

    float distance = 0.0;
    float rLen = 0.0;
    vec3  rPos = cPos;
    for(int i = 0; i < 64; i++){
        distance = dFn(rPos);
        rLen += distance;
        rPos = cPos + ray * rLen*.5;
    }

    vec4 col = vec4(0);
    if(abs(distance) < 0.001){
        vec3 normal = norm(rPos);
        float diff = clamp(dot(lightDir, normal), 0., 1.0);
        col = vec4(vec3(1.007*normal), 1.0);
    }else{
        col += vec4(0.5 + 0.5*cos(iTime+p.xyx+vec3(0,2,4)), 0);
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;
    fragColor = march(p);
}

// from Trisomie21's "Ball Of Fire" https://www.shadertoy.com/view/lsf3RH
// very slight strokes over

float snoise(vec3 uv, float res)
{
	vec3 s = vec3(1e0, 1e1, 1e2);
	uv *= res;
	vec3 uv0 = floor(mod(uv, res))*s;
	vec3 uv1 = floor(mod(uv+vec3(1.), res))*s;

	vec3 f = fract(uv); f = f*f*(3.0-2.0*f);

	vec4 v = vec4(uv0.x+uv0.y+uv0.z, uv1.x+uv0.y+uv0.z,
		      	  uv0.x+uv1.y+uv0.z, uv1.x+uv1.y+uv0.z);

	vec4 r = fract(sin(v*1e-1)*1e3);
	float r0 = mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y);

	r = fract(sin((v + uv1.z - uv0.z)*1e-1)*1e3);
	float r1 = mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y);

	return mix(r0, r1, f.z)*2.-1.7;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	vec2 p = -.5 + fragCoord.xy / iResolution.xy;
	float color =2.5 - (2.5*length(2.*p));

	vec3 coord = vec3(atan(p.x+.5,p.y)/6.3, length(p)*.4, .5);

	for(int i = 1; i <= 7; i++)
	{
		float power = pow(2.0, float(i));
		color += (1.5 / power) * snoise(coord + vec3(0.,-iTime*.01, iTime*.01), power*20.);
	}
	fragColor = vec4( color, pow(max(color,0.),2.)*0.4, pow(max(color,0.),3.)*0.15 , 1.0);
}

#define PI 3.14159265359
#define viewAngle (PI*0.6)
#define distToScreen (0.5*max(iResolution.x, iResolution.y)/tan(0.5*viewAngle))
#define maxDist 70.0
#define maxStep 500
#define nEPS 0.0125

#define meanWaterLevel -0.5

#define SKY 0.0
#define WATER 1.0
#define LAND 2.0

// MODE 0 sticks the camera to the ground. Drag the mouse to explore.
// MODE 1 makes the camera fly above the terrain
#define MODE 1


//increase this constant to increase the amount of detail
#define OCTAVES 2

float fbm(in vec2 uv)
{
    //this function generates the terrain height
    float value = 0.;
    float factor = 3.;
    uv *= factor;
    for (int i = 0; i < OCTAVES; i++)
    {
        uv += min(sin(uv*factor)/factor,cos(uv/factor)*factor).yx;
        value = -min(value+cos(uv.x-uv.y)/factor,-value+sin((uv.x-uv.y))/factor);
        uv /= factor;
    }

    return value+1.5;
}


struct MarchResult {
    float dist;
    vec3 pos;
    float type;
};

float getElevation(vec2 uv, float d) {
    float factor = 3.0;
    return fbm(uv/factor)*factor;
}

vec3 getNormal(vec3 p, float d) {
    return normalize(vec3(
        getElevation(vec2(p.x-nEPS,p.z), d) - getElevation(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getElevation(vec2(p.x,p.z-nEPS), d) - getElevation(vec2(p.x,p.z+nEPS), d)
    ));
}

float getWaterLevel(vec2 p, float d) {
    if (d<5.0) {
    	float t = iTime*1.0;
    	p*=7.0;
    	float w = 0.00025*smoothstep(0.0, 1.0, 0.5/(d+0.00001));
    	return w*(sin(p.y*7.37+t*2.0) + sin(p.x*2.37+t)) + meanWaterLevel;
    }
	else return meanWaterLevel;
}

vec3 getWaterNormal(vec3 p, float d) {
    return normalize(vec3(
        getWaterLevel(vec2(p.x-nEPS,p.z), d) - getWaterLevel(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getWaterLevel(vec2(p.x,p.z-nEPS), d) - getWaterLevel(vec2(p.x,p.z+nEPS), d)
    ));
}

vec3 rayToPixel(vec2 pixel) {
    pixel -= 0.5*iResolution.xy;
    return normalize(vec3(pixel.x, pixel.y, distToScreen));
}

float estDistToTrn(vec3 p, float d) {
    return (p.y - getElevation(p.xz, d))*(d*0.015+0.35);
}


// TODO generate procedural textures for rocks and grass on the fly
vec4 rock(vec3 p) {
    return texture(iChannel0, p.xz);
}

vec4 grass(vec3 p) {
    return mix(vec4(0.2, 0.4, 0.15, 1.0), texture(iChannel1, p.xz), 0.1);
}

vec4 snow(vec3 p) {
    return vec4(0.9, 0.9, 0.9, 1.0);
}

vec4 fog(vec3 ray, float d, vec3 sunDir, vec4 material) {
    float fogAmount = 1.0-exp(-d*0.035);
    float sunAmount = pow(max(dot(ray, sunDir), 0.0), 90.0);
    vec4 fogCol = mix(vec4(0.3, 0.7, 0.9, 1.0), vec4(1.0, 0.9, 0.7, 1.0), sunAmount);
    return mix(material, fogCol, fogAmount);
}

vec4 terrain(vec3 p, vec3 sunDir) {
    vec3 normal = getNormal(p, 0.0);
	vec3 abnormal = abs(normal);	    
	vec4 grassRock = mix(grass(p), rock(p), smoothstep(0.0, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 snowRock = mix(snow(p), rock(p), smoothstep(0.75, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 fragC = mix(grassRock, snowRock, smoothstep(0.5, 1.0, p.y));
   	fragC *= max(dot(sunDir, normal), 0.2);
    return fragC;
}

MarchResult march(vec3 p0, vec3 ray, bool withWater) {
    float type = SKY;
    float d = 0.0;
    int stp = 0;
    vec3 p = p0;
    while (type==SKY && d<(withWater?maxDist:maxDist*0.125) && (stp++<(withWater?maxStep:maxStep/3))) {
        p = p0 + d*ray;
        float waterLevel = withWater ? /*getWaterLevel(p.xz, d)*/ meanWaterLevel : -9999.9;
        float stpSize = estDistToTrn(p,d) * (withWater?1.0:2.0);
        // TODO fix this mess
        if (p.y<=waterLevel) {
            type = WATER;
            d = (waterLevel-p0.y)/ray.y;
            p = p0+d*ray;
        }
        else if (stpSize<d*0.001) type = LAND;
        else d+= stpSize;
    }
    d = min(d, maxDist);
    return MarchResult(d, p, type);
}


vec4 water(vec3 p, float d, vec3 ray, vec3 sunDir) {
    vec3 normal = getWaterNormal(p, d);
    vec3 ref = normalize(reflect(-sunDir, normal));
    vec4 wc = vec4(0.2,0.55,0.8,1.0);
    vec4 sc = vec4(0.9,0.9,0.7,1.0);
    wc *= max(0.35, dot(sunDir, normal));

    MarchResult uwr = march(p, normalize(reflect(ray, normal)), false);
    vec4 uwt = terrain(uwr.pos, sunDir);
    wc = mix(wc, uwt, uwr.type*0.25);

    return mix(wc, sc, 0.85*pow(max(dot(ref, -ray),0.0),8.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	

    float pitch = MODE==0 ? 0.0 : 0.2*sin(iTime*0.2);
    float yaw = 0.0;
    float roll = MODE==0 ? 0.0 : 0.1*sin(iTime*0.5);

    vec3 ray = rayToPixel(fragCoord);

    mat3 tr = mat3(
        cos(roll),  -sin(roll), 0.0,
        sin(roll), cos(roll), 0.0,
        0, 0, 1
    ) 
    * mat3(
        cos(yaw), 0.0, sin(yaw),
        0.0, 1.0, 0.0,
        -sin(yaw), 0.0, cos(yaw)
    )
    * mat3(
        1.0, 0.0, 0.0,
        0.0, cos(pitch), -sin(pitch),
        0.0, sin(pitch), cos(pitch)
    )
    ;
    ray *= tr;


    #if MODE
    vec3 p0 = vec3(17.25, 2.0, 1.0*iTime);
    #else
    vec3 p0 = vec3(60.0*iMouse.x/iResolution.x, -0.25, 60.0*iMouse.y/iResolution.y);
    #endif
    p0.y = max(getElevation(p0.xz,0.0), getWaterLevel(p0.xz,0.0)) + 1.;

    MarchResult res = march(p0, ray, true);
    vec3 sunDir = normalize(vec3(0.2, 0.1, 0.15));

    fragColor = vec4(1.0);
    if (res.dist<maxDist) {
        if (res.type==WATER) {
            fragColor = water(res.pos, res.dist, ray, sunDir);
        } else if (res.type==LAND) {
    		fragColor = terrain(res.pos, sunDir);
        }
    }

    fragColor = fog(ray, res.dist, sunDir, fragColor);
}

