    4./16., 12./16., 2./16., 10./16.,
    16./16., 8./16., 14./16., 6./16.);

float dit2(int x, int y, float v)
{
    return step(threshold[4*y+x],v);
}
float smin(float a, float b, float k){ float h = clamp(0.5+0.5*(b-a)/k,0.,1.);
return mix(b,a,h)-k*h*(1.-h);}
