float map2(vec3 p){
vec2 ppp = abs(map(iMouse.xy/iResolution.xy,vec2(0.29,0.5),vec2(1.,0.),vec2(0.),vec2(2.5,1.)));
vec2 pp2 = mix(vec2(0.),ppp,step(ppp.x,0.7)*step(ppp.y,0.7));
vec3 pp = vec3(pp2.x,0.,pp2.y)*-1.;
float fo = (sin(iTime*pi*0.5)*0.5+0.5)*4.;
float t1 = step(1.,fo);
float t2 = step(2.,fo);
float t3 = step(3.,fo);
float td = 0.1*t1;
p.xz += td;
vec3 p1 = p; vec3 p2 = p; vec3 p3 = p; vec3 p4 = p;

p1.xz -= td;
p1.xz *= rot(pi*0.25*2.);
p1 = abs(p1);
for(int i = 0 ; i <2 ; i++){
p2.xz -= td;
p2.xz *= rot(pi*0.25);
p2.xz = abs(p2.xz);
}
for(int i = 0 ; i <int(mix(2.,4.,t2)); i++){
p3.xz -= td;
p3.xz *= rot(pi*0.25);
p3.xz = abs(p3.xz);
}
for(int i = 0 ; i <int(mix(2.,mix(4.,6.,t3),t2)) ; i++){
p4.xz -= td;
p4.xz *= rot(pi*0.25);
p4.xz = abs(p4.xz);
}
float fa = 0.8;
float f1 = pow(1.,fa);float f2 = pow(2.,fa);float f3 =pow(3.,fa);

float d1 = max(box(p1+pp,vec3(0.3)*vec3(0.2,pow(4.,fa),0.2)),-(length(p.y)-0.3*f3));
float d2 = max(box(p2+pp,vec3(0.3)*vec3(0.2,pow(3.,fa),0.2)),-(length(p.y)-0.3*f2));
float d3 = max(box(p3+pp,vec3(0.3)*vec3(0.2,f2,0.2)),-(length(p.y)-0.3*f1));
float d4 = box(p4+pp,vec3(0.3)*vec3(0.2,f1,0.2));
float ts = pow(clamp(sin(iTime*pi*0.5),0.,1.),2.)*0.2;
float d5 = smin(smin(smin(d4,d3,ts),d2,ts),d1,ts)+ts*0.05;
return d5;}
vec3 nor (vec3 p){vec2 e = vec2(0.001,0.);return normalize(map2(p)-vec3(map2(p-e.xyy),map2(p-e.yxy),map2(p-e.yyx)));}
