//Distance to a sphere:
float sdfSphere(vec2 position, float radius){
    return length(position) - radius;
}

//Distance to a square with rounded corners:
float sdfSquare(vec2 position, float side_lenght, float roundness)
{
    vec2 d = abs(position) - vec2(side_lenght - roundness);
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0) - roundness;
}

//Simple coordinate transofrm to rotate existing sdf:
float sdfRotated(vec2 position, float theta){
    mat2 rotation_matrix = mat2(cos(theta), -sin(theta),
                                sin(theta),  cos(theta));

    vec2 q = rotation_matrix * position;

    //Return desired sdf passing in q as position:
    return sdfSquare(q, 0.5, 0.1);
}

//Signed angle between two vectors:
float signedAngle(vec2 v1, vec2 v2){
    vec3 w1 = vec3(v1, 0.0), w2 = vec3(v2, 0.0);

    float angle = acos(dot(normalize(w1), normalize(w2)));
    vec3 normal = cross(w1, w2);
    vec3 up = vec3(0.0, 0.0, 1.0);
    if (dot(up, normal) < 0.0) {
      angle = -angle;
    }
    return angle;
}

//Check if a point is between two vectors:
bool isBetweenVectors(vec2 point, vec2 v1, vec2 v2) {
    float range = signedAngle(v1, v2);
    if (range < 0.0) {
        range += 360.0;
    }

    float a1 = signedAngle(v1, point);
    if (a1 < 0.0){
        a1 += 360.0;
    }

    float a2 = signedAngle(point, v2);
    if (a2 < 0.0){
        a2 += 360.0;
    }

    if (a1<range && a2<range){
        return true;
    }

    return false;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
