    //Normalize coordinates to (-1;1):
    uv = 2.0 * uv - 1.0;
    //Aspect ratio correction:
    uv.x *= iResolution.x / iResolution.y;

    // Background color:
    vec3 col = vec3(0.0);

    //Composite in the box:
    if (sdfRotated(uv, -4.0*iTime) <= 0.0 ) {
        col = vec3(0.21, 0.50, 0.74);
    }


    //Composite in the swirling bullshit:

    float angle1 = -4.0*iTime, angle2 = -8.0*iTime;

    //Standard polar parametrization:
    vec2 v1 = vec2(cos(angle1), sin(angle1));
    vec2 v2 = vec2(cos(angle2), sin(angle2));

    if (!isBetweenVectors(uv, v1, v2)) {
        if (sdfSphere(uv, 0.49) <= 0.0 && sdfSphere(uv, 0.44) >= 0.0){
            col = vec3(0.88, 0.88, 1.0);
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

