// Signed distance to a 2D tunnel shape

// List of some other 2D distances:
//    https://www.shadertoy.com/playlist/MXdSRf
// and
//    www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm

float dot2( vec2 v ) { return dot(v,v); }

float sdTunnel( in vec2 p, in vec2 wh )
{
    p.x = abs(p.x); p.y = -p.y;
    vec2 q = p - wh;

    float d1 = dot2(vec2(max(q.x,0.0),q.y));
    q.x = (p.y>0.0) ? q.x : length(p)-wh.x;
    float d2 = dot2(vec2(q.x,max(q.y,0.0)));
    float d = sqrt( min(d1,d2) );

    return (max(q.x,q.y)<0.0) ? -d : d;
}

// alternative formulation
float sdTunnel2( in vec2 p, in vec2 wh )
{
    vec2 q = abs(p);
    q.x -= wh.x;

    if( p.y>=0.0 )
    {
    q.x = max(q.x,0.0);
    q.y += wh.y;
    return -min( wh.x-length(p), length(q) );
    }
    else
    {
    q.y -= wh.y;
    float f = max(q.x,q.y);
    return (f<0.0) ? f : length(max(q,0.0));
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    vec2 wh = 0.4 + 0.4*sin(iTime*vec2(1.1,1.2)+vec2(3.0,1.0));

	float d = sdTunnel( p, wh );

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-3.0*abs(d));
	col *= 0.8 + 0.2*cos(150.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

    if( iMouse.z>0.001 )
    {
    d = sdTunnel( m, wh );
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col,1.0);
}

/*

A very simple truchet pattern.

References

    Shane has many many many awesome Truchet shaders.

    Animated Two-Tiled Truchet  - Shane
    https://www.shadertoy.com/view/tsSfWK

    Fabrice already made ultra short versions of this "weaving" pattern
    https://www.shadertoy.com/view/llfyDn
    https://www.shadertoy.com/view/4lsyWn (I like this one, 185 chars !)

    iResolution, iMouse, iDate, etc - FabriceNeyret2
    https://www.shadertoy.com/view/llySRh

    Ring Twister - Flyguy - I may use this as a variation.
    https://www.shadertoy.com/view/Xt23z3 

    more: https://www.shadertoy.com/results?query=weaving

*/

#define T iTime*.1
#define AA(d,r,pix) smoothstep( .75, -.75, (d)/(pix)-(r))   // antialiased draw. r >= 1.

vec4 blendOver (vec4 A, vec4 B) {
        return (A + (1.-A.a)*B); 
}

mat2 rot(float a) {
    float s = sin(a),
          c = cos(a);
    return mat2(c,-s,s,c);
}

vec4 tape(vec2 uv, float pix, float side) {
    float d = abs(uv.x);
    vec4 col = vec4(0);
    float spec = .5+.5*cos((uv.y+side)*3.14159);
    spec*=spec;
    vec3 c1 = vec3(0.25,0.25,1.5);
    c1 *= 1.0 - max(0.0,cos((uv.x+uv.y)*3.14159*14.0)*.5); 
    vec3 c2 = vec3(1.2,1.2,0.7);
    c2 *= 1.0 - max(0.0,cos((uv.x-uv.y)*3.14159*14.0)*.5); 
    col.a = AA(d-.3,1.0,pix);    // r=1 pixel black border for contrast
    col.rgb = mix(col.rgb,c1,AA(d-.3,0.0,pix));    
    col.rgb = mix(col.rgb,c2,AA(d-.15,0.0,pix))*spec;        
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    float zoom = 3.0 + sin(T);

    uv*=zoom;
    uv*=rot(T+1.0);
    uv += vec2(T,T*.1)*.2;
    float pix = 2.0*zoom/iResolution.y;
    vec2 guv = fract(uv)-.5;
    vec2 gid = floor(uv);
    vec4 col = vec4(vec3(0.025),1.0); 
    col.rgb *= 1.0 - max(0.0,cos((guv.x-guv.y)*3.14159*14.0)*.25);
    if ( mod(gid.x + gid.y,2.0 ) == 1.0 ) guv = guv.yx;
    col = blendOver(tape(guv,pix,1.0),col);
    col = blendOver(tape(guv.yx,pix,0.0),col);
    fragColor = vec4(sqrt(col.rgb),1.0);
}

