#define PI acos(-1.)
#define TAU (2.*PI)

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define crep(p,c,l) p-=c*clamp(round(p/c),-l,l)
#define cyl(p,r,h) max(length(p.xy)-r,abs(p.z)-h)

#define frt(sp,off) fract((iTime+off)*sp)
#define flt(sp,off) floor((iTime+off)*sp)

struct obj{
    float d;
    vec3 sha;
    vec3 li;
};

obj minobj (obj a, obj b)
{if(a.d<b.d)return a; else return b;}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}

float sc (vec3 p, float d)
{
    p = abs(p);
    p = max(p,p.yzx);
    return min(p.x,min(p.y,p.z))-d;
}

obj grid (vec3 p)
{
    crep(p.xz,8.,2.);
    float per=1.;
    crep(p,per,2.);
    float d = max(-sc(p,per*0.34),box(p,vec3(per*0.4)));
    obj scene = obj(d,vec3(0.),vec3(1.));

    return scene;  
}

float pipe (vec3 p, float pid)
{
    float d = cyl(p,0.15,20.);

    float per = 1.5, id = round(p.z/per), speed=1., off=id*0.1,
    anim=(TAU/4.)*(flt(speed,off)+pow(frt(speed,off),6.));
    crep(p.z,per,6.);
    p.xy *= (mod(pid,2.)<0.5)?rot(anim):rot(-anim);
    d = min(d,cyl(p,0.2, 0.15));
    d = min(d, cyl(p.xzy,0.065,0.4));
    d = min(d, cyl(p.yzx,0.065,0.4));

    return d; 
}

obj SDF (vec3 p)
{
    p.yz *= rot(-atan(1./sqrt(2.)));
    p.xz *= rot(PI/4.);
    vec3 pp = p;
    obj scene = grid(p);

    p.y -= 1.;
    float p1id = round(p.x/2.);
    crep(p.x, 2., 5.);
    scene = minobj(scene,obj(pipe(p,p1id),vec3(0.2,0.,0.),vec3(1.,0.5,0.9)));

    p=pp;
    p.y += 1.;
    float p2id = round(p.z/2.);
    crep(p.z,2.,5.);
    scene = minobj(scene,obj(pipe(p.yzx,p2id),vec3(0.,0.,0.1),vec3(0.,1.,0.5)));

    return scene;
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.001,0.);
    return normalize(SDF(p).d-vec3(SDF(p-eps.xyy).d,SDF(p-eps.yxy).d,SDF(p-eps.yyx).d));
}

float AO (float eps, vec3 p, vec3 n)
{return clamp(SDF(p+eps*n).d/eps,0.,1.);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    vec3 ro=vec3(uv*5.,-30.),rd=vec3(0.,0.,1.),p=ro,
    col=vec3(0.),l=normalize(vec3(2.,3.,-3.));

    bool hit=false; obj O;
    for(float i=0.;i<64.;i++)
    {
        O = SDF(p);
        if (O.d<0.001)
        {hit=true; break;}
        p += O.d*rd;
    }

    if (hit)
    {
        vec3 n = getnorm(p);
        float light = max(dot(n,l),0.);
        float ao = AO(0.1,p,n)+AO(0.3,p,n)+AO(0.5,p,n);
        col = mix(O.sha,O.li,light)*ao/3.;
    }

    fragColor = vec4(sqrt(col),1.0);
}

float M_PI = 3.14;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int xor = (int(fragCoord.x) ^ int(fragCoord.y));

    float r = float((xor * 2) & 0xff) / 255.0 * 0.3;
    float g = float((xor * 4) & 0xff) / 255.0 * 0.7;
	float b = float((xor * 8) & 0xff) / 255.0 * 0.6;

    vec3 col = vec3(b * atan(r * cos(iTime + M_PI / 3.0), b * sin(iTime - M_PI / 2.0) + 0.0001), 
    g * atan(r * cos(iTime - M_PI / 4.0), g * sin(iTime + M_PI * 4.0 / 3.0) + 0.0004),
    b * atan(b * sin(iTime + M_PI / 2.0), g * cos(iTime + M_PI / 2.0)) + 0.0004);

    fragColor = vec4(mix(col.x * atan(iTime * 0.8 + M_PI / 8.0, col.y), 1.0, cos(g * b - M_PI / r / 2.0)), col.y, col.z, 1.0);
}

#define PI 3.1415
#define NUM_LAYERS 4.0

vec4 firstColor = vec4(0.2,1.0,0.4,1.0);//green
vec4 endColor = vec4(0.0,0.3,1.0,1.0);//blue

//rotation matrix
mat2 rot(float a){
	float s=sin(a);
	float c=cos(a);
	return mat2(c,-s,s,c);
}

//gradient
vec4 gradient(vec2 uv){
    //uv*=rot(iTime*0.1);
	vec4 col = mix(mix(firstColor*0.9, endColor*1.2, abs(uv.x)),mix(firstColor*0.9, endColor*1.2, abs(uv.x)),uv.x);	
	return col;
}

//draw 1 circle
float Circle(vec2 uv){
	float d=length(uv);
	float m=0.05/d;
	m*=smoothstep(1.0,0.2,d);
	return m;
}

//draw 1 star
float Star(vec2 uv,float flare){

	//center of star
	float d=length(uv);
	float m=0.05/d;

	//flare
	float rays=max(0.0,1.0-abs(uv.x*uv.y*1000.0));
	m+=rays*flare;
	uv*=rot(PI/4.0);
	rays=max(0.0,1.0-abs(uv.x*uv.y*1000.0));
	m+=rays*flare*0.3;
	m*=smoothstep(1.0,0.2,d);
	return m;
}

//make random value
float Hash21(vec2 p){
	p=fract(p*vec2(123.34,456.21));
	p+=dot(p,p+45.32);
	return fract(p.x*p.y);
}

//repetition circles 
vec3 CircleLayer(vec2 uv){
	vec2 gv=fract(uv)-0.5;
	vec2 id=floor(uv);

	vec3 col=vec3(0.0);

	for(int y=-1;y<=1;y++){
		for(int x=-1;x<=1;x++){
			vec2 offset=vec2(x,y);
			float n=Hash21(id+offset);
			float size=fract(n*345.32);
			float circle=Circle(gv-offset-vec2(n,fract(n*34.0))+0.5);
			col+=circle*n*1.5;
		}
	}
	return col;
}

//repetition stars
vec3 StarLayer(vec2 uv){
	vec3 col=vec3(0.0);

	vec2 gv=fract(uv)-0.5;
	vec2 id=floor(uv);

	for(int y=-1;y<=1;y++){
		for(int x=-1;x<=1;x++){
			vec2 offset=vec2(x,y);
			float n=Hash21(id+offset);
			float size=fract(n*345.32);
			float star=Star(gv-offset-vec2(n,fract(n*34.0))+0.5,smoothstep(0.5,0.9,size));

			//blink
			star*=sin(iTime*3.0+n*PI*2.0)*0.5+1.0;
			col+=star*size;
		}
	}
	return col;
}


void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 uv=(fragCoord-iResolution.xy*0.5)/iResolution.y;
	uv*=2.0;
	uv.y-=iTime;

	vec4 background=gradient(uv);
    vec4 circle=vec4(CircleLayer(uv),1.0);


	vec3 col=vec3(0.0);

	for(float i=0.0;i<1.0;i+=1.0/NUM_LAYERS){
		float depth=fract(i);
		float scale=mix(20.0,0.5,depth);
		float fade=depth*smoothstep(1.0,0.9,depth);
		col=StarLayer(uv*scale+i*453.2*fade);	
	}

	fragColor=vec4(col,1.0)+background+circle;
}

const float e=2.7182818284;
const float pi=3.1415926535;

// Sinus based random
float sinRand(float x)
{
    float y1=( sin(x+e) + cos(x*e) + sin(x/e) )/6.0+0.5;
    float y2=sqrt(abs(sin(x*e))); // (mod( sin(mod(x, 11.0)) * 7.0, 7.0))/7.0;
    float y3=sin(x*7.0)/2.0+0.5;

    return sqrt(y1*y2*y3);
}


vec4 sinRandColor(float randValue)
{
    bool isRedNote  =randValue>0.08 && randValue<0.16 ? true:false;
    bool isGreenNote=randValue>0.24 && randValue<0.48 ? true:false;
    bool isBlueNote =randValue>0.48 && randValue<0.96 ? true:false;

    if(isRedNote)
    {
        return vec4( 1.0, 0.0, 0.0, 1.0 );
    }
    if(isGreenNote)
    {
        return vec4( 0.0, 1.0, 0.0, 1.0 );
    }
    if(isBlueNote)
    {
        return vec4( 0.2, 0.8, 1.0, 1.0 );
    }

    return vec4( vec3(0.0), 0.0 );
}


vec4 showLines(vec2 uvPixelPosition)
{
    const float timeStep=0.1;
    const float timeWidth=0.002;

    float timeSpeed=0.1;
    float geometricSpeed=2.5;

    vec4 color=vec4( 0.0 );

    // First color
    // 1.0-uv.x for move from left to right
    float seed=iTime*timeSpeed + (1.0-uvPixelPosition.x)*geometricSpeed;
    float randValue=sinRand( seed );

    // Time graduate filter
    float modTime=mod(seed, timeStep);
    if( !(modTime>=0.0 && modTime<timeWidth))
    {
        randValue=0.0;
    }

    vec4 color1=sinRandColor(randValue);

    // Second color for nearest point
    float seed2=seed+timeWidth;
    float randValue2=sinRand( seed2 );
    vec4 color2=sinRandColor(randValue2);

    // Result color
    if(color1==color2)
    {
        color=color1;
    }

    // Sin vertical moving
    if(color!=vec4( 0.0 ))
    {
        float phaseSeed=seed*1.5;
        float apmSeed=seed*1.7;
        float timeSeed=seed*1.9;

        float phase=sinRand(phaseSeed)*2.0*pi;

        float amp=sinRand(phaseSeed);
        if(amp<0.1)
        {
            amp=1.0-amp;
        }


        // float time=iTime*(1.0-sinRand(timeSeed/100.0));
        float time=iTime;

        float y=(sin(phase+time)*amp)/2.0 + 0.5;

        if(y>0.0 && uvPixelPosition.y>y && uvPixelPosition.y<y+0.01)
        {
            return color;
        }

    }

    return vec4( 0.0 );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 color = showLines( uv );

    // Output to screen
    fragColor = color;
}

#define T iTime
