// hept32 bitmagic hash
// https://www.shadertoy.com/view/flXXWs

uint _HEPT32_X = 0u, _HEPT32_Y = 0u, _HEPT32_Z = 0u;
uint hept32(uint x, uint y, uint z) {
    x = (x * _HEPT32_X) - (~x * 0x77777777u) - ~(x * _HEPT32_Y);
	y = (y * _HEPT32_Y) - (~y * 0x77777777u) - ~(y * _HEPT32_Z);
	z = (z * _HEPT32_Z) - (~z * 0x77777777u) - ~(z * _HEPT32_X);
	z = ~(~(~x * y * z) * ~(x * ~y * z) * ~(x * y * ~z));
    return z ^ (z >> 16);
}

void hept32_seed(uint seed) {
	_HEPT32_X = (hept32(seed,0x77777777u,0x77777777u) * 0x77777777u) - 0x77777777u;
	_HEPT32_Y = (hept32(0x77777777u,seed,0x77777777u) * 0x77777777u) - 0x77777777u;
	_HEPT32_Z = (hept32(0x77777777u,0x77777777u,seed) * 0x77777777u) - 0x77777777u;
}

// Based off TheTurk's Fast Improved 3D Perlin Noise
// https://www.shadertoy.com/view/slB3z3

uint hash(uvec3 h) {
    return hept32(h.x,h.y,h.z);
}

vec3 grad_dir(uint h) {
    switch (int(h) & 15) {
        case 0:
        case 12:
            return vec3(1, 1, 0);
        case 1:
        case 13:
            return vec3(-1, 1, 0);
        case 2:
        case 14:
            return vec3(1, -1, 0);
        case 3:
        case 15:
            return vec3(-1, -1, 0);
        case 4:
            return vec3(1, 0, 1);
        case 5:
            return vec3(-1, 0, 1);
        case 6:
            return vec3(1, 0, -1);
        case 7:
            return vec3(-1, 0, -1);
        case 8:
            return vec3(0, 1, 1);
        case 9:
            return vec3(0, -1, 1);
        case 10:
            return vec3(0, 1, -1);
        case 11:
            return vec3(0, -1, -1);
    }
}

float interp(float v1, float v2, float v3, float v4, float v5, float v6, float v7, float v8, vec3 t) {
    return mix(
        mix(mix(v1, v2, t.x), mix(v3, v4, t.x), t.y),
        mix(mix(v5, v6, t.x), mix(v7, v8, t.x), t.y),
        t.z
    );
}

vec3 fade(vec3 t) {
	return t * t * t * (t * (t * 6. - 15.) + 10.);
}

float noise(vec3 p) {
    vec3 fl_p = floor(p);
    vec3 fr_p = p - fl_p;
    uvec3 c = uvec3(fl_p);

    return interp(
        dot(grad_dir(hash(c)), fr_p),
        dot(grad_dir(hash((c + uvec3(1, 0, 0)))), fr_p - vec3(1, 0, 0)),
        dot(grad_dir(hash((c + uvec3(0, 1, 0)))), fr_p - vec3(0, 1, 0)),
        dot(grad_dir(hash((c + uvec3(1, 1, 0)))), fr_p - vec3(1, 1, 0)),
        dot(grad_dir(hash((c + uvec3(0, 0, 1)))), fr_p - vec3(0, 0, 1)),
        dot(grad_dir(hash((c + uvec3(1, 0, 1)))), fr_p - vec3(1, 0, 1)),
        dot(grad_dir(hash((c + uvec3(0, 1, 1)))), fr_p - vec3(0, 1, 1)),
        dot(grad_dir(hash((c + uvec3(1, 1, 1)))), fr_p - vec3(1, 1, 1)),
        fade(fr_p));
}

float perlin(vec3 p, int freq, int octa, float pers, float lacu) {
    float v = 0., a = 1., c_f = float(freq);
    for (int i = 0; i < octa; i++) {
        v += noise(p * c_f) * a;
        a *= pers;
        c_f *= lacu;
    }
    return v;
}

void mainImage(out vec4 O, in vec2 I) {
    vec2 UV = ((I / R) + (T*.03)) * 4.;

    hept32_seed(7u);

    float V = perlin(vec3(UV, T * 0.25), 1, 7, 0.333, 1.777);
    V = 1.-abs(((V*V)+V)*.5);

    O = vec4(vec3(floor((pow(V,7.777))+.5))*vec3(1.,.0,.25), 1.0);
}

#define PI 3.14159265
//Camera field of view Â°
#define FOV 45.0
