vec3 center = vec3(0.0,1.0,0.0);

vec3 lightColor = vec3(1.0,1.0,1.0);
float intensity = 5.0;
vec3 diffuse = vec3(1.0,1.0,1.0);
vec3 spec = vec3(1.0, 1.0,1.0);

vec3 quadCenter = vec3(0.0,1.0,0.0);
