//Todo : Compute the spherical triangle area not the planar triangle
float triangleArea(vec3 A, vec3 B, vec3 C)
{
    vec3 AB = B-A;
    return length(cross(B-A, C-A))/2.0;
    return length(AB) * length(C - (A + AB * dot(AB, C-A))) / 2.;
}

// Diffuse BRDF
// Todo, sample the quad domain, and weight the samples with a ggx
vec3 BRDF(vec3 normal, vec3 pos, vec3 qc, vec3 qn, vec2 qs)
{
    vec3 right = cross(qn, vec3(0., 1., 0.));
    vec3 up = cross(right, qn);

    vec3 A = vec3(qc - (right * qs.x / 2.) - (up * qs.y / 2.));
    vec3 B = vec3(qc - (right * qs.x / 2.) + (up * qs.y / 2.));
    vec3 C = vec3(qc + (right * qs.x / 2.) + (up * qs.y / 2.));
    vec3 D = vec3(qc + (right * qs.x / 2.) - (up * qs.y / 2.));

    return intensity * lightColor * (triangleArea(normalize(A-pos), normalize(B-pos), normalize(C-pos)) +
        triangleArea(normalize(A-pos), normalize(D-pos), normalize(C-pos))) / (2. * PI)
        ;
}


float hitPlane(vec3 dir, vec3 rayPoint, vec3 center, vec3 normal)
{
    return max(-(dot(rayPoint, normal)-dot(center, normal)) / dot(normal, dir),0.0);
}

float hitQuad(vec3 dir, vec3 rayPoint, vec3 center, vec3 normal, vec2 size, out vec2 uv)
{
    float t = hitPlane(dir, rayPoint, center, normal);
    vec3 interPoint = rayPoint + t*dir;
    vec3 right = cross(normal, vec3(0., 1., 0.));
    vec3 up = cross(right, normal);
    vec3 interVec = interPoint - center;
    uv = vec2(dot(right, interVec), dot(up, interVec));
    vec2 posCenter = abs(uv);
    uv = (uv/size) + vec2(.5);
    if(posCenter.x<size.x/2. && posCenter.y<size.y/2.)
        return t;
    else
        return -9999999999999.;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    quadNormal.y = quadNormal.y - .000001;
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cuv = (uv - vec2(0.5)) * 2.0 * vec2(1.0, float(iResolution.x)/float(iResolution.y));

    // Camera infos
    vec3 cam = vec3(CAM_DISTANCE, 1.0, CAM_DISTANCE) * vec3(cos(iTime * ROTATION_SPEED), CAM_HEIGHT, sin(iTime * ROTATION_SPEED)) ;

    float near = 0.01f;
    float viewportHeight = 2.0f * near * tan(FOV * 0.5f * PI/180.0f);
    float viewportWidth = viewportHeight * iResolution.x / iResolution.y;
    vec3 forward = normalize(center-cam) * near;

    vec3 right = normalize(cross(forward, vec3(0.0,1.0,0.0)));
    vec3 up = normalize(cross(right, forward)) / (float(iResolution.x)/float(iResolution.y));

    // Ray generation
    vec3 dir = (right * cuv.x * (viewportWidth/2.0f));
    dir += (up * cuv.y * (viewportHeight/2.0f));
    dir += forward;
    dir = normalize(dir);

    // Raytrace ground
    vec3 normal = vec3(0.0,1.0,0.0);
    float t = hitPlane(dir, cam, vec3(0.0,0.0,0.0), normal);
    vec3 colGround = BRDF(normal, cam + dir*t, quadCenter, quadNormal , quadSize);

    //Raytrace quad
    vec2 quadUv;
    float quadT = hitQuad(dir, cam, quadCenter, quadNormal , quadSize, quadUv);
    vec3 colQuad = lightColor;

    //Compose
	vec3 col;
    if(length(t*dir) < length(quadT*dir))
     col = colGround;
    else
     col = colQuad;

    col = mix(background, col, min(max(t, quadT),1.0));

    fragColor = vec4(col,1.0);
}

