#define MAX_STEPS 100
#define MAX_DIST 200.
#define SURF_DIST .005

#define PI 3.14159265359

const vec3 lightPosition = vec3(0, 3, 4);
const vec3 lightColor    = vec3(0.2, 0.2, 0.226) * 2.;

float noise(vec2 uv)
{
    return fract(sin(uv.x * 113. + uv.y * 412.) * 6339.);
}

float noiseSmooth(vec2 uv)
{
    vec2 index = floor(uv);

    vec2 pq = fract(uv);
    pq = smoothstep(0., 1., pq);

    float topLeft = noise(index);
    float topRight = noise(index + vec2(1, 0.));
    float top = mix(topLeft, topRight, pq.x);

    float bottomLeft = noise(index + vec2(0, 1));
    float bottomRight = noise(index + vec2(1, 1));
    float bottom = mix(bottomLeft, bottomRight, pq.x);

    return mix(top, bottom, pq.y);
}

float plane(vec3 p, vec3 n, float h)
{
  return dot(p,n) + h;
}

float getDist(in vec3 p)
{
    float h = noiseSmooth(p.xz / 8.) * 5.2;

    h += texture(iChannel1, p.xz / 20.).r;
    return plane(p, vec3(0., 1., 0), h);
}

vec3 getNormal(in vec3 p) 
{
    int body;
    float d = getDist(p);

    vec2 e = vec2(.01, 0); 

    vec3 n = d - vec3(
        getDist(p - e.xyy), 
        getDist(p - e.yxy), 
        getDist(p - e.yyx));

    return normalize(n);
}

float rayMarch(in vec3 ro, in vec3 rd)
{
    float dO = 0.;

    for (int i = 0; i < MAX_STEPS; i++)
    {
        vec3 p = ro + rd * dO;
        float dS = getDist(p);
        dO += dS;

        if (dO > MAX_DIST || dS < SURF_DIST) 
            break;
    }

    return dO;
}

vec3 getLight(in vec3 p, in vec3 rd)
{    
	vec3 normal = getNormal(p);
    vec3 lightDir = normalize(p - lightPosition);

    float cosa = pow(0.5+0.5*dot(normal, -lightDir), 3.0);
    float cosr = max(dot(-rd, reflect(lightDir, normal)), 0.0);

    vec3 ambiant = vec3(0.42);
    vec3 diffuse = vec3(0.8 * cosa);
    vec3 phong = vec3(0.5 * pow(cosr, 16.0));

    return lightColor * (ambiant + diffuse + phong);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(-.15 + sin(iTime) / 4., 1.9 + sin(sin(iTime)) / 2., iTime * 2.);
    vec3 rd = normalize(vec3(uv.x, uv.y-0.2, 0.8));

    float d = rayMarch(ro, rd);
    if (d < MAX_DIST)
    {
        vec3 p = ro + rd * d;   

        vec3 n = getNormal(p);
        col = (vec3(getLight(p, rd)) / exp(d / 4.) * 10.); 
    }
    else 
    {    
        col = vec3(smoothstep(0.998, 0.999, noise(uv)) * noise(uv * 0.5));
        col -= min(0., noiseSmooth((uv + sin(iTime/10.)) * 20.) / 2.);
        // col += (1.1 - uv.y * 2.) / 20.;
    }

    fragColor = vec4(col,1.0);
}

