float hash(vec2 p)  // thanks to iq
{
    p  = 50.0*fract( p*0.3183099 + vec2(0.71,0.113));
    return -1.0+2.0*fract( p.x*p.y*(p.x+p.y) );
}

float noise( in vec2 p )// thanks to iq
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}
float pattern( in vec2 p )
{
    vec2 q = vec2( noise( p + vec2(0.0,0.0) ),
                   noise( p + vec2(5.2,1.3) ) );

    vec2 r = vec2( noise( p + 8.0*q + vec2(1.7,9.2) ),
                   noise( p + 8.0*q + vec2(8.3,2.8) ) );

    return noise( p + 8.0*r );
}
