		f += 0.1250*noise( uv ); uv = m*uv;
		f += 0.0625*noise( uv ); uv = m*uv;




      vec4 landCol = vec4(
       sin(f*.75)*.5+.5,
       sin(f)   *.5+.5,
       sin(f*.1)*.5+.5,
       1.);

       landCol *= vec4(.4 + f,.7+ f,.1,1.);


       vec4 colorLand = vec4(landCol / pow(f , smoothstep(.2,.21,f)));

       float c = smoothstep(.1,.4,f);
      // colorLand = mix(
       vec4 color = vec4(.05,c,.6,1.) + (f*.5); 


        color = max(color , colorLand);
       return color -= c;

       }



