float iPlane(vec3 ro, vec3 rd, vec3 n, vec3 pop){

    //first of all, if the dot of the rd and n is ... nvm
    //if(dot(rd,n) > 0.9999) return -1.;
    //dot(vector in plane with normal) = zero (orthogonal)
    //(p-pointinplane)*n = 0

    //ray origin + rd at time t is point
    //p = ro + rd*t;

    //what time t is p from eq 1 = p in eq 2
    //(ro + rd*t - pointinplane)*n = 0

    //rearange to get some time alone
    //float t = (dot(pop,n) - dot(ro,n)) / dot(rd,n);

    //we can do better than that, factor out n from top 
    return dot(pop-ro,n) / dot(rd,n);
}

