//second try since the first one didn't allow me to make
//usable texture coordinates
//Here I create a basis (3 orthogonal vectors)
//based on the plane normal
//then I get the vector from center to hit point(call it CP)
//then I take the dot(CP,basisUP) as y
//and dot(CP,basisRIGHT) as x
//then convert to polar and return.
//I think the first method might actually be ok
//now looking at it again. The main issue is in main heh
// in  main I rotate the normal and center. I have to 
//rotate them by the same amount on the same axis
//or else I get the same issue as in the first try.
vec3 iDisk2(vec3 ro, vec3 rd, vec3 normal, vec3 Center){

    float t;
    vec2 uv;

    //if there isn't a hit we give backa  t of -1., 
    //and a u and v of 0.,0.

    vec3 no_hit = vec3(-1.,0.,0.);

    //trying something like this
    //https://www.shadertoy.com/view/4tySz1
    //I think the idea is creating a basis from the normal
    //and then projecting the vector form centerto hit point
    //onto the right and up of that basis.
    //to get coordinates I mean

    vec3 uu = vec3(0.,1.,0.); 
    vec3 right = cross(normal,uu);
    vec3 up = cross(right,normal);

    //we use the cross of two of the above vectors
    //to get a vector perpendicular to both
    //any two of these holds all three points (a,b,and c)
    //so the normal of them is the normal of the plane all three
    //points lie in

    //so we find the intersection of that plane
    t = iPlane(ro,rd,normal,right);

    //and get the hit point
    vec3 point_on_plane = ro + rd*t;
    vec3 center_to_hit = point_on_plane - Center;

    //and return no hit if the t negative meaning it's behind us
    if(t < 0.)
        return no_hit;

    // most of this code is from the triangle intersection
    //and barycentric coordinates
    //I am just modifying to hit whenever the distance to our
    //specified disk cetner is less than 2.
    float lange = length(point_on_plane-Center);
    if( lange > 2.)
        return no_hit;

    //another mod from the barycentric coordinates
    //I just use the same two coordinates which are now
    //the x and y on the plane oriented by the normal
    float x = dot(up,normalize(center_to_hit));
    float y = dot(right,normalize(center_to_hit));

    //this is the usual way to get polar coordinates 
    float u = atan(y,x);
    float v = lange;
    return vec3(t,u,v);
}

float checker(vec2 st){
    return mod(floor(st.x)+floor(st.y),2.);
}

mat2 rot(float a){
    float si = sin(a);
    float cs = cos(a);

    return mat2(cs,-si,si,cs);
}

vec3 grid_design(vec2 uv){

      ////////////////////////////////////////////
      //                                       //
////////      BACKGROUND GRID DESIG          //
      //                                   //
      /////////////////////////////////////


    vec2 st = uv;//save the unscaled uv
    //I'm only making an 8(*aspect)x8 grid, higher variables
    //may end up with graphs completely outside it
    uv*=8.;

    //graph background
    vec3 col = vec3(0.1);

    //center highlights
    col = mix(col,vec3(0.16),1.0-length(uv/8.));

    //dust 
    float specks = fract(sin(dot(uv,vec2(123.,16.)))*433413.);

    //nice soft texture 
    float tex = texture(iChannel0,st).x;
    col = mix(col,vec3(0.25),pow(tex,2.));

    //small grid lines
    vec2 lines = fract(uv*5.);
    lines = smoothstep(0.45,0.52,abs(lines-0.5));
    col = mix(col,vec3(0.24),lines.x);
    col = mix(col,vec3(0.24),lines.y);

    //larger grid lines
    lines = fract(uv);
    lines = smoothstep(0.47,0.52,abs(lines-0.5));
    col = mix(col,vec3(0.5),lines.x);
    col = mix(col,vec3(0.5),lines.y);

    //axis lines
    lines = smoothstep(0.0,0.02,abs(uv));
    col = mix(col,vec3(0.6),1.0-lines.x);
    col = mix(col,vec3(0.6),1.-lines.y);
    col = mix(col,vec3(0.1),step(0.1,specks)*0.2);

    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;
    vec3 col = vec3(1.);
    float a = iTime/4.;
    vec3 ro = vec3(6.*cos(a), 0., 6.*sin(a));
    vec3 ta = vec3(0.,0.,0.);
    vec3 fwd = normalize(ta - ro);
    vec3 uu = vec3(0., -1., 0.);

    vec3 right = normalize(cross(uu,fwd));
    vec3 up = normalize(cross(right,fwd));

    float focal = 1.52;

    vec3 rd = right*uv.x + up*uv.y + fwd*focal;

    vec3 planeN = vec3(0., 1., 0.);
    vec3 point_on_plane = vec3(0., -1., 0.);

    float t = iPlane(ro, rd, planeN, point_on_plane);

    if(t >= 0.){
        vec3 pos = ro + rd*t;
        col = grid_design(pos.xz/10.5);
    }

    vec3 cPos = vec3(0., 0., 0.);
    float r = 1.;

    vec3 A = vec3(-1.,1.,0.);
    A.xy*=rot(iTime);
    //vec3 B = vec3(1.,0.,0.);
    //B.zx*=rot(iTime);
    vec3 Center = vec3(0.,2.,0.);
    Center.xy*=rot(iTime);
    //vec3 t_and_uv = iDisk(ro,rd,A,B,Center);


    vec3 t_and_uv = iDisk2(ro,rd,A,Center);

    if(t_and_uv.x >= 0.){

        vec3 tex = texture(iChannel0, t_and_uv.yz).xyz;
        col = tex;

    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

bool hit = false;
vec3 color = vec3(0.);

void circle(float x, float y, float r, vec2 fragCoord, vec3 c) {
    if (!hit) {
        if (distance(vec2(x, y), fragCoord) <= r) {
            hit = true;
            color = c;
        }
    }
}

void scene(vec2 fragCoord) {
    for (float woof = 0.; woof/98. < 6.; woof+=98.) {
        for (float meow = 0.; meow/98. < 10.; meow+=98.) {
            circle(meow, woof, (sin(iTime-fragCoord.x/iResolution.x*3.)+1.)*5.-2., fragCoord, vec3(0, 0, 0));
            circle(meow, woof, (sin(iTime-fragCoord.x/iResolution.x*3.)+1.)*5., fragCoord, vec3((sin(fragCoord/iResolution.xy+iTime)+1.)/2., 0.5));
            circle(meow, woof, (sin(iTime-fragCoord.x/iResolution.x*3.)+1.)*25.-2., fragCoord, vec3(0, 0, 0));
            circle(meow, woof, (sin(iTime-fragCoord.x/iResolution.x*3.)+1.)*25., fragCoord, vec3((sin(fragCoord/iResolution.xy+iTime)+1.)/2., 0.5));
        }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    scene(fragCoord);
    fragColor = vec4(hit?color:vec3(0.), 1.0);
}

// ---------------------------------------------------------------------------
// project stochastics round 2
//
// continuing goal: 
//
//   approximate an ideal noise function,
//   that reveals no visual correlations,
//   when sampled at regular intervals,
//   everywhere!
//
//   efficiency is still of only secondary consideration
//
//   diving in and generalizing now
//   almost to the point of an actual noise() function
//
// NOTES
//
//   lehmer()     known as lehmer hash, or lehmer cipher, or lehmer rng
//
//       the analogy I like to use is that it can be viewed as a function of a 
// _pile_shuffle_ of a very large (4 billion cards) abstract deck of cards .. 
//
//                it answers the question:
//                  where does the card that was in position #h go after 
//                  doing this pile shuffle once?
//
//  it is both good and bad that lehmer() is a simple singular multiplication 
//  by an odd constant with good properties
//
//  the good is that it is efficient
//  the bad is that bitwise information only propagates "upwards"
// 
//  lox() is a 2-operation shuffle that bitwise propagates "downwards"
//
//  note that all bijective transforms of a machine word can be viewed as some kind
//  of shuffling - its shuffling all the way down - addition is a cut of the deck - 
//  multiplication is a pile shuffle - bitwise rolling is a faro shuffle!
// ---------------------------------------------------------------------------
// lehmer shuffle (bijective) propgates information towards the MSBs

uint LEHMER(uint h)   { return 0xCC2BFE9Du * h; }
uvec2 LEHMER(uvec2 h) { return 0xCC2BFE9Du * h; } 
uvec3 LEHMER(uvec3 h) { return 0xCC2BFE9Du * h; }
uvec4 LEHMER(uvec4 h) { return 0xCC2BFE9Du * h; }

// ---------------------------------------------------------------------------
// XOR shuffle (bijective) propgates information towards the LSBs

uint LOX(uint h)        { return h ^ (h >> 16); }
uvec2 LOX(uvec2 h)      { return h ^ (h >> 16); }
uvec3 LOX(uvec3 h)      { return h ^ (h >> 16); }
uvec4 LOX(uvec4 h)      { return h ^ (h >> 16); }

// ---------------------------------------------------------------------------
// key handling - the keys themselves could get a minor preshuffle (bijective)

uint KEYBITS(float k) {return floatBitsToUint(k);} //{ return LOX(LEHMER(floatBitsToUint(k))); }
uvec2 KEYBITS(vec2 k) {return floatBitsToUint(k);} //{ return LOX(LEHMER(floatBitsToUint(k))); }
uvec3 KEYBITS(vec3 k) {return floatBitsToUint(k);} //{ return LOX(LEHMER(floatBitsToUint(k))); }
uvec4 KEYBITS(vec4 k) {return floatBitsToUint(k);} //{ return LOX(LEHMER(floatBitsToUint(k))); }

// ---------------------------------------------------------------------------
// interprets as uniformly distributed in the range 0.0 <= x < 1.0, loses 8-bits

float UNIFORM(uint h) { return float(h & 0xffffffu) * (1.0 / float(0x1000000)); }
vec2 UNIFORM(uvec2 h) { return vec2(h & 0xffffffu) * (1.0 / float(0x1000000)); }
vec3 UNIFORM(uvec3 h) { return vec3(h & 0xffffffu) * (1.0 / float(0x1000000)); }
vec4 UNIFORM(uvec4 h) { return vec4(h & 0xffffffu) * (1.0 / float(0x1000000)); }

// ---------------------------------------------------------------------------
// sufficient decorrelation is called a hash here, yet it too is a shuffle

uint HASH(uint h, vec2 key)
{ uvec2 k = KEYBITS(key);
  h = LOX(LEHMER(h + k.x)); // cut-shuffle-shuffle
  h = LOX(LEHMER(h + k.y)); // cut-shuffle-shuffle
  return LEHMER(LOX(LEHMER(h))); // shuffle-shuffle-shuffle
}

uvec2 HASH(uvec2 h, vec2 key)
{ uvec2 k = KEYBITS(key);
  h = LOX(LEHMER(h + k.x));
  h = LOX(LEHMER(h + k.y));
  return LEHMER(LOX(LEHMER(h)));
}

uvec3 HASH(uvec3 h, vec2 key)
{ uvec2 k = KEYBITS(key);
  h = LOX(LEHMER(h + k.x));
  h = LOX(LEHMER(h + k.y));
  return LEHMER(LOX(LEHMER(h)));
}

uvec4 HASH(uvec4 h, vec2 key)
{ uvec2 k = KEYBITS(key);
  h = LOX(LEHMER(h + k.x));
  h = LOX(LEHMER(h + k.y));
  return LEHMER(LOX(LEHMER(h)));
}

// handy

uint  HASH1(vec2 key) { return HASH(2u, key); }
uvec2 HASH2(vec2 key) { return HASH(uvec2(3u, 5u), key); }
uvec3 HASH3(vec2 key) { return HASH(uvec3(7u, 11u, 13u), key); }
uvec4 HASH4(vec2 key) { return HASH(uvec4(17u, 19u, 23u, 29u), key); }

