// http://www.pouet.net/prod.php?which=57245
// If you intend to reuse this shader, please add credits to 'Danilo Guanabara'

#define t iTime
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=t;
	for(int i=0;i<3;i++) {
		vec2 uv,p=fragCoord.xy/r;
		uv=p;
		p-=.5;
		p.x*=r.x/r.y;
		z+=.07;
		l=length(p);
		uv+=p/l*(tan(z)+1.)*abs(tan(l*9.-z*2.));
		c[i]=.01/length(abs(mod(uv,1.)-.5));
	}
	fragColor=vec4(c/l,t);
}

// based on this shader by Silexars
// https://www.shadertoy.com/view/XsXXDn

const float pi = acos(-1.);

//https://gist.github.com/companje/29408948f1e8be54dd5733a74ca49bb9
float map_range(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

//https://gist.github.com/ayamflow/c06bc0c8a64f985dd431bd0ac5b557cd
vec2 rotateUV(vec2 uv, float rotation)
{
    float mid = 0.5;
    return vec2(
        cos(rotation) * (uv.x - mid) + sin(rotation) * (uv.y - mid) + mid,
        cos(rotation) * (uv.y - mid) - sin(rotation) * (uv.x - mid) + mid
    );
}

//https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float map(vec2 uv) {
    int iterations = 5;
    vec2 ouv = uv;

    uv = abs(mod(uv, 1.)*4. - 2.);

    for(int i = 0; i < iterations; i ++) {
        float fi = float(i);
        float fit = float(iterations);
        uv = abs(uv - (vec2(cos(iTime/4.) + (fi/fit)*1. )));
        uv *= 1.6;        
        uv = rotateUV(uv, map_range(ouv.x,-1.,1., 0.5, 5.) );
    }

    return sdBox(uv, vec2(0.2));
}

//https://www.shadertoy.com/view/MsGSRd
vec2 getGrad(vec2 uv,float delta)
{
    vec2 d=vec2(delta,0);
    return vec2(
        map(uv+d.xy)-map(uv-d.xy),
        map(uv+d.yx)-map(uv-d.yx)
    )/delta;
}

// https://www.shadertoy.com/view/wlSBzD
vec3 SpectrumPoly(in float x) {
    return (vec3( 1.220023e0,-1.933277e0, 1.623776e0)+(vec3(-2.965000e1, 6.806567e1,-3.606269e1)+(vec3( 5.451365e2,-7.921759e2, 6.966892e2)+(vec3(-4.121053e3, 4.432167e3,-4.463157e3)+(vec3( 1.501655e4,-1.264621e4, 1.375260e4)+(vec3(-2.904744e4, 1.969591e4,-2.330431e4)+(vec3( 3.068214e4,-1.698411e4, 2.229810e4)+(vec3(-1.675434e4, 7.594470e3,-1.131826e4)+ vec3( 3.707437e3,-1.366175e3, 2.372779e3)*x)*x)*x)*x)*x)*x)*x)*x)*x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 uv_o = uv;
    uv /= dot(uv,uv);

    //Log polar-tiling -> https://www.osar.fr/notes/logspherical/
    vec2 pos = vec2(log(length(uv)), atan(uv.y, uv.x));
    pos *= 1./pi;
    pos = fract(pos) - 0.5;   
    uv = pos;
    uv.x += iTime/2. + 5700.;

    //Lightning
    vec2 norm = normalize(getGrad(uv, 0.011));
    float l_speed = 4.;
    vec2 lightpos = vec2(sin(iTime*l_speed), cos(iTime*l_speed));
    float light = abs(dot(norm, normalize(lightpos)));  
    vec3 color = SpectrumPoly(sin(light*2. + sin(iTime/3.))*0.5 + 0.5)*1.4;

    //Shine
    float mask = (1. - pow(length(uv_o),0.2))*map_range(sin(iTime), -1.,1., 1.8, 2.);
    color += vec3(mask,mask,mask*0.78);

    fragColor = vec4(color,1.0);
}

#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

float t; //time
vec3 glw = vec3(0); //glow

float bx(vec3 p, vec3 s) //box sdf
{
  vec3 q=abs(p)-s;
  return min(max(q.x,max(q.y,q.z)), 0.) + length(max(q,0.));
}

float cy(vec3 p, float r) //cylinder sdf
{
  return length(p.xz)-r;
}


vec2 mp(vec3 p) //map/scene
{
  vec3 pp = p; //temp position

  //audio based modifier - just replaced with a sine here
  float fb = sin(t)*0.5+0.5;//texture(texFFTSmoothed,0.1).x * 300. + texture(texFFTSmoothed,0.99).x * 300.;
  float tt = t + fb*0.6; //audio adjusted time
  float g = length(pp) - fb*2.; //centre glow calculation
  glw +=0.01/(0.01+g*g)*vec3(0.4,0.1,0.9); //add glow

  for(float i=0.;i<4.;i++) //kifs loop for centre
  {
     pp.xy=abs(pp.xy)-1.2 - fb*0.5;
     pp.xy *= rot(tt + i);
     pp.yz *= rot(i);
     pp.z += fb;

  }
  vec2 b = vec2(bx(pp, vec3(1.)) - 0.1, 1.); //create centre crystal
  pp=p; //reset temp position

  pp.xz *= rot(t/10.); //rotate around y
  pp.xz=abs(pp.xz)-9.; //domain repeat to create 4 pillars

  vec2 c = vec2(cy(pp,1.),2.); //outer cylinders
  pp.y*=sin(t/3.); //hacky way to get the colours to shift by shifting the y
  g = length(pp.xz) * 0.85; //slight glow on pillars
  glw += 0.01/(0.01+g*g)*mix(vec3(0.1,0.0,0.9), vec3(0.9,0.0,0.1), (pp.y+10.)/20.); //add glow with y based colour
  c.x = min(c.x, -abs(p.y) + 15.); //floor plane, domain repeated to create ceiling too

  return b.x < c.x ? b : c;
} 


vec2 tr(vec3 ro,vec3 rd,float x) //raymarcher
{
  vec2 d = vec2(0);
  for(int i = 0; i < 256; i++)
  {
    vec3 p=ro+rd*d.x;
    vec2 s=mp(p);s.x*=x;
    d.x+=s.x;d.y=s.y;
    if(d.x>64.||s.x<0.001)break;
  }
  if(d.x>64.)d.y=0.;return d;
}

vec3 nm(vec3 p) //normal calc
{
  vec2 e = vec2(0.001,0); return normalize(mp(p).x-vec3(mp(p-e.xyy).x,mp(p-e.yxy).x,mp(p-e.yyx).x));
}

vec4 px(vec4 h, vec3 rd, vec3 n) //hit "shader" - calculates the colour from position + object + ray + normal data
{
  vec4 b=vec4(0,0,0,1); //background
  if(h.a==0.)return vec4(b.rgb,1.); //return background for object id 0
  vec4 a=h.a == 1. ? vec4(cos(t)*0.5+0.5,0.1,0.3, 0.2) : vec4(0.,0.,0.,0.8); //base colour
  float d=dot(n,-rd); //unclamped diffuse
  float dd=max(d,0.); //diffuse proper
  float f=pow(1.-d,4.); //easy fres by using inverse of unclamped diffuse
  float s=pow(abs(dot(reflect(rd,n),-rd)),40.); //specular
  return vec4(a.rgb*(dd+f)+s,a.a); //mix together
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
  t=iTime; //assign time global
  vec2 uv = vec2(fragCoord.x/iResolution.x, fragCoord.y/iResolution.y); //uv
  uv-=0.5;uv/=vec2(iResolution.y/iResolution.x,1); //uv normalise
  vec3 ro = vec3(0, 0, -30),rd=normalize(vec3(uv + vec2(0, 0),1.)), //ray origin and direction
  oro=ro,ord=rd,cn,cp,cc;float ts=1.; //lots of variables to track transparency loop
  for(int i=0;i<4;i++) //transparency loop
  {
    vec2 f=tr(oro,ord,1.); //march to front object
    cp=oro+ord*f.x;cn=nm(cp); //update current position and normal
    vec4 c=px(vec4(cp,f.y),ord,cn); //colour for front object
    if(f.y==0.||c.a==1.){cc=mix(cc,c.rgb,ts);break;}; //mix colour and break if object is solid or there was no object
    ro=cp-cn*0.01;rd=refract(ord,cn,1./1.3); //refract and update ray
    vec2 z=tr(ro,rd,-1.); //march through object
    cp=ro+rd*z.x;cn=nm(cp); //update current position and normal
    oro=cp+cn*0.01;ord=refract(rd,-cn,1.3); //refract and update the original ray variables
    if(dot(ord,ord)==0.)ord=reflect(rd,-cn); //reflect if refraction failed
    cc=mix(cc,c.rgb,ts);ts-=c.a; //mix colour
    if(ts<=0.)break; //break if we reached 0 transmission
  }
  fragColor=vec4(cc + glw,1); //write output
}

