vec3 getColorZcam(float rawLightness, float rawChroma, float hue) {
    ZcamViewingConditions cond = getZcamCond();

    vec3 jch = vec3(rawLightness * 100.0, rawChroma * 170.0, hue);

    if (CLIP_ZCAM) {
        return clipZcamJchToLinearSrgb(jch, cond);
